    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            background-color: #f0f0f0;
        }
        #demo-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            margin-top: 20px;
        }
        #control-panel {
            display: flex;
            gap: 15px;
            margin-bottom: 10px;
            padding: 10px;
            background-color: #fff;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        #control-panel div {
            display: flex;
            align-items: center;
            gap: 5px;
        }
        #canvas-container {
            width: 800px;
            height: 800px;
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #fff;
            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let currentPalette = ['#1E4363', '#2E6341'];
        const palettes = {
            DarkRainbow: ['#1E4363', '#2E6341'],
            SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],
            RustTones: ['#B7410E', '#000033', '#FFFFFF'],
            FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],
            Grays: ['#404040', '#808080', '#C0C0C0']
        };

        function setup() {
            const canvas = createCanvas(800, 800);
            canvas.parent('canvas-container');
            angleMode(DEGREES);
            noLoop();

            document.getElementById('input-steps').addEventListener('input', () => redraw());
            document.getElementById('checkbox-zoom').addEventListener('change', () => redraw());
            document.getElementById('select-color').addEventListener('change', (e) => {
                currentPalette = palettes[e.target.value];
                redraw();
            });
        }

        function draw() {
            background(255);
            const steps = int(document.getElementById('input-steps').value);
            const zoom = document.getElementById('checkbox-zoom').checked;
            const colorName = document.getElementById('select-color').value;
            currentPalette = palettes[colorName] || currentPalette;

            translate(width / 2, height / 2);

            if (zoom) {
                const zoomFactor = pow(sqrt(7), steps);
                scale(zoomFactor);
            } else {
                const baseScale = 120 / pow(sqrt(7), max(1, steps));
                scale(baseScale);
            }

            const initialSize = 100;
            for (let i = 0; i < 6; i++) {
                const orientation = (i % 3) * 120;
                drawTiling(steps, 0, 0, initialSize, orientation);
            }

            if (colorName === 'RustTones' || colorName === 'FuchsiaTones') {
                fill(currentPalette[2]);
                noStroke();
                beginShape();
                for (let i = 0; i < 6; i++) {
                    const angle = i * 60;
                    const x = initialSize * 0.6 * cos(angle);
                    const y = initialSize * 0.6 * sin(angle);
                    vertex(x, y);
                }
                endShape(CLOSE);
            }
        }

        function drawTiling(level, cx, cy, size, angle) {
            if (level === 0) {
                const orientationIndex = (angle / 120) % 3;
                const colorIndex = int(orientationIndex) % currentPalette.length;
                fill(currentPalette[colorIndex]);
                stroke(200);
                strokeWeight(0.3);
                drawRhombus(cx, cy, size, angle);
            } else {
                const newSize = size / sqrt(7);
                const subAngle = degrees(atan(sqrt(3)/5));
                for (let i = 0; i < 7; i++) {
                    const childAngle = angle + subAngle;
                    const distance = size * 0.6;
                    const theta = angle + i * (360 / 7);
                    const childCx = cx + distance * cos(theta);
                    const childCy = cy + distance * sin(theta);
                    drawTiling(level - 1, childCx, childCy, newSize, childAngle);
                }
            }
        }

        function drawRhombus(cx, cy, size, angle) {
            const half = size;
            const short = size * tan(30);
            push();
            translate(cx, cy);
            rotate(angle);
            beginShape();
            vertex(half, 0);
            vertex(0, short);
            vertex(-half, 0);
            vertex(0, -short);
            endShape(CLOSE);
            pop();
        }
    </script>
</body>
</html>
