        function barycentricToCartesian(b) {
            const size = min(width, height) * 0.9;
            const padding = (width - size)/2;
            const A = {x: width/2, y: padding};
            const B = {x: padding, y: height - padding};
            const C = {x: width - padding, y: height - padding};

            return {
                x: b.xA*A.x + b.xB*B.x + b.xC*C.x,
                y: b.xA*A.y + b.xB*B.y + b.xC*C.y
            };
        }

        function cartesianToBarycentric(x, y) {
            // Simplified conversion for equilateral triangle
            const size = min(width, height) * 0.9;
            const padding = (width - size)/2;
            const A = {x: width/2, y: padding};
            const B = {x: padding, y: height - padding};
            const C = {x: width - padding, y: height - padding};

            const area = abs((B.x - A.x)*(C.y - A.y) - (C.x - A.x)*(B.y - A.y)) / 2;
            const areaA = abs((B.x - x)*(C.y - y) - (C.x - x)*(B.y - y)) / 2;
            const areaB = abs((C.x - x)*(A.y - y) - (A.x - x)*(C.y - y)) / 2;
            const areaC = abs((A.x - x)*(B.y - y) - (B.x - x)*(A.y - y)) / 2;

            return {
                xA: areaA / area,
                xB: areaB / area,
                xC: areaC / area
            };
        }

