<body>
  <div id="main-container">
    <div id="controls-top-row">
      <button id="btn-random-set">new random set</button>
      <input type="text" id="seed-input" readonly value="1245">
      <button id="btn-reset-seed">X</button>
    </div>
    <div id="controls-points">
      <label for="slider-points">number of points</label>
      <input type="range" id="slider-points" min="10" max="50" step="5" value="10">
      <div id="slider-labels">
        <span>10</span><span>15</span><span>20</span><span>25</span><span>30</span>
        <span>35</span><span>40</span><span>45</span><span>50</span>
      </div>
    </div>
