      <label>Mathematica method</label>
      <input type="radio" id="radio-orzweig" name="math-method" value="OrZweig" checked>
      <label for="radio-orzweig">OrZweig</label>
      <input type="radio" id="radio-oropt" name="math-method" value="OrOpt">
      <label for="radio-oropt">OrOpt</label>
      <input type="radio" id="radio-twoopt" name="math-method" value="TwoOpt">
      <label for="radio-twoopt">TwoOpt</label>
      <input type="radio" id="radio-cca" name="math-method" value="CCA">
      <label for="radio-cca">CCA</label>
    </div>
