      margin-right: 5px;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <div id="control-panel">
      <div>
        <label for="slider-initialization">initialization</label>
        <input type="range" id="slider-initialization" min="0" max="65535" step="1">
        <span id="value-initialization">+ 462</span>
      </div>
      <div>
        <label for="slider-max-steps">max steps</label>
        <input type="range" id="slider-max-steps" min="1" max="50" step="1">
        <span id="value-max-steps">+ 9</span>
      </div>
      <div>
        <input type="checkbox" id="checkbox-cycle4">
        <label for="checkbox-cycle4">length 4 cycle</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-cycle2">
        <label for="checkbox-cycle2">length 2 cycles</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-cycle1">
        <label for="checkbox-cycle1">length 1 cycle points</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-all-points">
        <label for="checkbox-all-points">all map points</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-modify">
        <label for="checkbox-modify">modify initialization</label>
      </div>
    </div>
    <div id="visualization-area">
      <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
      <div id="canvas-container"></div>
    </div>
  </div>

  <script>
    // Bit reversal function for lower 15 bits
    function bitReverse15(n) {
      let reversed = 0;
      for (let i = 0; i < 15; i++) {
        reversed = (reversed << 1) | (n & 1);
        n >>= 1;
      }
      return reversed;
    }

    // Automaton function
    function f(n) {
      const floorHalf = Math.floor(n / 2);
      const reversed = bitReverse15(floorHalf);
      return (n + reversed) % 65536;
    }

    // Precompute fixed points (cycle length 1)
    const fixedPoints = [];
    for (let n = 0; n <= 65535; n++) {
      if (f(n) === n) {
        fixedPoints.push(n);
      }
    }

    // Hardcoded cycle pairs and cycle4 points
    const cycle2Pairs = [
      [10922, 21845], [13107, 26214], [13653, 27306], [15339, 30678],
      [21844, 43691], [26213, 52427], [27305, 54611], [30677, 61355], [43690, 54613]
    ];
    const cycle4 = [43690, 54612, 32768, 49152];

    // Global state variables
    let currentSequence = [];
    let currentCycleInfo = null;

    // Initialize p5 sketch
    let myp5;
    document.addEventListener('DOMContentLoaded', function () {
      const sliderInit = document.getElementById('slider-initialization');
      const sliderMaxSteps = document.getElementById('slider-max-steps');
      const valueInit = document.getElementById('value-initialization');
      const valueMaxSteps = document.getElementById('value-max-steps');
      const infoText = document.getElementById('info-text');

      // Set initial slider values
      sliderInit.value = 462;
      sliderMaxSteps.value = 9;

      // Generate initial sequence
      function generateSequence(initial, maxSteps) {
        const sequence = [initial];
        for (let i = 0; i <= maxSteps; i++) {
          sequence.push(f(sequence[i]));
        }
        return sequence;
      }

      // Cycle detection
      function detectCycle(sequence) {
        const seen = new Map();
        for (let i = 0; i < sequence.length; i++) {
          const n = sequence[i];
          if (seen.has(n)) {
            const prevIndex = seen.get(n);
            return { cycleLength: i - prevIndex, step: i };
          }
          seen.set(n, i);
        }
        return null;
      }

      // Update info text
      function updateInfoText(initValue, maxSteps, cycleInfo) {
        if (cycleInfo) {
          infoText.textContent = `result of initial condition ${initValue} repeats in ${cycleInfo.cycleLength} steps`;
        } else {
          infoText.textContent = `result of initial condition ${initValue} does not repeat in ${maxSteps + 1} steps`;
        }
      }

      // Update visualization
      function updateVisualization() {
        const initValue = parseInt(sliderInit.value);
        const maxSteps = parseInt(sliderMaxSteps.value);
        currentSequence = generateSequence(initValue, maxSteps);
        currentCycleInfo = detectCycle(currentSequence);
        updateInfoText(initValue, maxSteps, currentCycleInfo);
        myp5.redraw();
      }

      // Initialize p5 sketch
      myp5 = new p5(function (p) {
        p.setup = function () {
          const canvas = p.createCanvas(512, 512);
          canvas.parent('canvas-container');
          p.noLoop();
        };

        p.draw = function () {
          p.background('#e0f0e0');

          // Draw all map points if checkbox is checked
          if (document.getElementById('checkbox-all-points').checked) {
            p.stroke('#808080');
            p.strokeWeight(1);
            for (let n = 0; n <= 65535; n++) {
              const next = f(n);
              const x = next & 255;
              const y = (next >> 8) & 255;
              p.point(x * 2, y * 2);
            }
          }

          // Draw automaton path
          if (currentSequence.length > 0) {
            p.stroke('#800080');
            p.strokeWeight(2);
            p.noFill();
            p.beginShape();
            for (let i = 0; i < currentSequence.length; i++) {
              const n = currentSequence[i];
              const x = n & 255;
              const y = (n >> 8) & 255;
              p.vertex(x * 2, y * 2);
            }
            p.endShape();

            // Start point (red)
            const startX = (currentSequence[0] & 255) * 2;
            const startY = ((currentSequence[0] >> 8) & 255) * 2;
            p.fill('#FF0000');
            p.noStroke();
            p.ellipse(startX, startY, 6, 6);

            // End point (blue)
            const endN = currentSequence[currentSequence.length - 1];
            const endX = (endN & 255) * 2;
            const endY = ((endN >> 8) & 255) * 2;
            p.fill('#0000FF');
            p.ellipse(endX, endY, 6, 6);
          }

          // Draw cycle length 1 points
          if (document.getElementById('checkbox-cycle1').checked) {
            p.stroke('#00FFFF');
            p.strokeWeight(1);
            for (const n of fixedPoints) {
              const x = (n & 255) * 2;
              const y = ((n >> 8) & 255) * 2;
              p.point(x, y);
            }
          }

          // Draw cycle length 2 pairs
          if (document.getElementById('checkbox-cycle2').checked) {
            p.stroke('#008000');
            p.strokeWeight(1);
            for (const pair of cycle2Pairs) {
              const n1 = pair[0];
              const n2 = pair[1];
              const x1 = (n1 & 255) * 2;
              const y1 = ((n1 >> 8) & 255) * 2;
              const x2 = (n2 & 255) * 2;
              const y2 = ((n2 >> 8) & 255) * 2;
              p.line(x1, y1, x2, y2);
            }
          }

          // Draw cycle length 4
          if (document.getElementById('checkbox-cycle4').checked) {
            p.stroke('#008000');
            p.strokeWeight(1);
            const cycle4Points = cycle4.map(n => {
              const x = (n & 255) * 2;
              const y = ((n >> 8) & 255) * 2;
              return { x, y };
            });

            // Draw cycle path
            for (let i = 0; i < 4; i++) {
              const p1 = cycle4Points[i];
              const p2 = cycle4Points[(i + 1) % 4];
              p.line(p1.x, p1.y, p2.x, p2.y);
            }

            // Draw bounding box
            let minX = Infinity, minY = Infinity;
            let maxX = -Infinity, maxY = -Infinity;
            for (const p of cycle4Points) {
              if (p.x < minX) minX = p.x;
              if (p.y < minY) minY = p.y;
              if (p.x > maxX) maxX = p.x;
              if (p.y > maxY) maxY = p.y;
            }
            p.stroke('#FFA500');
            p.noFill();
            p.rect(minX, minY, maxX - minX, maxY - minY);

            // Draw perpendicular bisectors
            p.stroke('#008000');
            for (let i = 0; i < 4; i++) {
              const p1 = cycle4Points[i];
              const p2 = cycle4Points[(i + 1) % 4];
              const midX = (p1.x + p2.x) / 2;
              const midY = (p1.y + p2.y) / 2;
              const dx = p2.x - p1.x;
              const dy = p2.y - p1.y;
              const perpDX = -dy;
              const perpDY = dx;
              const length = Math.sqrt(perpDX ** 2 + perpDY ** 2);
              const unitPerpDX = perpDX / length;
              const unitPerpDY = perpDY / length;
              const extend = 50;
              p.line(
                midX - unitPerpDX * extend,
                midY - unitPerpDY * extend,
                midX + unitPerpDX * extend,
                midY + unitPerpDY * extend
              );
            }
          }
        };
      });

      // Initialize sliders and event listeners
      valueInit.textContent = '+ ' + sliderInit.value;
      valueMaxSteps.textContent = '+ ' + sliderMaxSteps.value;
      updateVisualization();

      sliderInit.addEventListener('input', function () {
        valueInit.textContent = '+ ' + sliderInit.value;
        updateVisualization();
      });

      sliderMaxSteps.addEventListener('input', function () {
        valueMaxSteps.textContent = '+ ' + sliderMaxSteps.value;
        updateVisualization();
      });

      const checkboxes = document.querySelectorAll('#control-panel input[type="checkbox"]');
      checkboxes.forEach(checkbox => {
        checkbox.addEventListener('change', function () {
          myp5.redraw();
        });
      });
    });
  </script>
</body>
</html>
