            try {
                if (plotType === 'composition') {
                    params = simulationParams[stepType][plotType][component][stage];
                } else {
                    params = simulationParams[stepType][plotType][stage];
                }
            } catch (e) {
                console.error('Missing parameters for:', { stepType, plotType, component, stage });
                return;
            }

            // Generate data
            const time = [];
            const y = [];
            const tStep = 0.1;
            const stepTime = 10;

            for (let t = 0; t <= 20; t += tStep) {
                time.push(t);
                if (t < stepTime) {
                    y.push(params.initial);
                } else {
                    const delta = params.Kp * stepPercent * (1 - Math.exp(-(t - stepTime) / params.tau));
                    y.push(params.initial + delta);
                }
            }

            // Plot configuration
            const colorMap = {
                benzene: '#FF0000',
                toluene: '#0000FF',
                pxylene: '#008B8B'
            };
            const lineColor = plotType === 'composition' ? colorMap[component] : '#000000';

            // Layout
            const layout = {
                xaxis: {
                    title: 'time in minutes',
                    gridcolor: '#CCCCCC',
                    range: [0, 20]
                },
                yaxis: {
                    title: get ylabel(plotType, component),
                    gridcolor: '#CCCCCC'
                },
                margin: { l: 80, r: 20, t: 20, b: 50 },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)'
            };

            // Trace
            const trace = {
                x: time,
                y: y,
                type: 'line',
                line: { color: lineColor, width: 2 },
                hovertemplate: 'Time: %{x:.1f} min<br>Value: %{y:.4f}<extra></extra>'
            };

            // Plot or update
            if (!plotContainer.layout) {
                Plotly.newPlot(plotContainer, [trace], layout);
            } else {
                Plotly.react(plotContainer, [trace], layout);
            }
        }

        function get ylabel(plotType, component) {
            switch (plotType) {
                case 'composition':
                    return `${component} mole fraction`;
                case 'temperature':
                    return 'temperature (C)';
                case 'molar_hold_up':
                    return 'molar hold-up (kmol)';
                default:
                    return 'value';
            }
        }
    </script>
</body>
</html>
