            </div>

            <h3>DFT</h3>
            <div class="control-group">
                <label>scale</label>
                <button id="btn-linear" class="active">linear</button>
                <button id="btn-decibel">decibel</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            const sliderC1 = document.getElementById('slider-c1');
            const sliderC2 = document.getElementById('slider-c2');
            const checkboxClipping = document.getElementById('checkbox-clipping');
            const sliderLevel = document.getElementById('slider-level');
            const resetC1 = document.getElementById('reset-c1');
            const resetC2 = document.getElementById('reset-c2');
            const btnLinear = document.getElementById('btn-linear');
            const btnDecibel = document.getElementById('btn-decibel');
            const levelControlGroup = document.getElementById('level-control-group');
            const c1Display = document.getElementById('c1-value-display');
            const c2Display = document.getElementById('c2-value-display');
            const levelDisplay = document.getElementById('level-value-display');

            // Set default values
            sliderC1.min = -2;
            sliderC1.max = 2;
            sliderC1.step = 0.1;
            sliderC1.value = 0;
            sliderC2.min = -2;
            sliderC2.max = 2;
            sliderC2.step = 0.1;
            sliderC2.value = 0;
            sliderLevel.min = 0;
            sliderLevel.max = 1.5;
            sliderLevel.step = 0.05;
            sliderLevel.value = 0.7;
            levelDisplay.textContent = '0.7';

            // Event listeners
            sliderC1.addEventListener('input', updateSimulation);
            sliderC2.addEventListener('input', updateSimulation);
            resetC1.addEventListener('click', () => {
                sliderC1.value = 0;
                updateSimulation();
            });
            resetC2.addEventListener('click', () => {
                sliderC2.value = 0;
                updateSimulation();
            });
            checkboxClipping.addEventListener('change', () => {
                if (checkboxClipping.checked) {
                    levelControlGroup.style.display = 'block';
                } else {
                    levelControlGroup.style.display = 'none';
                }
                updateSimulation();
            });
            sliderLevel.addEventListener('input', updateSimulation);
            btnLinear.addEventListener('click', () => {
                if (!btnLinear.classList.contains('active')) {
                    btnLinear.classList.add('active');
                    btnDecibel.classList.remove('active');
                    updateSpectrumScale();
                }
            });
            btnDecibel.addEventListener('click', () => {
                if (!btnDecibel.classList.contains('active')) {
                    btnDecibel.classList.add('active');
                    btnLinear.classList.remove('active');
                    updateSpectrumScale();
                }
            });

            // FFT implementation
            function fft(real, imag) {
                var n = real.length;
                var i, j, k, m;
                var tmp, c, s, treal, timag;
                // Bit-reversal
                j = 0;
                for (i = 0; i < n; i++) {
                    if (i < j) {
                        tmp = real[i];
                        real[i] = real[j];
                        real[j] = tmp;
                        tmp = imag[i];
                        imag[i] = imag[j];
                        imag[j] = tmp;
                    }
                    k = n / 2;
                    while (j >= k && k > 0) {
                        j -= k;
                        k /= 2;
                    }
                    j += k;
                }
                // FFT
                for (m = 2; m <= n; m *= 2) {
                    for (i = 0; i < n; i += m) {
                        for (k = 0; k < m / 2; k++) {
                            c = Math.cos(Math.PI * k / (m / 2));
                            s = Math.sin(Math.PI * k / (m / 2));
                            treal = real[i + k + m / 2] * c - imag[i + k + m / 2] * s;
                            timag = real[i + k + m / 2] * s + imag[i + k + m / 2] * c;
                            real[i + k + m / 2] = real[i + k] - treal;
                            imag[i + k + m / 2] = imag[i + k] - timag;
                            real[i + k] += treal;
                            imag[i + k] += timag;
                        }
                    }
                }
            }

            function updateSimulation() {
                const c1 = parseFloat(sliderC1.value);
                const c2 = parseFloat(sliderC2.value);
                const clippingEnabled = checkboxClipping.checked;
                const level = parseFloat(sliderLevel.value);

                // Update displays
                c1Display.textContent = c1.toFixed(1);
                c2Display.textContent = c2.toFixed(1);
                levelDisplay.textContent = level.toFixed(2);

                // Constants
                const f = 1000;
                const A = 1;
                const N = 2048;
                const fs = 32000;

                // Generate time vector
                const t = Array.from({ length: N }, (_, i) => i / fs);

                // Generate input signal x(t)
                const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));

                // Compute output signal y(t)
                const y = x.map(xi => xi + c1 * xi**2 + c2 * xi**3);

                // Apply clipping
                if (clippingEnabled) {
                    for (let i = 0; i < y.length; i++) {
                        y[i] = Math.max(-level, Math.min(level, y[i]));
                    }
                }

                // Update signal plot
                const signalTrace = {
                    x: t,
                    y: y,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Signal'
                };
                const signalLayout = {
                    title: 'signal',
                    xaxis: {
                        title: 'time[s]',
                        range: [0, 0.005],
                        autorange: false
                    },
                    yaxis: {
                        title: '',
                        range: [-2, 2],
                        autorange: false
                    },
                    margin: { l: 30, r: 20, b: 40, t: 40 }
                };
                if (window.signalPlotInitialized) {
                    Plotly.react('plot-signal', [signalTrace], signalLayout);
                } else {
                    Plotly.newPlot('plot-signal', [signalTrace], signalLayout);
                    window.signalPlotInitialized = true;
                }

                // Perform DFT using FFT
                const real = y.slice();
                const imag = new Array(N).fill(0);
                fft(real, imag);

                // Compute magnitude
                const mag = real.map((re, i) => Math.sqrt(re**2 + imag[i]**2));

                // Normalize for single-sided spectrum
                const normalizedMag = new Array(N/2);
                normalizedMag[0] = mag[0] / N;
                for (let k = 1; k < N/2; k++) {
                    normalizedMag[k] = 2 * mag[k] / N;
                }

                // Store for spectrum update
                window.normalizedMag = normalizedMag;

                // Update spectrum plot
                updateSpectrumScale();
            }

            function updateSpectrumScale() {
                const normalizedMag = window.normalizedMag || [];
                const fs = 32000;
                const N = 2048;
                const freq = Array.from({ length: N/2 }, (_, k) => k * fs / N);

                const scale = document.getElementById('btn-linear').classList.contains('active') ? 'linear' : 'decibel';
                let yData;
                let yLayout;

                if (scale === 'linear') {
                    yData = normalizedMag;
                    yLayout = {
                        title: 'magnitude spectrum (DFT)',
                        xaxis: {
                            title: 'frequency[Hz]',
                            range: [0, 8000],
                            autorange: false
                        },
                        yaxis: {
                            title: '',
                            range: [0, 1.1],
                            autorange: false
                        },
                        margin: { l: 30, r: 20, b: 40, t: 40 }
                    };
                } else {
                    // Convert to dB, avoiding log(0)
                    yData = normalizedMag.map(m => {
                        const val = m > 0 ? 20 * Math.log10(m) : -100;
                        return val > -100 ? val : -100;
                    });
                    yLayout = {
                        title: 'magnitude spectrum (DFT)',
                        xaxis: {
                            title: 'frequency[Hz]',
                            range: [0, 8000],
                            autorange: false
                        },
                        yaxis: {
                            title: '',
                            range: [-100, 0],
                            autorange: false
                        },
                        margin: { l: 30, r: 20, b: 40, t: 40 }
                    };
                }

                const spectrumTrace = {
                    x: freq,
                    y: yData,
                    type: 'bar',
                    width: 5,
                    marker: { color: 'blue' }
                };

                if (window.spectrumPlotInitialized) {
                    Plotly.react('plot-spectrum', [spectrumTrace], yLayout);
                } else {
                    Plotly.newPlot('plot-spectrum', [spectrumTrace], yLayout);
                    window.spectrumPlotInitialized = true;
                }
            }

            // Initial simulation
            updateSimulation();
        });
    </script>
</body>
</html>
