<!DOCTYPE html>
<html>
<head>
    <title>Ray Tracing Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            display: flex;
            margin: 0;
            font-family: sans-serif;
        }
        #control-panel {
            width: 250px;
            padding: 10px;
            background-color: #f0f0f0;
            overflow-y: auto;
        }
        .slider-group, .checkbox-group {
            margin-bottom: 15px;
        }
        label {
            display: block;
            margin-bottom: 5px;
        }
        input[type=range] {
            width: 100%;
        }
        .checkbox-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 5px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div class="slider-group">
            <label>ray count: <span id="value-ray-count">15</span></label>
            <input type="range" id="slider-ray-count" min="1" max="50" step="1" value="15">
        </div>
        <div class="slider-group">
            <label>beam width: <span id="value-beam-width">1.25</span></label>
            <input type="range" id="slider-beam-width" min="0.1" max="2.0" step="0.01" value="1.25">
        </div>
        <div class="slider-group">
            <label>incident angle (°): <span id="value-incident-angle">-45</span></label>
            <input type="range" id="slider-incident-angle" min="-90" max="90" step="1" value="-45">
        </div>
        <div class="slider-group">
            <label>refraction ratio: <span id="value-refraction-ratio">2.0</span></label>
            <input type="range" id="slider-refraction-ratio" min="1.0" max="3.0" step="0.01" value="2.0">
        </div>
        <div class="checkbox-group">
            <div class="checkbox-row">
                <label><input type="checkbox" id="checkbox-injected" checked> injected</label>
                <label><input type="checkbox" id="checkbox-refracted" checked> refracted</label>
            </div>
            <div class="checkbox-row">
                <label><input type="checkbox" id="checkbox-reflected" checked> reflected</label>
                <label><input type="checkbox" id="checkbox-ejected" checked> ejected</label>
            </div>
            <div class="checkbox-row">
                <label><input type="checkbox" id="checkbox-normals"> normals</label>
                <label><input type="checkbox" id="checkbox-intersections"> intersections</label>
            </div>
            <div class="checkbox-row">
                <label><input type="checkbox" id="checkbox-beam-top" checked> beam top half</label>
                <label><input type="checkbox" id="checkbox-beam-bottom" checked> beam bottom half</label>
            </div>
        </div>
    </div>
    <div id="canvas-container"></div>
    <script>
        function setup() {
            let canvas = createCanvas(800, 800);
            canvas.parent('canvas-container');
            document.getElementById('value-ray-count').textContent = document.getElementById('slider-ray-count').value;
            document.getElementById('value-beam-width').textContent = document.getElementById('slider-beam-width').value;
            document.getElementById('value-incident-angle').textContent = document.getElementById('slider-incident-angle').value;
            document.getElementById('value-refraction-ratio').textContent = document.getElementById('slider-refraction-ratio').value;

            document.getElementById('slider-ray-count').addEventListener('input', function() {
                document.getElementById('value-ray-count').textContent = this.value;
            });
            document.getElementById('slider-beam-width').addEventListener('input', function() {
                document.getElementById('value-beam-width').textContent = this.value;
            });
            document.getElementById('slider-incident-angle').addEventListener('input', function() {
                document.getElementById('value-incident-angle').textContent = this.value;
            });
            document.getElementById('slider-refraction-ratio').addEventListener('input', function() {
                document.getElementById('value-refraction-ratio').textContent = this.value;
            });
        }

        function draw() {
            background(255);
            translate(width/2, height/2);
            scale(width/5);

            drawAxes();
            noFill();
            stroke(0);
            ellipse(0, 0, 2, 2);

            let rayCount = parseInt(document.getElementById('slider-ray-count').value);
            let beamWidth = parseFloat(document.getElementById('slider-beam-width').value);
            let incidentAngle = parseFloat(document.getElementById('slider-incident-angle').value);
            let refractionRatio = parseFloat(document.getElementById('slider-refraction-ratio').value);

            let showInjected = document.getElementById('checkbox-injected').checked;
            let showRefracted = document.getElementById('checkbox-refracted').checked;
            let showReflected = document.getElementById('checkbox-reflected').checked;
            let showEjected = document.getElementById('checkbox-ejected').checked;
            let showNormals = document.getElementById('checkbox-normals').checked;
            let showIntersections = document.getElementById('checkbox-intersections').checked;
            let showTop = document.getElementById('checkbox-beam-top').checked;
            let showBottom = document.getElementById('checkbox-beam-bottom').checked;

            let angleRad = radians(incidentAngle);
            let direction = createVector(cos(angleRad), sin(angleRad));

            let rays = [];
            let spacing = beamWidth / (rayCount - 1);
            for (let i = 0; i < rayCount; i++) {
                let y = -beamWidth/2 + i * spacing;
                let start = createVector(-3, y);
                rays.push({ start: start, direction: direction.copy() });
            }

            for (let ray of rays) {
                let isTop = ray.start.y > 0;
                let isBottom = ray.start.y < 0;
                if (ray.start.y === 0) {
                    if (!showTop && !showBottom) continue;
                } else {
                    if (isTop && !showTop) continue;
                    if (isBottom && !showBottom) continue;
                }

                let t1 = findIntersection(ray.start, ray.direction);
                if (t1 === null) continue;
                let p1 = p5.Vector.add(ray.start, p5.Vector.mult(ray.direction, t1));
                let normal1 = p1.copy().normalize();

                let eta1 = 1.0 / refractionRatio;
                let refractedDir = refract(ray.direction, normal1, eta1);
                if (!refractedDir) continue;

                let t2 = findIntersection(p1, refractedDir);
                if (t2 === null || t2 <= 0) continue;
                let p2 = p5.Vector.add(p1, p5.Vector.mult(refractedDir, t2));
                let normal2 = p2.copy().normalize();

                let reflectedDir = p5.Vector.reflect(refractedDir, normal2);

                let t3 = findIntersection(p2, reflectedDir);
                if (t3 === null || t3 <= 0) continue;
                let p3 = p5.Vector.add(p2, p5.Vector.mult(reflectedDir, t3));
                let normal3 = p3.copy().normalize();

                let eta3 = refractionRatio;
                let ejectedDir = refract(reflectedDir, normal3, eta3);
                if (!ejectedDir) continue;

                if (showInjected) {
                    stroke(255, 0, 0);
                    line(ray.start.x, ray.start.y, p1.x, p1.y);
                }
                if (showRefracted) {
                    stroke(0, 255, 0);
                    line(p1.x, p1.y, p2.x, p2.y);
                }
                if (showReflected) {
                    stroke(0, 0, 255);
                    line(p2.x, p2.y, p3.x, p3.y);
                }
                if (showEjected) {
                    stroke(0);
                    let ejectedEnd = p5.Vector.add(p3, p5.Vector.mult(ejectedDir, 5));
                    line(p3.x, p3.y, ejectedEnd.x, ejectedEnd.y);
                }

                if (showNormals) {
                    stroke(128);
                    drawDashedLine(p1.x, p1.y, normal1.x * 0.1, normal1.y * 0.1);
                    drawDashedLine(p2.x, p2.y, normal2.x * 0.1, normal2.y * 0.1);
                    drawDashedLine(p3.x, p3.y, normal3.x * 0.1, normal3.y * 0.1);
                }

                if (showIntersections) {
                    fill(255, 0, 0);
                    ellipse(p1.x, p1.y, 0.05, 0.05);
                    fill(0, 255, 0);
                    ellipse(p2.x, p2.y, 0.05, 0.05);
                    fill(0, 0, 255);
                    ellipse(p3.x, p3.y, 0.05, 0.05);
                }
            }
        }

        function findIntersection(start, direction) {
            let b = 2 * (start.x * direction.x + start.y * direction.y);
            let c = start.x * start.x + start.y * start.y - 1;
            let discriminant = b * b - 4 * c;
            if (discriminant < 0) return null;
            let sqrtD = Math.sqrt(discriminant);
            let t1 = (-b - sqrtD) / 2;
            let t2 = (-b + sqrtD) / 2;
            return t1 > 0 ? t1 : (t2 > 0 ? t2 : null);
        }

        function refract(incident, normal, eta) {
            let dotIN = incident.dot(normal);
            let k = 1.0 - eta * eta * (1.0 - dotIN * dotIN);
            if (k < 0.0) return null;
            let refracted = p5.Vector.mult(incident, eta);
            let temp = eta * dotIN + Math.sqrt(k);
            temp = p5.Vector.mult(normal, temp);
            refracted.sub(temp);
            refracted.normalize();
            return refracted;
        }

        function drawAxes() {
            stroke(128);
            line(-2.5, 0, 2.5, 0);
            line(0, -2.5, 0, 2.5);
            textSize(0.1);
            textAlign(CENTER, CENTER);
            for (let x of [-2, -1, 1, 2]) {
                line(x, -0.05, x, 0.05);
                text(x, x, 0.1);
            }
            for (let y of [-2, -1, 1, 2]) {
                line(-0.05, y, 0.05, y);
                text(y, 0.1, y);
            }
        }

        function drawDashedLine(x, y, dx, dy) {
            let steps = 5;
            let stepX = dx / steps;
            let stepY = dy / steps;
            for (let i = 0; i < steps; i += 2) {
                line(x + i * stepX, y + i * stepY, x + (i + 1) * stepX, y + (i + 1) * stepY);
            }
        }
    </script>
</body>
</html>
