<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Bipartite Graph Matching Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #controls-container {
            display: flex;
            flex-wrap: wrap;
            gap: 10px;
            padding: 10px;
            background: #f0f0f0;
            width: 100%;
            box-sizing: border-box;
        }
        #controls-container > span {
            font-weight: bold;
            align-self: center;
        }
        #size-controls, #degree-controls, #step-controls {
            display: flex;
            gap: 5px;
        }
        button {
            padding: 5px 10px;
            cursor: pointer;
        }
        button.active {
            background-color: #007bff;
            color: white;
            border: none;
        }
        #visualization-container {
            display: flex;
            flex-direction: column;
            align-items: center;
            width: 100%;
        }
        #status-text {
            font-size: 1.2em;
            margin: 10px 0;
            text-align: center;
        }
        #canvas-wrapper {
            width: 100%;
            height: 600px;
            display: flex;
            justify-content: center;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="controls-container">
        <span>size of bipartite graph</span>
        <div id="size-controls">
            <button id="size-6">6</button>
            <button id="size-10">10</button>
            <button id="size-14">14</button>
            <button id="size-18" class="active">18</button>
            <button id="size-22">22</button>
            <button id="size-26">26</button>
            <button id="size-30">30</button>
        </div>
        <span>maximum degree of lower part</span>
        <div id="degree-controls">
            <button id="degree-2">2</button>
            <button id="degree-3" class="active">3</button>
            <button id="degree-4">4</button>
            <button id="degree-5">5</button>
            <button id="degree-6">6</button>
        </div>
        <span>algorithm step</span>
        <div id="step-controls"></div>
        <button id="reset-button" title="Generate new graph">(+)</button>
    </div>
    <div id="visualization-container">
        <p id="status-text"></p>
        <div id="canvas-wrapper"></div>
    </div>

    <script>
        let currentSize = 18;
        let currentDegree = 3;
        let currentStep = 1;
        let history = [];
        let p5Canvas;
        let graph = { U: [], V: [], edges: {} };

        document.addEventListener('DOMContentLoaded', () => {
            setupControls();
            generateGraph();
            setupP5();
        });

        function setupControls() {
            document.querySelectorAll('#size-controls button').forEach(btn => {
                btn.addEventListener('click', () => {
                    currentSize = parseInt(btn.id.split('-')[1]);
                    document.querySelectorAll('#size-controls button').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    generateGraph();
                });
            });

            document.querySelectorAll('#degree-controls button').forEach(btn => {
                btn.addEventListener('click', () => {
                    currentDegree = parseInt(btn.id.split('-')[1]);
                    document.querySelectorAll('#degree-controls button').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                    generateGraph();
                });
            });

            document.getElementById('reset-button').addEventListener('click', () => {
                generateGraph();
            });
        }

        function generateGraph() {
            const n = currentSize;
            const maxDegree = currentDegree;
            const half = n / 2;
            graph.U = Array.from({ length: half }, (_, i) => i + 1);
            graph.V = Array.from({ length: half }, (_, i) => i + 1 + half);
            graph.edges = {};

            for (let v of graph.V) {
                const degree = Math.min(maxDegree, Math.floor(Math.random() * maxDegree) + 1);
                const connectedU = [];
                for (let i = 0; i < degree; i++) {
                    let u;
                    do {
                        u = graph.U[Math.floor(Math.random() * graph.U.length)];
                    } while (connectedU.includes(u));
                    connectedU.push(u);
                }
                graph.edges[v] = connectedU;
            }

            history = runAlgorithm(graph.U, graph.V, graph.edges);
            updateStepControls();
            currentStep = 1;
            redraw();
        }

        function runAlgorithm(U, V, edges) {
            let history = [];
            let matching = {}; // V -> U
            let freeU = [...U];

            function bfs() {
                let parent = {};
                let queue = [...freeU];
                queue.forEach(u => parent[u] = null);
                let found = false;
                let augmentingPath = [];

                while (queue.length > 0 && !found) {
                    let u = queue.shift();
                    for (let v of (edgesForU(u) || [])) {
                        if (!matching[v]) {
                            augmentingPath = reconstructPath(parent, u, v);
                            found = true;
                            break;
                        } else {
                            let nextU = matching[v];
                            if (!parent[nextU]) {
                                parent[nextU] = u;
                                queue.push(nextU);
                            }
                        }
                    }
                }

                if (found) {
                    for (let i = 0; i < augmentingPath.length; i += 2) {
                        let u = augmentingPath[i];
                        let v = augmentingPath[i + 1];
                        matching[v] = u;
                        freeU = freeU.filter(x => x !== u);
                    }
                }

                history.push({
                    bfsTree: { ...parent },
                    augmentingPath: [...augmentingPath],
                    matching: { ...matching },
                    cover: computeCover(matching, U, V),
                    statusText: `Matched: ${Object.keys(matching).length}, Covered: ${computeCover(matching, U, V).length}`
                });

                return found;
            }

            function edgesForU(u) {
                return Object.keys(edges).filter(v => edges[v].includes(u)).map(Number);
            }

            function reconstructPath(parent, u, v) {
                let path = [v];
                while (u !== null) {
                    path.unshift(u);
                    u = parent[u];
                }
                return path;
            }

            function computeCover(matching, U, V) {
                let reachableU = new Set();
                let reachableV = new Set();
                let queue = [...freeU];
                let visitedU = new Set();
                let visitedV = new Set();

                while (queue.length > 0) {
                    let u = queue.shift();
                    visitedU.add(u);
                    for (let v of (edgesForU(u) || [])) {
                        if (!visitedV.has(v)) {
                            visitedV.add(v);
                            reachableV.add(v);
                            if (matching[v]) {
                                let nextU = matching[v];
                                if (!visitedU.has(nextU)) {
                                    queue.push(nextU);
                                    reachableU.add(nextU);
                                }
                            }
                        }
                    }
                }

                return [...U.filter(u => !reachableU.has(u)), ...V.filter(v => reachableV.has(v))];
            }

            while (bfs()) {}

            return history;
        }

        function updateStepControls() {
            const stepControls = document.getElementById('step-controls');
            stepControls.innerHTML = '';
            history.forEach((_, i) => {
                const btn = document.createElement('button');
                btn.id = `step-${i + 1}`;
                btn.innerText = i + 1;
                btn.addEventListener('click', () => {
                    currentStep = i + 1;
                    redraw();
                    document.querySelectorAll('#step-controls button').forEach(b => b.classList.remove('active'));
                    btn.classList.add('active');
                });
                stepControls.appendChild(btn);
            });
            if (history.length > 0) {
                document.getElementById('step-1').classList.add('active');
            }
        }

        function setupP5() {
            new p5(sketch => {
                p5Canvas = sketch;
            }, 'canvas-wrapper');
        }

        function redraw() {
            if (p5Canvas) {
                p5Canvas.redraw();
            }
        }

        function sketch(p) {
            p.setup = function () {
                const canvas = p.createCanvas(800, 600);
                canvas.parent('canvas-wrapper');
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function () {
                p.background(255);
                if (history.length === 0 || currentStep > history.length) return;

                const state = history[currentStep - 1];
                const halfWidth = p.width / 2;
                const half = currentSize / 2;
                const spacing = halfWidth / (half + 1);

                // Draw BFS Tree (Left)
                p.push();
                p.translate(0, 0);
                drawBFSTree(p, state.bfsTree, halfWidth, p.height, spacing);
                p.pop();

                // Draw Bipartite Graph (Right)
                p.push();
                p.translate(halfWidth, 0);
                drawBipartiteGraph(p, state.matching, state.cover, state.augmentingPath, halfWidth, p.height, spacing, half);
                p.pop();

                document.getElementById('status-text').innerText = state.statusText;
            };

            function drawBFSTree(p, tree, width, height, spacing) {
                const nodeSize = 20;
                const positions = {};

                function layoutNode(node, depth, siblings) {
                    if (positions[node]) return positions[node];
                    const x = 20 + depth * 60;
                    const y = 20 + siblings * 40;
                    positions[node] = { x, y };
                    return { x, y };
                }

                for (const child in tree) {
                    const parent = tree[child];
                    if (parent === null) continue;
                    const parentPos = layoutNode(parent, 0, 0);
                    const childPos = layoutNode(child, 1, 0);
                    p.stroke(255, 0, 0);
                    p.line(parentPos.x, parentPos.y, childPos.x, childPos.y);
                }

                for (const node in positions) {
                    const pos = positions[node];
                    p.fill(255, 165, 0);
                    p.stroke(0);
                    p.rect(pos.x - nodeSize / 2, pos.y - nodeSize / 2, nodeSize, nodeSize);
                    p.fill(128, 128, 200);
                    p.noStroke();
                    p.text(node, pos.x, pos.y);
                }
            }

            function drawBipartiteGraph(p, matching, cover, path, width, height, spacing, half) {
                const uY = height / 4;
                const vY = 3 * height / 4;

                // Draw edges
                for (const v of graph.V) {
                    const uList = graph.edges[v] || [];
                    for (const u of uList) {
                        const uX = spacing * u;
                        const vX = spacing * (v - half);
                        p.stroke(128);
                        p.line(uX, uY, vX, vY);
                    }
                }

                // Draw matching edges
                for (const v in matching) {
                    const u = matching[v];
                    const uX = spacing * u;
                    const vX = spacing * (parseInt(v) - half);
                    p.stroke(160, 160, 255);
                    p.strokeWeight(4);
                    p.line(uX, uY, vX, vY);
                }

                // Draw vertices
                for (const u of graph.U) {
                    const uX = spacing * u;
                    p.fill(cover.includes(u) ? '#00C000' : '#333333');
                    p.ellipse(uX, uY, 20);
                    p.fill(255);
                    p.text(u, uX, uY);
                }

                for (const v of graph.V) {
                    const vX = spacing * (v - half);
                    p.fill(cover.includes(v) ? '#00C000' : '#333333');
                    p.ellipse(vX, vY, 20);
                    p.fill(255);
                    p.text(v, vX, vY);
                }
            }
        }
    </script>
</body>
</html>
