<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Time Complexity Visualizer</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
            display: flex;
            justify-content: center;
            flex-direction: column;
            align-items: center;
        }
        #main-container {
            width: 90%;
            max-width: 1200px;
        }
        .control-panel {
            display: flex;
            flex-wrap: wrap;
            gap: 20px;
            margin-bottom: 20px;
            padding: 15px;
            background: #f5f5f5;
            border-radius: 8px;
        }
        .control-group {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .radio-group {
            display: flex;
            gap: 10px;
            align-items: center;
        }
        .slider-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #plot-container {
            width: 100%;
            height: 600px;
        }
        .complexity-group {
            margin-right: 20px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div class="control-panel">
            <!-- Plot Settings -->
            <div class="control-group">
                <div class="radio-group">
                    <label>Scale:</label>
                    <label><input type="radio" id="radio-scale-linear" name="scale-type" checked> linear</label>
                    <label><input type="radio" id="radio-scale-logarithmic" name="scale-type"> logarithmic</label>
                </div>
                <div class="radio-group">
                    <label>Range:</label>
                    <label><input type="radio" id="radio-range-fixed" name="range-type" checked> fixed</label>
                    <label><input type="radio" id="radio-range-automatic" name="range-type"> automatic</label>
                </div>
                <div class="slider-container">
                    <label for="slider-elements">Number of elements:</label>
                    <input type="range" id="slider-elements" min="1" max="20" value="10" step="1">
                    <span id="display-elements">+ 10</span>
                </div>
            </div>

            <!-- Algorithm Selection -->
            <div class="control-group">
                <div class="complexity-group">
                    <span>\\(O(n^2)\\)</span>
                    <div><label><input type="checkbox" id="check-bubble" checked> bubble</label></div>
                    <div><label><input type="checkbox" id="check-selection" checked> selection</label></div>
                    <div><label><input type="checkbox" id="check-insertion" checked> insertion</label></div>
                </div>
                <div class="complexity-group">
                    <span>\\(O(n \\log(n))\\)</span>
                    <div><label><input type="checkbox" id="check-merge"> merge</label></div>
                    <div><label><input type="checkbox" id="check-quick"> quick</label></div>
                    <div><label><input type="checkbox" id="check-heap"> heap</label></div>
                </div>
                <div class="complexity-group">
                    <span>\\(O(n \\cdot n!)\\)</span>
                    <div><label><input type="checkbox" id="check-bogo"> bogo</label></div>
                </div>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Helper function
        function factorial(n) {
            if (n <= 1) return 1;
            return n * factorial(n - 1);
        }

        // Initial state
        let currentN = 10;
        const traces = {
            bubble: { visible: true, color: 'green', width: 5, dash: 'solid' },
            selection: { visible: true, color: 'orange', width: 2, dash: 'dot' },
            insertion: { visible: true, color: 'red', width: 2, dash: 'solid' },
            merge: { visible: false, color: 'blue', width: 2, dash: 'solid' },
            quick: { visible: false, color: 'purple', width: 2, dash: 'solid' },
            heap: { visible: false, color: 'cyan', width: 2, dash: 'solid' },
            bogo: { visible: false, color: 'red', width: 2, dash: 'solid' }
        };

        // Initialize plot
        function createPlot() {
            const x = Array.from({ length: currentN }, (_, i) => i + 1);

            const data = [
                {
                    x: x,
                    y: x.map(n => n ** 2),
                    name: 'bubble',
                    line: { color: traces.bubble.color, width: traces.bubble.width, dash: traces.bubble.dash },
                    visible: traces.bubble.visible
                },
                {
                    x: x,
                    y: x.map(n => 0.25 * n ** 2),
                    name: 'selection',
                    line: { color: traces.selection.color, width: traces.selection.width, dash: traces.selection.dash },
                    visible: traces.selection.visible
                },
                {
                    x: x,
                    y: x.map(n => n * factorial(n)),
                    name: 'insertion',
                    line: { color: traces.insertion.color, width: traces.insertion.width, dash: traces.insertion.dash },
                    visible: traces.insertion.visible || traces.bogo.visible
                },
                {
                    x: x,
                    y: x.map(n => n * Math.log2(n)),
                    name: 'merge',
                    line: { color: traces.merge.color, width: traces.merge.width, dash: traces.merge.dash },
                    visible: traces.merge.visible
                },
                {
                    x: x,
                    y: x.map(n => 1.2 * n * Math.log2(n)),
                    name: 'quick',
                    line: { color: traces.quick.color, width: traces.quick.width, dash: traces.quick.dash },
                    visible: traces.quick.visible
                },
                {
                    x: x,
                    y: x.map(n => 1.5 * n * Math.log2(n)),
                    name: 'heap',
                    line: { color: traces.heap.color, width: traces.heap.width, dash: traces.heap.dash },
                    visible: traces.heap.visible
                }
            ];

            const layout = {
                title: 'time complexity',
                xaxis: { title: 'number of elements', range: [0, 20] },
                yaxis: { title: 'operations', range: [0, 500], type: 'linear' },
                legend: { orientation: 'h', y: -0.2 },
                margin: { t: 40, b: 60 }
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        // Event listeners
        document.getElementById('slider-elements').addEventListener('input', (e) => {
            currentN = parseInt(e.target.value);
            document.getElementById('display-elements').textContent = `+ ${currentN}`;
            updatePlot();
        });

        document.getElementById('radio-scale-linear').addEventListener('change', () => {
            updateLayout({ yaxis: { type: 'linear' } });
        });

        document.getElementById('radio-scale-logarithmic').addEventListener('change', () => {
            updateLayout({ yaxis: { type: 'log' } });
        });

        document.getElementById('radio-range-fixed').addEventListener('change', () => {
            updateLayout({ 
                xaxis: { range: [0, 20], autorange: false },
                yaxis: { range: [0, 500], autorange: false }
            });
        });

        document.getElementById('radio-range-automatic').addEventListener('change', () => {
            updateLayout({ 
                xaxis: { autorange: true },
                yaxis: { autorange: true }
            });
        });

        function updatePlot() {
            const x = Array.from({ length: currentN }, (_, i) => i + 1);

            const update = {
                x: [x, x, x, x, x, x],
                y: [
                    x.map(n => n ** 2),
                    x.map(n => 0.25 * n ** 2),
                    x.map(n => n * factorial(n)),
                    x.map(n => n * Math.log2(n)),
                    x.map(n => 1.2 * n * Math.log2(n)),
                    x.map(n => 1.5 * n * Math.log2(n))
                ]
            };

            const visibility = [
                traces.bubble.visible,
                traces.selection.visible,
                traces.insertion.visible || traces.bogo.visible,
                traces.merge.visible,
                traces.quick.visible,
                traces.heap.visible
            ];

            Plotly.react('plot-container', update, { visible: visibility });
        }

        function updateLayout(layoutUpdate) {
            Plotly.relayout('plot-container', layoutUpdate);
        }

        // Checkbox event listeners
        const checkboxes = {
            'check-bubble': 'bubble',
            'check-selection': 'selection',
            'check-insertion': 'insertion',
            'check-merge': 'merge',
            'check-quick': 'quick',
            'check-heap': 'heap',
            'check-bogo': 'bogo'
        };

        Object.keys(checkboxes).forEach(id => {
            document.getElementById(id).addEventListener('change', (e) => {
                const key = checkboxes[id];
                traces[key].visible = e.target.checked;

                // Special case for insertion/bogo shared trace
                if (key === 'insertion' || key === 'bogo') {
                    traces.insertion.visible = document.getElementById('check-insertion').checked;
                    traces.bogo.visible = document.getElementById('check-bogo').checked;
                    const traceIndex = 2; // Index of insertion trace in data array
                    Plotly.restyle('plot-container', {
                        visible: traces.insertion.visible || traces.bogo.visible
                    }, [traceIndex]);
                } else {
                    const traceIndex = Object.keys(traces).indexOf(key);
                    Plotly.restyle('plot-container', {
                        visible: traces[key].visible
                    }, [traceIndex]);
                }
            });
        });

        // Initial render
        createPlot();
        MathJax.typesetPromise();
    </script>
</body>
</html>
