<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Distillation Bifurcation Demo</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
            background-color: #f5f5f5;
        }
        #main-container {
            width: 550px;
            margin: 0 auto;
            background-color: #f0f0f0;
            padding: 20px;
            border: 1px solid #ccc;
            border-radius: 5px;
        }
        #control-panel {
            display: flex;
            align-items: center;
            margin-bottom: 15px;
        }
        #control-panel label {
            margin-right: 10px;
            width: 160px;
        }
        #slider-xD {
            flex: 1;
        }
        #label-xD {
            margin-left: 10px;
            width: 50px;
            font-weight: bold;
        }
        #plot {
            border: 1px solid #ccc;
            border-radius: 3px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        const slider = document.getElementById('slider-xD');
        const label = document.getElementById('label-xD');

        // Initialize plot on load
        function initPlot() {
            const xD = parseFloat(slider.value);
            label.textContent = xD.toFixed(2);
            updatePlot(xD);
        }

        // Core calculation and plotting function
        function updatePlot(xD) {
            const a = 9;
            const b = -0.6;

            // VLE equilibrium function
            const y = (x) => (a * x) / (1 + (a - 1) * x + b * x * (1 - x));

            // Reflux ratio function
            const r = (x, xD) => {
                const yx = y(x);
                return (xD - yx) / (yx - x);
            };

            // Generate bifurcation curve data
            const x_vals = [];
            const r_vals = [];

            for (let x = 0.001; x <= xD; x += 0.001) {
                const currentR = r(x, xD);
                if (currentR >= 0 && currentR < 5 && isFinite(currentR)) {
                    x_vals.push(x);
                    r_vals.push(currentR);
                }
            }

            // Find tangent pinch point (local maximum)
            const pinch_x = [];
            const pinch_y = [];

            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break; // Only keep the first local maximum
                }
            }

            // Plot configuration
            const curveTrace = {
                x: x_vals,
                y: r_vals,
                mode: 'lines',
                type: 'scatter',
                line: { color: '#8B0000', width: 2.5 }
            };

            const markerTrace = {
                x: pinch_x,
                y: pinch_y,
                mode: 'markers',
                type: 'scatter',
                marker: { color: 'royalblue', size: 10 }
            };

            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    gridcolor: '#e0e0e0',
                    tick0: 0,
                    dtick: 0.2,
                    showticklabels: false
                },
                yaxis: {
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    tick0: 0,
                    dtick: 0.5,
                    showticklabels: false
                },
                showlegend: false,
                margin: { l: 40, r: 20, b: 40, t: 20 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            Plotly.react('plot', [curveTrace, markerTrace], layout);
        }

        // Event listener for slider
        slider.addEventListener('input', () => {
            const xD = parseFloat(slider.value);
            label.textContent = xD.toFixed(2);
            updatePlot(xD);
        });

        // Initialize on page load
        window.addEventListener('load', initPlot);
    </script>
</body>
</html>
