<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Chemical Structure Editor</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #toolbar, #footer-controls {
            display: flex;
            gap: 8px;
            padding: 10px;
            background: #f0f0f0;
            width: 100%;
            box-sizing: border-box;
        }
        #toolbar {
            flex-wrap: wrap;
            justify-content: center;
        }
        #footer-controls {
            justify-content: space-between;
            align-items: center;
        }
        button {
            background: #e0e0e0;
            border: 1px solid #999;
            padding: 5px 10px;
            cursor: pointer;
        }
        button.selected {
            background: #007bff;
            color: white;
            border-color: #0056b3;
        }
        #canvas-container {
            flex: 1;
            display: flex;
            justify-content: center;
            align-items: center;
            width: 100%;
            background: #fff;
            border: 1px solid #ccc;
            box-sizing: border-box;
        }
        #select-structure {
            width: 200px;
        }
    </style>
</head>
<body>
    <div id="toolbar"></div>
    <div id="canvas-container"></div>
    <div id="footer-controls">
        <button id="btn-undo">undo</button>
        <div>
            <select id="select-structure" size="4">
                <option value="bond" selected>—</option>
                <option value="water">Water</option>
                <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
                <option value="borazine">Borazine</option>
                <option value="isopropylamine">Isopropylamine</option>
            </select>
        </div>
        <button id="btn-add">add</button>
    </div>

    <script>
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let dragOffset = { x: 0, y: 0 };
        let initialAngle = 0;

        const charges = ['-', '+', '2'];
        const elements = ['H', 'B', 'C', 'N', 'O', 'F', 'P', 'S', 'Cl', 'Se', 'Br', 'I', 'Li', 'Na', 'K', 'Be', 'Mg', 'Ca', 'Cr', 'Mn', 'Fe', 'Co', 'Cu', 'Zn', 'Al', 'Si'];

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            background(255);
        }

        function draw() {
            background(255);
            sceneObjects.forEach(obj => {
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);
                if (obj.type === 'atom') {
                    textAlign(CENTER, CENTER);
                    textSize(16);
                    text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    drawStructure(obj.value);
                }
                pop();
            });

            if (selectedTool && selectedTool.type === 'atom') {
                textAlign(CENTER, CENTER);
                textSize(16);
                text(selectedTool.value, mouseX, mouseY);
            }
        }

        function drawStructure(name) {
            stroke(0);
            strokeWeight(2);
            fill(0);
            textAlign(CENTER, CENTER);
            textSize(14);

            switch (name) {
                case 'water':
                    line(-20, -20, 0, 0);
                    line(20, -20, 0, 0);
                    text('O', 0, 0);
                    text('H', -20, -20);
                    text('H', 20, -20);
                    break;
                case 'cyclopropyl_ethane':
                    // Simplified representation
                    line(-40, 0, -20, 20);
                    line(-20, 20, -20, -20);
                    line(-20, -20, -40, 0);
                    line(-40, 0, -60, 0);
                    text('C', -60, 0);
                    text('C', -40, 0);
                    text('C', -20, 20);
                    text('C', -20, -20);
                    break;
                case 'borazine':
                    const borazinePts = [[0, -40], [35, -20], [35, 20], [0, 40], [-35, 20], [-35, -20]];
                    for (let i = 0; i < borazinePts.length; i++) {
                        const p1 = borazinePts[i];
                        const p2 = borazinePts[(i+1)%borazinePts.length];
                        line(p1[0], p1[1], p2[0], p2[1]);
                        text(i%2 ? 'B' : 'N', p1[0], p1[1]);
                    }
                    break;
                case 'isopropylamine':
                    line(0, 0, 30, 30);
                    line(0, 0, -30, 30);
                    line(0, 0, 0, -40);
                    text('N', 0, 0);
                    text('H', 40, 40);
                    text('H', -40, 40);
                    text('H', 0, -50);
                    break;
            }
        }

        function mousePressed() {
            if (mouseButton === LEFT) {
                for (let i = sceneObjects.length - 1; i >= 0; i--) {
                    const obj = sceneObjects[i];
                    const d = dist(mouseX, mouseY, obj.x, obj.y);
                    if (d < 25) {
                        activeObject = obj;
                        if (d < 15) {
                            interactionMode = 'rotate';
                            initialAngle = Math.atan2(mouseY - obj.y, mouseX - obj.x) - obj.rotation;
                        } else {
                            interactionMode = 'translate';
                            dragOffset.x = obj.x - mouseX;
                            dragOffset.y = obj.y - mouseY;
                        }
                        break;
                    }
                }
            }
        }

        function mouseDragged() {
            if (activeObject) {
                if (interactionMode === 'translate') {
                    activeObject.x = mouseX + dragOffset.x;
                    activeObject.y = mouseY + dragOffset.y;
                } else if (interactionMode === 'rotate') {
                    const angle = Math.atan2(mouseY - activeObject.y, mouseX - activeObject.x);
                    activeObject.rotation = angle - initialAngle;
                }
            }
        }

        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        function windowResized() {
            resizeCanvas(800, 600);
        }

        // Initialize UI elements
        function initToolbar() {
            const toolbar = document.getElementById('toolbar');

            charges.forEach(charge => {
                const btn = document.createElement('button');
                btn.id = `btn-charge-${charge}`;
                btn.textContent = charge;
                btn.addEventListener('click', () => {
                    selectTool({ type: 'atom', value: charge });
                });
                toolbar.appendChild(btn);
            });

            elements.forEach(element => {
                const btn = document.createElement('button');
                btn.id = `btn-element-${element}`;
                btn.textContent = element;
                btn.addEventListener('click', () => {
                    selectTool({ type: 'atom', value: element });
                });
                toolbar.appendChild(btn);
            });
        }

        function selectTool(tool) {
            selectedTool = tool;
            document.querySelectorAll('#toolbar button').forEach(btn => {
                btn.classList.remove('selected');
            });
            event.target.classList.add('selected');
        }

        document.getElementById('btn-undo').addEventListener('click', () => {
            sceneObjects.pop();
        });

        document.getElementById('btn-add').addEventListener('click', () => {
            const select = document.getElementById('select-structure');
            const value = select.value;
            sceneObjects.push({
                type: 'structure',
                value: value,
                x: width/2,
                y: height/2,
                rotation: 0
            });
        });

        // Initialize the toolbar on page load
        window.addEventListener('DOMContentLoaded', initToolbar);
    </script>
</body>
</html>
