</head>
<body>
  <div id="controls-container">
    <div class="control-row">
      <label for="slider-amplitude">Amplitude \(a\)</label>
      <input type="range" id="slider-amplitude" min="0.1" max="2.0" step="0.01" value="1.5">
      <span id="value-amplitude">1.50</span>
    </div>
    <div class="control-row">
      <label for="slider-frequency">Frequency \(f\)</label>
      <input type="range" id="slider-frequency" min="0.1" max="1.0" step="0.01" value="0.3">
      <span id="value-frequency">0.30</span>
    </div>
    <div class="control-row">
      <label for="slider-wavelength">Wavelength \(\lambda\)</label>
      <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
      <span id="value-wavelength">4.50</span>
    </div>
    <div class="control-row">
      <label for="slider-time">Time \(t\)</label>
      <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
      <span id="value-time">2.50</span>
    </div>
    <div class="control-row">
      <label for="select-phase">Initial phase \(\phi_0\)</label>
      <select id="select-phase">
        <option value="-3.141592653589793">-π</option>
        <option value="-2.0943951023931953">-2π/3</option>
        <option value="-1.5707963267948966" selected>-π/2</option>
        <option value="-1.0471975511965976">-π/3</option>
        <option value="0">0</option>
        <option value="1.0471975511965976">π/3</option>
        <option value="1.5707963267948966">π/2</option>
        <option value="2.0943951023931953">2π/3</option>
        <option value="3.141592653589793">π</option>
      </select>
    </div>
  </div>
  <div id="formula-display"></div>
  <div id="canvas-container"></div>

  <script>
    let A = 1.5, f = 0.3, lambda = 4.5, t = 2.5, phi0 = -Math.PI/2;
    let k = 2 * Math.PI / lambda, omega = 2 * Math.PI * f;

    document.getElementById('value-amplitude').textContent = A.toFixed(2);
    document.getElementById('value-frequency').textContent = f.toFixed(2);
    document.getElementById('value-wavelength').textContent = lambda.toFixed(2);
    document.getElementById('value-time').textContent = t.toFixed(2);

    function update() {
      A = parseFloat(document.getElementById('slider-amplitude').value);
      f = parseFloat(document.getElementById('slider-frequency').value);
      lambda = parseFloat(document.getElementById('slider-wavelength').value);
      t = parseFloat(document.getElementById('slider-time').value);
      phi0 = parseFloat(document.getElementById('select-phase').value);

      document.getElementById('value-amplitude').textContent = A.toFixed(2);
      document.getElementById('value-frequency').textContent = f.toFixed(2);
      document.getElementById('value-wavelength').textContent = lambda.toFixed(2);
      document.getElementById('value-time').textContent = t.toFixed(2);

      k = 2 * Math.PI / lambda;
      omega = 2 * Math.PI * f;

      let formula;
      if (phi0 === Math.PI/2) {
        formula = `y(x,t) = ${A.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
      } else if (phi0 === -Math.PI/2) {
        formula = `y(x,t) = -${A.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
      } else if (phi0 === Math.PI) {
        formula = `y(x,t) = -${A.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
      } else if (phi0 === 0) {
        formula = `y(x,t) = ${A.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
      } else {
        formula = `y(x,t) = ${A.toFixed(2)} \\cos(${omega.toFixed(2)}t - ${k.toFixed(2)}x + ${phi0.toFixed(2)})`;
      }
      document.getElementById('formula-display').innerHTML = formula;
      MathJax.typesetPromise().catch(console.error);
      redraw();
    }

    document.getElementById('slider-amplitude').addEventListener('input', update);
    document.getElementById('slider-frequency').addEventListener('input', update);
    document.getElementById('slider-wavelength').addEventListener('input', update);
    document.getElementById('slider-time').addEventListener('input', update);
    document.getElementById('select-phase').addEventListener('change', update);

    update();

    function setup() {
      const canvas = createCanvas(800, 600);
      canvas.parent('canvas-container');
      noLoop();
    }

    function draw() {
      background(255);
      translate(0, height/2);
      scale(30, -30);

      stroke(0);
      strokeWeight(0.05);
      line(0, 0, 16, 0);
      triangle(16, 0, 15.8, 0.1, 15.8, -0.1);
      line(0, 0, 0, 5);
      triangle(0, 5, 0.1, 4.8, -0.1, 4.8);

      stroke(0, 255, 0);
      strokeWeight(0.02);
      textSize(0.2);
      textAlign(CENTER, TOP);
      for (let x = 1; x <= 16; x++) {
        line(x, 0, x, 5);
        line(x, 0, x, -0.1);
        text(x, x, -0.2);
      }

      stroke(0, 0, 255);
      strokeWeight(0.03);
      noFill();
      beginShape();
      for (let x = 0; x <= 16; x += 0.1) {
        let y = A * Math.cos(omega * t - k * x + phi0);
        vertex(x, y);
      }
      endShape();

      strokeWeight(0.05);
      textSize(0.2);
      textAlign(LEFT, CENTER);
      for (let x = 1; x <= 16; x++) {
        let y = A * Math.cos(omega * t - k * x + phi0);
        let v = -A * omega * Math.sin(omega * t - k * x + phi0);
        let a = -A * omega * omega * Math.cos(omega * t - k * x + phi0);

        fill(0);
        noStroke();
        circle(x, y, 0.1);

        drawArrow(x, y, v, 'red', '\\vec{v}');
        drawArrow(x, y, a, 'black', '\\vec{a}');
      }
    }

    function drawArrow(baseX, baseY, vectorY, color, label) {
      const scaleFactor = color === 'red' ? 15 : 5;
      const length = vectorY * scaleFactor;

      stroke(color);
      strokeWeight(0.03);
      line(baseX, baseY, baseX, baseY + length);

      fill(color);
      noStroke();
      triangle(baseX, baseY + length,
               baseX - 0.05, baseY + length - 0.1,
               baseX + 0.05, baseY + length - 0.1);

      fill(color);
      noStroke();
      text(label, baseX + 0.1, baseY + length);
    }
  </script>
</body>
</html>
