        <div id="canvas-container"></div>
        <div id="energy-display">energy = <span id="energy-value"></span></div>
    </div>

    <script>
        let eccentricity = 0;
        const p = 2; // Semi-latus rectum
        let energyDisplay = document.getElementById('energy-value');

        // Initialize p5.js sketch
        new p5(function(p) {
            p.setup = function() {
                const canvas = p.createCanvas(700, 500);
                canvas.parent('canvas-container');
                p.stroke('#4a80b5');
                p.strokeWeight(2);
                p.fill(0);
                updateVisualization();
            };

            p.draw = function() {
                // Clear canvas
                p.background('#f0f0f0');

                // Transform coordinate system
                p.translate(p.width * 0.7, p.height / 2);
                p.scale(30, -30); // 30 pixels per unit, invert y-axis

                // Draw coordinate axes
                p.stroke(0);
                p.strokeWeight(1);

                // X-axis
                p.line(-20, 0, 10, 0);
                for (let x = -15; x <= 5; x++) {
                    if (x !== 0) {
                        p.line(x, -0.1, x, 0.1);
                        p.textAlign(p.CENTER, p.TOP);
                        p.text(x, x, 0.2);
                    }
                }
                p.textAlign(p.LEFT, p.CENTER);
                p.text("Global'x", 5.2, 0);

                // Y-axis
                p.line(0, -8, 0, 8);
                for (let y = -7; y <= 7; y++) {
                    if (y !== 0) {
                        p.line(-0.1, y, 0.1, y);
                        p.textAlign(p.RIGHT, p.CENTER);
                        p.text(y, -0.2, y);
                    }
                }
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("Global'y", 0, 8.2);

                // Draw central body
                p.noStroke();
                p.fill(0);
                p.ellipse(0, 0, 0.2, 0.2);

                // Draw orbit
                p.stroke('#4a80b5');
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                if (eccentricity === 0) {
                    // Circle case
                    for (let theta = 0; theta < p.TWO_PI; theta += 0.02) {
                        let x = 2 * p.cos(theta) - 2;
                        let y = 2 * p.sin(theta);
                        p.vertex(x, y);
                    }
                } else {
                    let thetaStart, thetaEnd;
                    if (eccentricity < 1) {
                        thetaStart = 0;
                        thetaEnd = p.TWO_PI;
                    } else if (eccentricity === 1) {
                        thetaStart = -2.2;
                        thetaEnd = 2.2;
                    } else {
                        let limit = p.acos(-1 / eccentricity);
                        thetaStart = -limit;
                        thetaEnd = limit;
                    }

                    for (let theta = thetaStart; theta <= thetaEnd; theta += 0.02) {
                        let r = p / (1 + eccentricity * p.cos(theta));
                        let x = r * p.cos(theta);
                        let y = r * p.sin(theta);
                        p.vertex(x, y);
                    }
                }
                p.endShape();
            };
        });

        // Event listener for slider
        document.getElementById('slider-eccentricity').addEventListener('input', function() {
            eccentricity = parseFloat(this.value);
            document.getElementById('value-eccentricity').textContent = eccentricity.toFixed(1);
            updateEnergyDisplay();
            updateVisualization();
        });

        function updateEnergyDisplay() {
            const E = -0.25 * (1 - Math.pow(eccentricity, 2));
            if (eccentricity === 0) {
                energyDisplay.innerHTML = '\\(-\\frac{1}{4}\\)';
                MathJax.typeset();
            } else {
                energyDisplay.textContent = E.toFixed(2);
            }
        }

        function updateVisualization() {
            const sketch = document.querySelector('#canvas-container canvas');
            if (sketch) {
                sketch.getContext('2d').clearRect(0, 0, sketch.width, sketch.height);
                sketch.p5.draw();
            }
        }

        // Initialize with default value
        updateEnergyDisplay();
    </script>
