            document.getElementById('value-hg_m').textContent = sliders.hg_m.value.toFixed(1);
            document.getElementById('value-k').textContent = sliders.k.value.toFixed(3);
            document.getElementById('value-a').textContent = sliders.a.value.toFixed(3);
