                dragmode: false,
                showlegend: true
            };
            Plotly.newPlot('plot2', traces2, layout2);

            // Plot 3 Initialization
            const hgT = Array.from({length: 101}, (_, i) => i * 0.02);
            const kVal = parseFloat(sliders.k.value);
            const aVal = parseFloat(sliders.a.value);
            const meHg = hgT.map(h => (aVal * h) / (kVal + h));
            const trace3 = {
                x: hgT,
                y: meHg,
                mode: 'lines',
                line: { color: '#1f77b4' },
                fill: 'tozeroy',
                fillcolor: 'rgba(31, 119, 180, 0.2)'
