                annotations: [{
                    x: 1.0,
                    y: 0.05,
                    text: `[MeHg] = ${aVal.toFixed(3)}`,
                    showarrow: false,
                    font: { size: 14 },
                    bgcolor: 'rgba(255, 255, 255, 0.7)'
                }]
            };
            Plotly.newPlot('plot3', [trace3], layout3);
        }

        function updatePlots() {
            const srb = Array.from({length: 100}, (_, i) => i + 1);
            const poc = parseFloat(sliders.poc.value);
            const eh = parseInt(sliders.eh.value);
            const hg_m = parseFloat(sliders.hg_m.value);
            const k = parseFloat(sliders.k.value);
            const a = parseFloat(sliders.a.value);

            // Update Plot 1
            const C1 = 125 * (-eh + 200) * poc * hg_m;
            const mi1 = srb.map(s => C1 / s);
            Plotly.restyle('plot1', 'y', [mi1]);

            // Update Plot 2
            const fixedPOCs = [56, 6, 0.3];
            fixedPOCs.forEach((pocVal, i) => {
                const C = 125 * (-eh + 200) * pocVal * hg_m;
                const mi = srb.map(s => C / s);
                Plotly.restyle('plot2', 'y', [mi], [i]);
            });

            // Update Plot 3
            const hgT = Array.from({length: 101}, (_, i) => i * 0.02);
            const meHg = hgT.map(h => (a * h) / (k + h));
            Plotly.restyle('plot3', 'y', [meHg]);
            Plotly.relayout('plot3', {
                annotations: [{
                    text: `[MeHg] = ${a.toFixed(3)}`
                }]
            });
        }

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                updateDisplay();
                updatePlots();
            });
        });

        window.onload = function() {
            updateDisplay();
            initPlots();
        };
    </script>
</body>
