</head>
<body>
    <h1>Quantum Computer Search Algorithms</h1>
    <div class="main-container">
        <div class="control-panel">
            <div class="control-section">
                <span>algorithm</span>
                <div class="algorithm-buttons">
                    <button id="btn-unstructured">unstructured</button>
                    <button id="btn-adiabatic">adiabatic</button>
                    <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                    <button id="btn-heuristic" class="active">heuristic</button>
                </div>
            </div>
            <div class="control-section">
                <label for="slider-total-steps">number of steps to complete the search</label>
                <div class="slider-container">
                    <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
                    <span id="display-total-steps">10</span>
                </div>
            </div>
            <div class="control-section">
                <label for="slider-current-step" id="label-current-step">current step (0 to 10)</label>
                <div class="slider-container">
                    <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
                    <span id="display-current-step">0</span>
                </div>
            </div>
        </div>
        <div class="visualization-area">
            <div class="plot-container">
                <div class="plot-title">amplitudes</div>
                <div id="plot-amplitudes"></div>
            </div>
            <div class="plot-container">
                <div class="plot-mode-buttons">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <div id="plot-probability"></div>
            </div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/mathjs/12.2.2/math.min.js"></script>
    <script>
        const N = 64;
        let solutions = [];
        let conflictNumbers = [];
        let algorithm = 'heuristic';
        let totalSteps = 10;
        let currentStep = 0;
        let plotMode = 'conflicts';

        // Initialize problem instance
        function generateProblem() {
            solutions = [];
            while (solutions.length < 2) {
                const candidate = Math.floor(Math.random() * N);
                if (!solutions.includes(candidate)) solutions.push(candidate);
            }
            conflictNumbers = Array(N).fill(0).map((_, i) => {
                if (solutions.includes(i)) return 0;
                return Math.min(...solutions.map(s => hammingDistance(i, s)));
            });
        }

        function hammingDistance(a, b) {
            let xor = a ^ b;
            let count = 0;
            while (xor) {
                count += xor & 1;
                xor >>= 1;
            }
            return count;
        }

        // Algorithm implementations
        function calculateStateVector() {
            const s = currentStep / totalSteps;
            let state = Array(N).fill(0).map(() => math.complex(1 / Math.sqrt(N), 0));

            if (algorithm === 'unstructured') {
                const Uw = createGroverOracle();
                const Us = createDiffusionOperator();
                const G = math.multiply(Us, Uw);
                for (let k = 0; k < currentStep; k++) {
                    state = math.multiply(G, state);
                }
            } else if (algorithm === 'adiabatic') {
                const H = createAdiabaticHamiltonian(s);
                const eigens = math.eigs(H);
                const groundStateIndex = eigens.values.findIndex(v => math.re(v) === Math.min(...eigens.values.map(v => math.re(v))));
                state = eigens.vectors[groundStateIndex].map(v => v);
            } else if (algorithm === 'discrete-adiabatic' || algorithm === 'heuristic') {
                const T = totalSteps;
                state = Array(N).fill(0).map(() => math.complex(1 / Math.sqrt(N), 0));
                for (let k = 0; k < currentStep; k++) {
                    const s_k = k / T;
                    const H = createAdiabaticHamiltonian(s_k);
                    const U = math.expm(math.multiply(math.complex(0, -1), H));
                    state = math.multiply(U, state);
                }
            }
            return state;
        }

        function createGroverOracle() {
            const diag = Array(N).fill(1);
            solutions.forEach(s => diag[s] = -1);
            return math.diag(diag);
        }

        function createDiffusionOperator() {
            const P0 = math.multiply(math.ones(N, N), 1 / N);
            const I = math.eye(N);
            return math.subtract(math.multiply(2, P0), I);
        }

        function createAdiabaticHamiltonian(s) {
            const H0 = math.multiply(-1, math.outerMultiply(math.ones(N), math.ones(N)) / N);
            const Hp = math.diag(conflictNumbers);
            return math.add(math.multiply(1 - s, H0), math.multiply(s, Hp));
        }

        // Plotting functions
        function plotAmplitudes(state) {
            const re = state.map(c => math.re(c));
            const im = state.map(c => math.im(c));
            const colors = state.map((_, i) => {
                if (solutions.includes(i)) return 'black';
                if (currentStep === 0) return 'red';
                const conflict = conflictNumbers[i];
                return `hsl(${120 - conflict * 3}, 100%, 50%)`;
            });

            const data = [{
                x: re,
                y: im,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    color: colors,
                    size: solutions.includes(i => i) ? 10 : 6,
                    line: {width: 1}
                }
            }];

            const layout = {
                title: 'Amplitudes',
                xaxis: {title: 'Re', range: [-1, 1]},
                yaxis: {title: 'Im', range: [-1, 1]},
                hovermode: 'closest',
                margin: {l: 50, r: 50, b: 50, t: 50}
            };

            Plotly.newPlot('plot-amplitudes', data, layout);
        }

        function plotProbability(state) {
            let x, y, xlabel;
            if (plotMode === 'conflicts') {
                const groups = {};
                state.forEach((c, i) => {
                    const key = conflictNumbers[i];
                    groups[key] = (groups[key] || 0) + math.pow(math.abs(c), 2);
                });
                x = Object.keys(groups).map(Number);
                y = Object.values(groups);
                xlabel = 'number of conflicts';
            } else {
                if (algorithm === 'unstructured') {
                    const G = math.multiply(createDiffusionOperator(), createGroverOracle());
                    const eigens = math.eigs(G);
                    const projections = eigens.vectors.map(v => {
                        const proj = math.dotDivide(state, v);
                        return math.pow(math.abs(proj), 2);
                    });
                    x = eigens.values.map(v => math.arg(v));
                    y = projections;
                    xlabel = 'arg(eigenvalue)';
                } else {
                    x = conflictNumbers;
                    y = state.map(c => math.pow(math.abs(c), 2));
                    xlabel = 'eigenvalue';
                }
            }

            const data = [{
                x: x,
                y: y,
                type: 'bar',
                marker: {color: 'rgba(0, 123, 255, 0.7)'}
            }];

            const layout = {
                title: 'Probability Distribution',
                xaxis: {title: xlabel},
                yaxis: {title: 'probability', range: [0, 1]},
                margin: {l: 50, r: 50, b: 50, t: 50}
            };

            Plotly.newPlot('plot-probability', data, layout);
        }

        // Event handlers
        function update() {
            const state = calculateStateVector();
            plotAmplitudes(state);
            plotProbability(state);
        }

        function setupEventListeners() {
            const algorithmButtons = {
                'btn-unstructured': 'unstructured',
                'btn-adiabatic': 'adiabatic',
                'btn-discrete-adiabatic': 'discrete-adiabatic',
                'btn-heuristic': 'heuristic'
            };
            Object.entries(algorithmButtons).forEach(([id, name]) => {
                document.getElementById(id).addEventListener('click', () => {
                    algorithm = name;
                    document.querySelectorAll('.algorithm-buttons button').forEach(btn => btn.classList.remove('active'));
                    document.getElementById(id).classList.add('active');
                    const defaults = {unstructured: 5, adiabatic: 200, 'discrete-adiabatic': 200, heuristic: 10};
                    totalSteps = defaults[name];
                    document.getElementById('slider-total-steps').value = totalSteps;
                    document.getElementById('display-total-steps').textContent = totalSteps;
                    const currentStepSlider = document.getElementById('slider-current-step');
                    currentStepSlider.max = totalSteps;
                    currentStepSlider.value = 0;
                    document.getElementById('display-current-step').textContent = 0;
                    document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps})`;
                    update();
                });
            });

            document.getElementById('slider-total-steps').addEventListener('input', (e) => {
                totalSteps = parseInt(e.target.value);
                document.getElementById('display-total-steps').textContent = totalSteps;
                const currentStepSlider = document.getElementById('slider-current-step');
                currentStepSlider.max = totalSteps;
                if (currentStep > totalSteps) {
                    currentStep = totalSteps;
                    currentStepSlider.value = totalSteps;
                    document.getElementById('display-current-step').textContent = totalSteps;
                }
                document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps})`;
                update();
            });

            document.getElementById('slider-current-step').addEventListener('input', (e) => {
                currentStep = parseInt(e.target.value);
                document.getElementById('display-current-step').textContent = currentStep;
                update();
            });

            document.getElementById('btn-conflicts').addEventListener('click', () => {
                plotMode = 'conflicts';
                document.getElementById('btn-conflicts').classList.add('active');
                document.getElementById('btn-eigenvalues').classList.remove('active');
                update();
            });

            document.getElementById('btn-eigenvalues').addEventListener('click', () => {
                plotMode = 'eigenvalues';
                document.getElementById('btn-eigenvalues').classList.add('active');
                document.getElementById('btn-conflicts').classList.remove('active');
                update();
            });
        }

        // Initialize
        generateProblem();
        setupEventListeners();
        update();
    </script>
</body>
</html>
