        // Event listeners for controls
        selectPlot.addEventListener('change', updatePlot);
        sliderPressure.addEventListener('input', () => {
            valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(2);
            updatePlot();
        });
        sliderTemperature.addEventListener('input', () => {
            valueTemperature.textContent = Math.round(sliderTemperature.value);
            updatePlot();
        });
        checkboxHP.addEventListener('change', updatePlot);

        // Initial setup
        valuePressure.textContent = sliderPressure.value;
        valueTemperature.textContent = sliderTemperature.value;

        function updatePlot() {
            const plotType = selectPlot.value;

            // Toggle visibility of controls
            if (plotType === 'vs_temp') {
                controlGroupPressure.style.display = 'flex';
                controlGroupTemperature.style.display = 'none';
                checkboxContainer.style.display = 'none';
