        function createTemperaturePlotData() {
            const P = parseFloat(sliderPressure.value);
            const T_sat = 5818 / (8.65 - Math.log10(P)) - 230;

            // Generate temperature range in °C
            const T = Array.from({length: 200}, (_, i) => 280 + i * 0.6);

            // Calculate fugacity curves
            const f_sat = T.map(t => Math.pow(10, 8.65 - 5818 / (230 + t)));
            const f_vapor = T.map(() => P);

            // Split into stable/metastable regions
            const splitIndex = T.findIndex(t => t >= T_sat);

            return [
                // Stable liquid
                {
                    x: T.slice(0, splitIndex), 
                    y: f_sat.slice(0, splitIndex),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2},
