                    y: f_sat.slice(splitIndex),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#A0A0FF', width: 2, dash: 'dash'},
                    name: 'Metastable Liquid'
                },
                // Metastable vapor
                {
                    x: T.slice(0, splitIndex), 
                    y: f_vapor.slice(0, splitIndex),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#A0A0FF', width: 2, dash: 'dash'},
                    name: 'Metastable Vapor'
                },
                // Saturation point
                {
                    x: [T_sat], 
                    y: [P],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {color: '#000000', size: 8},
                    name: 'Saturation Point'
                },
                // Indicator line
                {
                    x: [T_sat, T_sat], 
                    y: [0, P],
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 2, dash: 'dash'},
                    name: 'Indicator'
                }
            ];
        }

        function createTemperaturePlotLayout() {
            const P = parseFloat(sliderPressure.value);
            return {
                title: 'Fugacity vs Temperature',
                xaxis: {
                    title: 'temperature (°C)',
                    range: [280, 400],
                    gridcolor: '#e0e0e0',
                    tickfont: {size: 12}
                },
                yaxis: {
                    title: 'fugacity (bar)',
