                    {x: 310, y: 0.01, text: 'liquid', showarrow: false},
                    {x: 390, y: P, text: 'vapor', showarrow: false},
                    {x: T_sat + 5, y: P, text: 'T<sup>sat</sup>', showarrow: false}
                ],
                margin: {l: 50, r: 50, b: 50, t: 50},
                paper_bgcolor: '#ffffff',
                font: {family: 'Arial, sans-serif', size: 14}
            };
        }

        function createPressurePlotData() {
            const T = parseFloat(sliderTemperature.value);
            const P_sat = Math.pow(10, 10.83 - 5047 / T);
            const f_sat = checkboxHP.checked ? P_sat * Math.exp(-25 * P_sat / T) : P_sat;

            // Generate pressure range in MPa (0 to 3 MPa = 30 bar)
            const P = Array.from({length: 200}, (_, i) => i * 0.015);

            // Calculate fugacity curves
            const f_vapor = P.map(p => 
                checkboxHP.checked ? p * Math.exp(-25 * p / T) : p
            );

            // Split into stable/metastable regions
            const splitIndex = P.findIndex(p => p >= P_sat);

            return [
                // Stable vapor
                {
                    x: P.slice(0, splitIndex), 
                    y: f_vapor.slice(0, splitIndex),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2},
                    name: 'Stable Vapor'
                },
                // Stable liquid
                {
                    x: P.slice(splitIndex), 
                    y: P.slice(splitIndex).map(() => f_sat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#0000FF', width: 2},
                    name: 'Stable Liquid'
                },
                // Metastable vapor
                {
                    x: P.slice(splitIndex), 
                    y: f_vapor.slice(splitIndex),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#A0A0FF', width: 2, dash: 'dash'},
                    name: 'Metastable Vapor'
                },
                // Metastable liquid
                {
                    x: P.slice(0, splitIndex), 
                    y: P.slice(0, splitIndex).map(() => f_sat),
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#A0A0FF', width: 2, dash: 'dash'},
