                    name: 'Saturation Point'
                },
                // Indicator line
                {
                    x: [P_sat, P_sat], 
                    y: [0, f_sat],
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: '#000000', width: 2, dash: 'dash'},
                    name: 'Indicator'
                }
            ];
        }

        function createPressurePlotLayout() {
            const T = parseFloat(sliderTemperature.value);
            const P_sat = Math.pow(10, 10.83 - 5047 / T);
            const f_sat = checkboxHP.checked ? P_sat * Math.exp(-25 * P_sat / T) : P_sat;

            return {
                title: 'Fugacity vs Pressure',
                xaxis: {
                    title: 'pressure (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    tickfont: {size: 12}
                },
                yaxis: {
                    title: 'fugacity (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    tickfont: {size: 12}
                },
                annotations: [
                    {x: 0.5, y: 0.25, text: 'vapor', showarrow: false},
                    {x: 2.7, y: f_sat, text: 'liquid', showarrow: false},
                    {x: P_sat + 0.1, y: f_sat, text: 'P<sup>sat</sup>', showarrow: false}
                ],
                margin: {l: 50, r: 50, b: 50, t: 50},
                paper_bgcolor: '#ffffff',
                font: {family: 'Arial, sans-serif', size: 14}
            };
        }

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
