    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
        }
        #visualization-container {
            display: flex;
            width: 100%;
            margin-bottom: 20px;
        }
        #canvas-container, #plot-container {
