    </style>
</head>
<body>
    <div>
        <h1>Quantum Observables Visualization</h1>
        <div id="visualization-container">
            <div id="canvas-container">
                <canvas id="canvas-bloch"></canvas>
            </div>
            <div id="plot-container">
                <div id="plot-error"></div>
            </div>
        </div>
        <div id="control-panel">
            <div class="control-group">
                <p><b>incompatibility of target observables</b></p>
                <label for="slider-theta">θ</label>
                <input type="range" id="slider-theta" min="0" max="1.57079" step="0.001">
                <span id="span-theta-value">π/7</span>
                <span style="margin-left: 20px;">
                    sin<sup>2</sup>θ = <span id="span-sinsq-theta-value">0.188255</span>
                </span>
            </div>
            <hr>
            <div class="control-group">
                <p><b>metric error measure</b></p>
                <input type="checkbox" id="checkbox-metric-circle">
                <label for="checkbox-metric-circle">display circle of equal metric error</label>
                <input type="checkbox" id="checkbox-metric-region" style="margin-left: 20px;">
                <label for="checkbox-metric-region">display region of joint measurability</label>
            </div>
            <hr>
            <div class="control-group">
                <p><b>first-order optimization</b></p>
                <input type="checkbox" id="checkbox-first-order-vec">
                <label for="checkbox-first-order-vec">display first-order optimizing <i>d</i> vector</label>
                <input type="checkbox" id="checkbox-first-order-region" style="margin-left: 20px;">
                <label for="checkbox-first-order-region">display regions of joint measurability</label>
            </div>
            <hr>
            <div class="control-group">
                <p><b>optimal approximators</b></p>
                <input type="checkbox" id="checkbox-optimal-pair">
                <label for="checkbox-optimal-pair">display closest optimal pair of approximating vectors</label>
                <input type="checkbox" id="checkbox-optimal-region" style="margin-left: 20px;">
                <label for="checkbox-optimal-region">display regions of joint measurability</label>
            </div>
        </div>
    </div>

    <script>
        let theta = Math.PI / 7;
        let R = 150;
        let a, b, c, d_opt;
        let sinThetaSpan, sinSqThetaSpan;
        let metricCircleCheckbox, metricRegionCheckbox, firstOrderVecCheckbox, firstOrderRegionCheckbox, optimalPairCheckbox, optimalRegionCheckbox;
        let isDragging = false;
        let dragOffset = createVector(0, 0);

        function setup() {
            let canvas = createCanvas(400, 400);
            canvas.parent('canvas-container');
            translate(width / 2, height);
            noLoop();
            recalculateVectors();
            calculateDOpt();
            updatePlotBoundary();
        }

        function draw() {
            background(255);
            drawSemiCircle();
            drawVectors();
            drawOptionalElements();
        }

        function drawSemiCircle() {
            noFill();
            stroke(0);
            arc(0, 0, 2 * R, 2 * R, 0, PI);
            line(-R, 0, R, 0);
        }

        function drawVectors() {
            drawVector(a, 'blue', 'a');
            drawVector(b, 'red', 'b');
            drawVector(c, 'green', 'c');
        }

        function drawVector(v, color, label) {
            push();
            stroke(color);
            fill(color);
            line(0, 0, v.x, v.y);
            let angle = Math.atan2(v.y, v.x);
            translate(v.x, v.y);
            rotate(angle);
            triangle(0, 0, -5, 2, -5, -2);
            fill(0);
            noStroke();
            text(label, 5, 0);
            pop();
        }

        function drawOptionalElements() {
            if (metricCircleCheckbox.checked) {
                let radius = dist(c.x, c.y, a.x, a.y);
                push();
                translate(a.x, a.y);
                stroke(0, 0, 255, 100);
                drawingContext.setLineDash([5, 5]);
                ellipse(0, 0, 2 * radius, 2 * radius);
                drawingContext.setLineDash([]);
                pop();
            }
            if (metricRegionCheckbox.checked || firstOrderRegionCheckbox.checked) {
                let cMag = c.mag();
                stroke(0, 255, 0, 100);
                drawingContext.setLineDash([5, 5]);
                ellipse(0, 0, 2 * cMag, 2 * cMag);
                if (d_opt) {
                    let dMag = d_opt.mag();
                    stroke(255, 165, 0, 100);
                    ellipse(0, 0, 2 * dMag, 2 * dMag);
                }
                drawingContext.setLineDash([]);
            }
            if (firstOrderVecCheckbox.checked && d_opt) {
                let dErr = p5.Vector.sub(d_opt, b);
                push();
                translate(b.x, b.y);
                stroke(255, 165, 0);
                drawingContext.setLineDash([5, 5]);
                line(0, 0, dErr.x, dErr.y);
                let angle = dErr.heading();
                translate(dErr.x, dErr.y);
                rotate(angle);
                line(0, 0, -5, 2, -5, -2);
                drawingContext.setLineDash([]);
                pop();
                fill(255, 165, 0);
                noStroke();
                text('d_err', b.x + dErr.x / 2, b.y + dErr.y / 2);
            }
            if (optimalPairCheckbox.checked) {
                let sin2Theta = Math.sin(2 * theta);
                let optimalError = (sin2Theta / 2) ** 2;
                let optimalLength = Math.sqrt(2 * optimalError) * R;
                let c_opt = createVector(optimalLength, optimalLength).mult(R / Math.sqrt(2));
                drawVector(c_opt, 'gray', 'c_opt');
                let d_opt_optimal = createVector(-optimalLength, optimalLength).mult(R / Math.sqrt(2));
                drawVector(d_opt_optimal, 'gray', 'd_opt');
            }
        }

        function recalculateVectors() {
            let sinTheta = Math.sin(theta);
            let cosTheta = Math.cos(theta);
            a = createVector(R * sinTheta, R * cosTheta);
            b = createVector(-R * sinTheta, R * cosTheta);
            if (!c) {
                c = createVector(R * 0.3, R * 0.7);
            }
        }

        function calculateDOpt() {
            if (!c || c.mag() === 0) {
                d_opt = createVector(0, 0);
                return;
            }
            let cHat = c.copy().normalize();
            let perpCHat = createVector(-cHat.y, cHat.x);
            let bParallel = b.dot(cHat);
            let bPerp = b.dot(perpCHat);
            let aEllipse = Math.sqrt(1 - c.magSq());
            let bEllipse = 1;

            let bestAlpha = 0, bestBeta = 0, minDistSq = Infinity;
            for (let phi = 0; phi < 2 * Math.PI; phi += 0.05) {
                let alpha = aEllipse * Math.cos(phi);
                let beta = bEllipse * Math.sin(phi);
                let distSq = (alpha - bParallel) ** 2 + (beta - bPerp) ** 2;
                if (distSq < minDistSq) {
                    minDistSq = distSq;
                    bestAlpha = alpha;
                    bestBeta = beta;
                }
            }
            d_opt = cHat.copy().mult(bestAlpha).add(perpCHat.copy().mult(bestBeta));
        }

        function mousePressed() {
            if (mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) return false;
            let d = dist(mouseX, mouseY, c.x, c.y);
            if (d < 10) {
                isDragging = true;
                dragOffset.x = c.x - mouseX;
                dragOffset.y = c.y - mouseY;
            }
            return false;
        }

        function mouseDragged() {
            if (isDragging) {
                let newCx = mouseX + dragOffset.x;
                let newCy = mouseY + dragOffset.y;
                let distance = dist(newCx, newCy, 0, 0);
                if (distance > R) {
                    newCx = (newCx / distance) * R;
                    newCy = (newCy / distance) * R;
                }
                if (newCy < 0) newCy = 0;
                c.set(newCx, newCy);
                calculateDOpt();
                updateErrorPlot();
                redrawAll();
            }
            return false;
        }

        function mouseReleased() {
            isDragging = false;
            return false;
        }

        function initPlot() {
            let xData = [];
            let yData = [];
            let sin2Theta = Math.sin(2 * theta);
            let maxSin2ThetaSq = sin2Theta ** 2;
            for (let x = 0; x <= maxSin2ThetaSq; x += 0.01) {
                yData.push((sin2Theta - Math.sqrt(x)) ** 2);
                xData.push(x);
            }
            let boundaryTrace = {
                x: xData,
                y: yData,
                mode: 'lines',
                line: { color: 'blue' },
                name: 'Boundary'
            };
            let currentErrorTrace = {
                x: [0.5 * p5.Vector.dist(c, a) ** 2],
                y: [0.5 * p5.Vector.dist(d_opt, b) ** 2],
                mode: 'markers',
                marker: { color: 'red', size: 10 },
                name: 'Current Error'
            };
            let optimalErrorTrace = {
                x: [((Math.sin(2 * theta) / 2) ** 2)],
                y: [((Math.sin(2 * theta) / 2) ** 2)],
                mode: 'markers',
                marker: { color: 'green', size: 10 },
                name: 'Optimal Error',
                visible: optimalPairCheckbox.checked ? true : 'legendonly'
            };
            Plotly.newPlot('plot-error', [boundaryTrace, currentErrorTrace, optimalErrorTrace], {
                title: 'Error Tradeoff',
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(C, B)', range: [0, 1.5] },
                showlegend: false
            });
        }

        function updatePlotBoundary() {
            let xData = [];
            let yData = [];
            let sin2Theta = Math.sin(2 * theta);
            let maxSin2ThetaSq = sin2Theta ** 2;
            for (let x = 0; x <= maxSin2ThetaSq; x += 0.01) {
                yData.push((sin2Theta - Math.sqrt(x)) ** 2);
                xData.push(x);
            }
            Plotly.restyle('plot-error', {
                x: [xData],
                y: [yData]
            }, [0]);
            updateOptimalErrorPoint();
        }

        function updateErrorPlot() {
            let errorCA = 0.5 * p5.Vector.dist(c, a) ** 2;
            let errorDB = 0.5 * p5.Vector.dist(d_opt, b) ** 2;
            Plotly.restyle('plot-error', {
                x: [[errorCA]],
                y: [[errorDB]]
            }, [1]);
        }

        function updateOptimalErrorPoint() {
            let sin2Theta = Math.sin(2 * theta);
            let optimalError = (sin2Theta / 2) ** 2;
            Plotly.restyle('plot-error', {
                x: [[optimalError]],
                y: [[optimalError]]
            }, [2]);
        }

        function updateThetaDisplay() {
            sinThetaSpan.textContent = theta.toFixed(4);
            sinSqThetaSpan.textContent = (Math.sin(theta) ** 2).toFixed(5);
        }

        window.onload = function () {
            thetaSlider = document.getElementById('slider-theta');
            sinThetaSpan = document.getElementById('span-theta-value');
            sinSqThetaSpan = document.getElementById('span-sinsq-theta-value');
            metricCircleCheckbox = document.getElementById('checkbox-metric-circle');
            metricRegionCheckbox = document.getElementById('checkbox-metric-region');
            firstOrderVecCheckbox = document.getElementById('checkbox-first-order-vec');
            firstOrderRegionCheckbox = document.getElementById('checkbox-first-order-region');
            optimalPairCheckbox = document.getElementById('checkbox-optimal-pair');
            optimalRegionCheckbox = document.getElementById('checkbox-optimal-region');

            thetaSlider.value = Math.PI / 7;
            updateThetaDisplay();
            initPlot();

            thetaSlider.addEventListener('input', function () {
                theta = parseFloat(this.value);
                updateThetaDisplay();
                recalculateVectors();
                updatePlotBoundary();
                calculateDOpt();
                updateErrorPlot();
                redrawAll();
            });

            [metricCircleCheckbox, metricRegionCheckbox, firstOrderVecCheckbox, firstOrderRegionCheckbox, optimalPairCheckbox, optimalRegionCheckbox].forEach(checkbox => {
                checkbox.addEventListener('change', () => {
                    redrawAll();
                    if (checkbox === optimalPairCheckbox) {
                        updateOptimalErrorPoint();
                        Plotly.restyle('plot-error', {
                            visible: optimalPairCheckbox.checked ? true : 'legendonly'
                        }, [2]);
                    }
                });
            });
        };
    </script>
</body>
</html>
