      margin-right: 10px;
    }
  </style>
</head>
<body>
  <div id="control-panel">
    <h4>Choose 'regular points' below, then the number of regular points,</h4>
    <div id="regular-points-selector">
      <button id="btn-regular-3">3</button>
      <button id="btn-regular-4" class="active">4</button>
      <button id="btn-regular-5">5</button>
    </div>
    <p>and drag them anywhere.</p>
    <p>Once these points are chosen, click 'Steiner points' and choose how many you want to add.</p>
    <h4>Now choose 'Steiner points' and how many you want to use.</h4>
    <div>
      <input type="radio" name="point-mode" id="radio-regular">
      <label for="radio-regular">regular points</label>
      <input type="radio" name="point-mode" id="radio-steiner" checked>
      <label for="radio-steiner">Steiner points</label>
    </div>
    <div id="steiner-points-selector">
      <button id="btn-steiner-0">0</button>
      <button id="btn-steiner-1">1</button>
      <button id="btn-steiner-2" class="active">2</button>
      <button id="btn-steiner-3">3</button>
    </div>
    <p>You can drag them anywhere.</p>
    <p>Can you find an optimal position to minimize the total length?</p>
    <hr>
    <div>
      <label for="checkbox-show-angles">show the angles</label>
      <input type="checkbox" id="checkbox-show-angles" checked>
    </div>
    <hr>
    <div>
      <p>The total length of the network is:</p>
      <h3 id="text-total-length">0.00000</h3>
    </div>
  </div>
  <div id="canvas-container"></div>

  <script>
    let numRegularPoints = 4;
    let numSteinerPoints = 2;
    let pointMode = 'steiner';
    let showAngles = true;
    let totalLength = 0;

    let regularPoints = [];
    let steinerPoints = [];
    let mstEdges = [];
    let draggingPoint = null;
    let draggingType = null;

    function setup() {
      const canvas = createCanvas(800, 600);
      canvas.parent('canvas-container');
      resetPoints();
      calculateMST();
      updateActiveButtons();
      document.getElementById('checkbox-show-angles').checked = showAngles;
    }

    function resetPoints() {
      const centerX = width / 2;
      const centerY = height / 2;
      const radius = 0.35 * width;

      regularPoints = [];
      for (let i = 0; i < numRegularPoints; i++) {
        const angle = TWO_PI * i / numRegularPoints;
        regularPoints.push(createVector(centerX + radius * cos(angle), centerY + radius * sin(angle)));
      }

      steinerPoints = [];
      for (let i = 0; i < numSteinerPoints; i++) {
        steinerPoints.push(createVector(centerX, centerY));
      }
    }

    function calculateMST() {
      const allPoints = [...regularPoints, ...steinerPoints];
      const edges = [];
      for (let i = 0; i < allPoints.length; i++) {
        for (let j = i + 1; j < allPoints.length; j++) {
          const dx = allPoints[i].x - allPoints[j].x;
          const dy = allPoints[i].y - allPoints[j].y;
          const dist = sqrt(dx * dx + dy * dy);
          edges.push({ i, j, weight: dist });
        }
      }

      edges.sort((a, b) => a.weight - b.weight);

      const parent = Array(allPoints.length).fill(-1);
      function find(u) {
        if (parent[u] < 0) return u;
        parent[u] = find(parent[u]);
        return parent[u];
      }
      function union(u, v) {
        const rootU = find(u);
        const rootV = find(v);
        if (rootU !== rootV) {
          if (parent[rootU] < parent[rootV]) {
            parent[rootU] += parent[rootV];
            parent[rootV] = rootU;
          } else {
            parent[rootV] += parent[rootU];
            parent[rootU] = rootV;
          }
          return true;
        }
        return false;
      }

      mstEdges = [];
      for (const edge of edges) {
        if (union(edge.i, edge.j)) {
          mstEdges.push(edge);
        }
      }

      totalLength = mstEdges.reduce((sum, edge) => sum + edge.weight, 0);
      document.getElementById('text-total-length').textContent = totalLength.toFixed(5);
    }

    function draw() {
      background(238);

      const allPoints = [...regularPoints, ...steinerPoints];

      stroke(190, 160, 130, 128);
      strokeWeight(1);
      for (let i = 0; i < allPoints.length; i++) {
        for (let j = i + 1; j < allPoints.length; j++) {
          line(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y);
        }
      }

      stroke(51);
      strokeWeight(2);
      for (const edge of mstEdges) {
        const p1 = allPoints[edge.i];
        const p2 = allPoints[edge.j];
        line(p1.x, p1.y, p2.x, p2.y);
      }

      fill(255);
      stroke(0, 0, 255);
      strokeWeight(2);
      for (const p of regularPoints) {
        ellipse(p.x, p.y, 16);
      }

      stroke(255, 0, 0);
      for (const p of steinerPoints) {
        ellipse(p.x, p.y, 16);
        ellipse(p.x, p.y, 8);
      }

      if (showAngles) {
        for (let s = 0; s < steinerPoints.length; s++) {
          const steiner = steinerPoints[s];
          const neighbors = [];
          for (const edge of mstEdges) {
            if (edge.i === regularPoints.length + s) {
              neighbors.push(allPoints[edge.j]);
            } else if (edge.j === regularPoints.length + s) {
              neighbors.push(allPoints[edge.i]);
            }
          }
          if (neighbors.length >= 2) {
            const angles = neighbors.map(p => {
              const dx = p.x - steiner.x;
              const dy = p.y - steiner.y;
              return atan2(dy, dx);
            });
            angles.sort((a, b) => a - b);
            angles.push(angles[0] + TWO_PI);

            for (let i = 0; i < angles.length - 1; i++) {
              const diff = angles[i + 1] - angles[i];
              const angleDeg = degrees(diff);
              const bisectorAngle = angles[i] + diff / 2;
              const textX = steiner.x + 20 * cos(bisectorAngle);
              const textY = steiner.y + 20 * sin(bisectorAngle);
              fill(0);
              noStroke();
              textAlign(CENTER, CENTER);
              text(`${angleDeg.toFixed(0)}°`, textX, textY);
            }
          }
        }
      }
    }

    function mousePressed() {
      const pointsToCheck = pointMode === 'regular' ? regularPoints : steinerPoints;
      for (const p of pointsToCheck) {
        if (dist(mouseX, mouseY, p.x, p.y) < 8) {
          draggingPoint = p;
          return;
        }
      }
    }

    function mouseDragged() {
      if (draggingPoint) {
        draggingPoint.x = mouseX;
        draggingPoint.y = mouseY;
        calculateMST();
      }
    }

    function mouseReleased() {
      draggingPoint = null;
    }

    function updateActiveButtons() {
      document.querySelectorAll('#regular-points-selector button').forEach(btn => {
        btn.classList.remove('active');
      });
      document.getElementById(`btn-regular-${numRegularPoints}`).classList.add('active');

      document.querySelectorAll('#steiner-points-selector button').forEach(btn => {
        btn.classList.remove('active');
      });
      document.getElementById(`btn-steiner-${numSteinerPoints}`).classList.add('active');
    }

    document.getElementById('btn-regular-3').addEventListener('click', () => {
      numRegularPoints = 3;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });
    document.getElementById('btn-regular-4').addEventListener('click', () => {
      numRegularPoints = 4;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });
    document.getElementById('btn-regular-5').addEventListener('click', () => {
      numRegularPoints = 5;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });

    document.getElementById('btn-steiner-0').addEventListener('click', () => {
      numSteinerPoints = 0;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });
    document.getElementById('btn-steiner-1').addEventListener('click', () => {
      numSteinerPoints = 1;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });
    document.getElementById('btn-steiner-2').addEventListener('click', () => {
      numSteinerPoints = 2;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });
    document.getElementById('btn-steiner-3').addEventListener('click', () => {
      numSteinerPoints = 3;
      resetPoints();
      calculateMST();
      updateActiveButtons();
    });

    document.getElementById('radio-regular').addEventListener('change', () => {
      pointMode = 'regular';
    });
    document.getElementById('radio-steiner').addEventListener('change', () => {
      pointMode = 'steiner';
    });

    document.getElementById('checkbox-show-angles').addEventListener('change', () => {
      showAngles = document.getElementById('checkbox-show-angles').checked;
    });
  </script>
</body>
</html>
