                    <input type="range" id="slider-sd-y" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-y">1.0</span>
                </div>
                <div>
                    <label for="slider-sd-z">Z</label>
                    <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-z">1.0</span>
                </div>
            </div>
        </div>
        <div id="visualization-panel"></div>
    </div>

    <script>
        let means = [0, 0, 0, 0];
        let sds = [1, 1, 1, 1];
        let fRatio = 0;
        let gm = 0;
        let msBetween = 0;
        let msWithin = 0;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('visualization-panel');
            noLoop();
            update();
        }

        function draw() {
            background(255);
            drawGroupDataPlot();
            drawANOVAPlot();
        }

        function drawGroupDataPlot() {
            const padding = 40;
            const plotHeight = height / 2 - padding;
            const plotWidth = width - padding * 2;
            const groupWidth = plotWidth / 4;
            const yScale = plotHeight / 20;

            // Draw axes
            stroke(0);
            line(padding, padding, padding, height/2 - padding);
            line(padding, height/2 - padding, width - padding, height/2 - padding);

            // Y-axis labels
            for (let y = -10; y <= 10; y += 5) {
                const yPos = height/2 - padding - y * yScale;
                line(padding - 5, yPos, padding, yPos);
                text(y, padding - 20, yPos + 4);
            }

            // X-axis labels
            const groups = ['W', 'X', 'Y', 'Z'];
            for (let i = 0; i < 4; i++) {
                const x = padding + i * groupWidth + groupWidth/2;
                text(groups[i], x, height/2 - padding + 20);
            }

            // Grand mean line
            stroke(0);
            strokeWeight(1);
            line(padding, height/2 - padding - gm * yScale, width - padding, height/2 - padding - gm * yScale);

            // Group bars
            fill('#F57F29');
            stroke(0);
            for (let i = 0; i < 4; i++) {
                const x = padding + i * groupWidth;
                const yCenter = height/2 - padding - means[i] * yScale;
                const barHeight = sds[i] * yScale * 2;

                rect(x, yCenter - barHeight/2, groupWidth - 10, barHeight);
                line(x, yCenter, x + groupWidth - 10, yCenter);
            }

            // F-ratio text
            fill(0);
            noStroke();
            textSize(16);
            text(`F ratio = ${fRatio.toFixed(2)}`, padding, padding - 10);
        }

        function drawANOVAPlot() {
            const padding = 40;
            const plotHeight = height / 2 - padding;
            const plotWidth = width - padding * 2;
            const barHeight = 30;
            const maxVal = 200;

            // Title
            fill(0);
            noStroke();
            textSize(14);
            text("Analysis of the F-Ratio", padding, height/2 + padding - 10);

            // X-axis
            stroke(0);
            line(padding, height/2 + padding, width - padding, height/2 + padding);

            // X-axis labels
            for (let x = 0; x <= 200; x += 50) {
                const xPos = padding + x * (plotWidth / maxVal);
                line(xPos, height/2 + padding, xPos, height/2 + padding + 5);
                text(x, xPos - 10, height/2 + padding + 25);
            }

            // Bars
            const stats = [
                { name: "MS Between", value: msBetween, color: '#4691A5' },
                { name: "MS Within", value: msWithin, color: '#D49D3C' },
                { name: "F-Ratio", value: fRatio, color: '#8C3A8D' }
            ];

            for (let i = 0; i < stats.length; i++) {
                const stat = stats[i];
                const y = height/2 + padding + 40 + i * (barHeight + 20);

                // Bar
                fill(stat.color);
                noStroke();
                const barWidth = Math.min(stat.value * (plotWidth / maxVal), plotWidth);
                rect(padding, y, barWidth, barHeight);

                // Label
                fill(0);
                textSize(12);
                text(stat.name, padding - 50, y + barHeight/2 + 4);
            }
        }

        function update() {
            // Read slider values
            means = [
                parseFloat(document.getElementById('slider-mean-w').value),
                parseFloat(document.getElementById('slider-mean-x').value),
                parseFloat(document.getElementById('slider-mean-y').value),
                parseFloat(document.getElementById('slider-mean-z').value)
            ];

            sds = [
                parseFloat(document.getElementById('slider-sd-w').value),
                parseFloat(document.getElementById('slider-sd-x').value),
                parseFloat(document.getElementById('slider-sd-y').value),
                parseFloat(document.getElementById('slider-sd-z').value)
            ];

            // Update value displays
            ['w', 'x', 'y', 'z'].forEach((id, i) => {
                document.getElementById(`value-mean-${id}`).textContent = means[i].toFixed(1);
                document.getElementById(`value-sd-${id}`).textContent = sds[i].toFixed(1);
            });

            // ANOVA calculations
            const k = 4;
            const n = 10;

            // Grand Mean
            gm = means.reduce((a, b) => a + b, 0) / k;

            // SS Between
            const ssBetween = n * means.reduce((sum, mean) => sum + Math.pow(mean - gm, 2), 0);
            msBetween = ssBetween / (k - 1);

            // SS Within
            const ssWithin = sds.reduce((sum, sd) => sum + Math.pow(sd, 2) * (n - 1), 0);
            msWithin = ssWithin / (n * k - k);

            // F-Ratio
            fRatio = msWithin > 0 ? msBetween / msWithin : Infinity;

            redraw();
        }

        // Event listeners
        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', update);
        });

        // Initial update
        update();
    </script>
</body>
</html>
