<body>
    <div id="main-container">
        <div id="control-panel">
            <h3 style="color: blue;">GEV distribution 1</h3>
            <div class="slider-group">
                <label for="slider-mu1">location μ₁</label>
                <input type="range" id="slider-mu1" min="2" max="6" step="0.01" value="3.87">
                <input type="text" id="display-mu1" readonly>
            </div>
            <div class="slider-group">
                <label for="slider-sigma1">scale σ₁</label>
                <input type="range" id="slider-sigma1" min="0.1" max="0.5" step="0.01" value="0.198">
                <input type="text" id="display-sigma1" readonly>
            </div>
            <div class="slider-group">
                <label for="slider-xi1">shape ξ₁</label>
                <input type="range" id="slider-xi1" min="-0.5" max="0.5" step="0.01" value="-0.05">
                <input type="text" id="display-xi1" readonly>
            </div>

            <h3 style="color: red;">GEV distribution 2</h3>
            <div class="slider-group">
                <label for="slider-mu2">location μ₂</label>
                <input type="range" id="slider-mu2" min="2" max="6" step="0.01" value="3.87">
                <input type="text" id="display-mu2" readonly>
            </div>
            <div class="slider-group">
                <label for="slider-sigma2">scale σ₂</label>
                <input type="range" id="slider-sigma2" min="0.1" max="0.5" step="0.01" value="0.3">
                <input type="text" id="display-sigma2" readonly>
            </div>
            <div class="slider-group">
                <label for="slider-xi2">shape ξ₂</label>
                <input type="range" id="slider-xi2" min="-0.5" max="0.5" step="0.01" value="-0.05">
                <input type="text" id="display-xi2" readonly>
            </div>

            <h3>Return level curves</h3>
            <div class="slider-group">
                <label for="slider-pmax">P_max</label>
                <input type="range" id="slider-pmax" min="10" max="1000" step="1" value="100">
                <input type="text" id="display-pmax" readonly>
            </div>
            <div id="plot-return-level" style="height: 300px;"></div>
        </div>

        <div id="pdf-plot-area">
            <div id="plot-pdf" style="height: 100%;"></div>
        </div>
    </div>

    <script>
        // Initialize slider displays
        document.getElementById('display-mu1').value = 3.87;
        document.getElementById('display-sigma1').value = 0.198;
        document.getElementById('display-xi1').value = -0.05;
        document.getElementById('display-mu2').value = 3.87;
        document.getElementById('display-sigma2').value = 0.3;
        document.getElementById('display-xi2').value = -0.05;
        document.getElementById('display-pmax').value = 100;

        // Slider event listeners
        const sliders = [
            'slider-mu1', 'slider-sigma1', 'slider-xi1',
            'slider-mu2', 'slider-sigma2', 'slider-xi2',
            'slider-pmax'
        ];
        sliders.forEach(id => {
            const slider = document.getElementById(id);
            const display = document.getElementById('display-' + id.replace('slider-', ''));
            slider.addEventListener('input', () => {
                display.value = slider.value;
                updatePlots();
            });
        });

        function gevPDF(x, mu, sigma, xi) {
            const z = (x - mu) / sigma;
            if (xi === 0) {
                return (1 / sigma) * Math.exp(-z - Math.exp(-z));
            } else {
                const t = 1 + xi * z;
                if (t <= 0) return 0;
                return (1 / sigma) * Math.pow(t, -1/xi - 1) * Math.exp(-Math.pow(t, -1/xi));
            }
        }

        function gevReturnLevel(p, mu, sigma, xi) {
            if (xi === 0) {
                return mu - sigma * Math.log(-Math.log(1 - 1/p));
            } else {
                return mu - (sigma / xi) * (1 - Math.pow(-Math.log(1 - 1/p), -xi));
            }
        }

        function generatePDFData(mu, sigma, xi, color, name) {
            const x = [];
            const y = [];
            for (let i = 2.5; i <= 7; i += 0.01) {
                x.push(i);
                y.push(gevPDF(i, mu, sigma, xi));
            }
            return {
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                name: name,
                line: {color: color},
                fill: 'tozeroy',
                fillcolor: color === 'blue' ? 'rgba(0,0,255,0.2)' : 'rgba(255,0,0,0.2)'
            };
        }

        function generateReturnLevelData(mu, sigma, xi, pmax, color, name) {
            const p = [];
            const y = [];
            for (let i = 1; i <= pmax; i++) {
                p.push(i);
                y.push(gevReturnLevel(i, mu, sigma, xi));
            }
            return {
                x: p,
                y: y,
                type: 'scatter',
                mode: 'lines',
                name: name,
                line: {color: color}
            };
        }

        function updatePlots() {
            const mu1 = parseFloat(document.getElementById('slider-mu1').value);
            const sigma1 = parseFloat(document.getElementById('slider-sigma1').value);
            const xi1 = parseFloat(document.getElementById('slider-xi1').value);
            const mu2 = parseFloat(document.getElementById('slider-mu2').value);
            const sigma2 = parseFloat(document.getElementById('slider-sigma2').value);
            const xi2 = parseFloat(document.getElementById('slider-xi2').value);
            const pmax = parseInt(document.getElementById('slider-pmax').value);

            // Update PDF plot
            const pdfData = [
                generatePDFData(mu1, sigma1, xi1, 'blue', 'GEV 1'),
                generatePDFData(mu2, sigma2, xi2, 'red', 'GEV 2')
            ];
            Plotly.newPlot('plot-pdf', pdfData, {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: {range: [2.5, 7], showticklabels: false},
                yaxis: {autorange: true, showticklabels: false},
                margin: {l: 40, r: 20, b: 40, t: 40}
            });

            // Update return level plot
            const rlData = [
                generateReturnLevelData(mu1, sigma1, xi1, pmax, 'blue', 'GEV 1'),
                generateReturnLevelData(mu2, sigma2, xi2, pmax, 'red', 'GEV 2')
            ];
            Plotly.newPlot('plot-return-level', rlData, {
                xaxis: {range: [0, pmax], title: 'Return Period (years)'},
                yaxis: {autorange: true, title: 'Maximum Sea Level (meters)'},
                margin: {l: 40, r: 20, b: 40, t: 20}
            });
        }

        // Initial plot render
        window.addEventListener('load', updatePlots);
    </script>
</body>
</html>
