<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Signal Transformation Demo</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #main-container {
            width: 90%;
            max-width: 1200px;
        }
        #control-panel {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
            padding: 15px;
            background: #f5f5f5;
            border-radius: 8px;
        }
        .control-row {
            display: flex;
            align-items: center;
            gap: 15px;
        }
        .signal-buttons {
            display: flex;
            gap: 8px;
        }
        button {
            padding: 6px 12px;
            border: 1px solid #ccc;
            border-radius: 4px;
            background: white;
            cursor: pointer;
        }
        button.active {
            background: #007bff;
            color: white;
            border-color: #0056b3;
        }
        .slider-row {
            display: flex;
            align-items: center;
            gap: 8px;
            flex: 1;
        }
        .checkbox-row {
            display: flex;
            align-items: center;
            gap: 15px;
        }
        #plot-top, #plot-bottom {
            width: 100%;
            height: 400px;
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-row">
                <label>signal</label>
                <div class="signal-buttons">
                    <button id="btn-rampdown">RampDown</button>
                    <button id="btn-tophat">TopHat</button>
                    <button id="btn-stairstep">StairStep</button>
                    <button id="btn-triangle" class="active">Triangle</button>
                    <button id="btn-uh">UH</button>
                </div>
            </div>
            <div class="control-row">
                <div class="slider-row">
                    <label for="slider-a">a</label>
                    <input type="range" id="slider-a" min="0.1" max="4" step="0.1" value="2">
                    <span id="slider-a-value">2.0</span>
                </div>
                <div class="slider-row">
                    <label for="slider-b">b</label>
                    <input type="range" id="slider-b" min="-10" max="10" step="0.1" value="4">
                    <span id="slider-b-value">4.0</span>
                </div>
                <div class="slider-row">
                    <label for="slider-t">T = b/a</label>
                    <input type="range" id="slider-t" min="-25" max="25" step="0.1" value="2">
                    <span id="slider-t-value">2.0</span>
                </div>
            </div>
            <div class="control-row checkbox-row">
                <label>display:</label>
                <label><input type="checkbox" id="check-x" checked> \(x(t)\)</label>
                <label><input type="checkbox" id="check-y" checked> \(y(t) = x(at - b) = x(a(t-T))\)</label>
                <label><input type="checkbox" id="check-z" checked> \(z(t) = x(t - b)\)</label>
                <label><input type="checkbox" id="check-w"> \(w(t) = x(at)\)</label>
            </div>
        </div>
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>

    <script>
        // Global variables
        let currentSignal = 'triangle';
        let a = 2, b = 4, T = 2;
        const tRange = [-10, 20];
        const dt = 0.01;

        // Signal definitions
        const signals = {
            triangle: () => [
                [0,0], [3,4], [6,0]
            ],
            tophat: () => [
                [1,0], [1,4], [5,4], [5,0]
            ],
            stairstep: () => [
                [1,0], [1,1], [2,1], [2,4], [4,4], [4,1], [5,1], [5,0]
            ],
            rampdown: () => [
                [0,4], [6,0]
            ],
            uh: () => [
                [[1,0], [1,4], [1.5,4], [1.5,1], [2.5,1], [2.5,4], [3,4], [3,0]],
                [[4,0], [4,4], [4.5,4], [4.5,2], [5.5,2], [5.5,4], [6,4], [6,0]]
            ]
        };

        // Initialize plots
        function initPlots() {
            const layoutTop = {
                title: 'signal',
                xaxis: { title: 'time (s)', range: tRange, zeroline: true },
                yaxis: { range: [-0.5, 4.5], zeroline: true },
                showlegend: true,
                legend: {x: 0.01, y: 0.75},
                shapes: [],
                annotations: []
            };

            const layoutBottom = {
                ...layoutTop,
                title: '',
                legend: {x: 0.01, y: 0.98}
            };

            Plotly.newPlot('plot-top', [], layoutTop);
            Plotly.newPlot('plot-bottom', [], layoutBottom);
        }

        // Generate signal data
        function generateSignalData(signalFunc, transformFn) {
            const points = signalFunc();
            if (!Array.isArray(points[0])) points.forEach(p => p[0] = transformFn(p[0]));
            else points.forEach(part => part.forEach(p => p[0] = transformFn(p[0])));
            return points;
        }

        // Update plot data
        function updatePlots() {
            const signalFunc = signals[currentSignal];
            const xData = generateSignalData(signalFunc, t => t);
            const yData = generateSignalData(signalFunc, t => (t - T)/a);
            const zData = generateSignalData(signalFunc, t => t - b);
            const wData = generateSignalData(signalFunc, t => t/a);

            // Update top plot
            Plotly.restyle('plot-top', {
                x: [xData.map(p => p[0])],
                y: [xData.map(p => p[1])],
                name: ['\\(x(t)\\)'],
                line: {color: 'rgb(70, 130, 180)', width: 2.5}
            }, [0]);

            // Update bottom plot traces
            const traces = [
                {data: xData, name: '\\(x(t)\\)', color: 'rgb(70, 130, 180)'},
                {data: yData, name: '\\(y(t) = x(at-b) = x(a(t-T))\\)', color: 'red'},
                {data: zData, name: '\\(z(t) = x(t-b)\\)', color: 'olivedrab'},
                {data: wData, name: '\\(w(t) = x(at)\\)', color: 'saddlebrown'}
            ];

            traces.forEach((trace, i) => {
                Plotly.restyle('plot-bottom', {
                    x: [trace.data.map(p => p[0])],
                    y: [trace.data.map(p => p[1])],
                    name: [trace.name],
                    line: {color: trace.color, width: 2.5},
                    visible: document.getElementById(`check-${['x','y','z','w'][i]}`).checked ? true : 'legendonly'
                }, [i]);
            });

            // Update annotations
            const annotations = [
                {x: T, y: 0, text: 'T', color: 'red'},
                {x: b, y: 0, text: 'b', color: 'green'}
            ];

            annotations.forEach(anno => {
                const shapes = [{
                    type: 'circle',
                    x0: anno.x - 0.1, y0: -0.2,
                    x1: anno.x + 0.1, y1: 0.2,
                    xref: 'x', yref: 'y',
                    fillcolor: anno.color,
                    line: {width: 0}
                }];
                const annos = [{
                    x: anno.x, y: 0.5,
                    text: anno.text,
                    showarrow: false,
                    font: {color: anno.color}
                }];

                Plotly.relayout('plot-top', {
                    shapes: shapes,
                    annotations: annos
                });

                Plotly.relayout('plot-bottom', {
                    shapes: shapes,
                    annotations: annos
                });
            });
        }

        // Event listeners
        document.querySelectorAll('.signal-buttons button').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.signal-buttons button').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                currentSignal = btn.id.split('-')[1];
                updatePlots();
            });
        });

        const sliders = {
            'slider-a': val => {
                a = parseFloat(val);
                T = b / a;
                document.getElementById('slider-t').value = T;
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
            },
            'slider-b': val => {
                b = parseFloat(val);
                T = b / a;
                document.getElementById('slider-t').value = T;
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
            },
            'slider-t': val => {
                T = parseFloat(val);
                b = a * T;
                document.getElementById('slider-b').value = b;
                document.getElementById('slider-b-value').textContent = b.toFixed(1);
            }
        };

        Object.keys(sliders).forEach(id => {
            const slider = document.getElementById(id);
            const valueSpan = document.getElementById(`${id}-value`);
            slider.addEventListener('input', () => {
                valueSpan.textContent = slider.value;
                sliders[id](slider.value);
                updatePlots();
            });
        });

        document.querySelectorAll('input[type="checkbox"]').forEach(checkbox => {
            checkbox.addEventListener('change', () => {
                updatePlots();
            });
        });

        // Initial setup
        initPlots();
        updatePlots();
        MathJax.typesetPromise();
    </script>
</body>
</html>
