            <div>
                <label>units in x direction</label>
                <input type="radio" name="unitsX" id="radio-x-0" value="0"> 0
                <input type="radio" name="unitsX" id="radio-x-1" value="1"> 1
                <input type="radio" name="unitsX" id="radio-x-2" value="2" checked> 2
                <input type="radio" name="unitsX" id="radio-x-3" value="3"> 3
                <input type="radio" name="unitsX" id="radio-x-4" value="4"> 4
            </div>
            <div>
                <label>units in y direction</label>
                <input type="radio" name="unitsY" id="radio-y-0" value="0"> 0
                <input type="radio" name="unitsY" id="radio-y-1" value="1"> 1
                <input type="radio" name="unitsY" id="radio-y-2" value="2" checked> 2
                <input type="radio" name="unitsY" id="radio-y-3" value="3"> 3
                <input type="radio" name="unitsY" id="radio-y-4" value="4"> 4
            </div>
            <div>
                <label>trim</label>
                <input type="range" id="slider-trim" min="0.65" max="0.9" step="0.001" value="0.8">
                <span id="label-trim-value">0.800</span>
            </div>
            <div>
                <input type="checkbox" id="checkbox-double"> <label for="checkbox-double">double</label>
                <input type="checkbox" id="checkbox-sphere"> <label for="checkbox-sphere">sphere</label>
                <input type="checkbox" id="checkbox-triangles"> <label for="checkbox-triangles">triangles</label>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="main-canvas"></canvas>
            <button id="btn-reset-view">+</button>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        let scene, camera, renderer, controls, latticeGroup;

        init();
        animate();
        setupEventListeners();

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);

            // Renderer setup
            const canvas = document.getElementById('main-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);

            // Lights
            const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444, 1);
            scene.add(hemiLight);

            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 10, 7.5);
            scene.add(dirLight);

            // Orbit controls
            controls = new THREE.OrbitControls(camera, canvas);
            controls.enableDamping = true;

            // Lattice group
            latticeGroup = new THREE.Group();
            scene.add(latticeGroup);

            // Initial scene update
            updateScene();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function setupEventListeners() {
            const controls = [
                'radio-x-0', 'radio-x-1', 'radio-x-2', 'radio-x-3', 'radio-x-4',
                'radio-y-0', 'radio-y-1', 'radio-y-2', 'radio-y-3', 'radio-y-4',
                'slider-trim', 'checkbox-double', 'checkbox-sphere', 'checkbox-triangles'
            ];
            controls.forEach(id => {
                document.getElementById(id).addEventListener('input', updateScene);
                document.getElementById(id).addEventListener('change', updateScene);
            });

            document.getElementById('btn-reset-view').addEventListener('click', () => {
                controls.reset();
            });

            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function generateArcPoints(start, end, trim, segments = 10) {
            const points = [];
            const M0 = new THREE.Vector3().addVectors(start, end).normalize();
            const control = M0.clone().multiplyScalar(trim);
            for (let i = 0; i <= segments; i++) {
                const t = i / segments;
                const t2 = t * t;
                const oneMinusT = 1 - t;
                const oneMinusT2 = oneMinusT * oneMinusT;
                const point = new THREE.Vector3(
                    oneMinusT2 * start.x + 2 * oneMinusT * t * control.x + t2 * end.x,
                    oneMinusT2 * start.y + 2 * oneMinusT * t * control.y + t2 * end.y,
                    oneMinusT2 * start.z + 2 * oneMinusT * t * control.z + t2 * end.z
                );
                point.normalize().multiplyScalar(1);
                points.push(point);
            }
            return points;
        }

        function generateCurvedTriangle(trimValue, colorCenter, colorEdge) {
            const radius = 1;
            const A = new THREE.Vector3(radius, 0, 0);
            const B = new THREE.Vector3(-radius/2, (Math.sqrt(3)/2)*radius, 0);
            const C = new THREE.Vector3(-radius/2, -(Math.sqrt(3)/2)*radius, 0);

            const segments = 10;
            const edgeAB = generateArcPoints(A, B, trimValue, segments);
            const edgeBC = generateArcPoints(B, C, trimValue, segments);
            const edgeCA = generateArcPoints(C, A, trimValue, segments);

            const allEdgePoints = [...edgeAB, ...edgeBC, ...edgeCA];

            const positions = [0, 0, 0];
            const colors = [
                colorCenter.r, colorCenter.g, colorCenter.b,
                ...allEdgePoints.flatMap(p => [colorEdge.r, colorEdge.g, colorEdge.b])
            ];

            allEdgePoints.forEach(p => positions.push(p.x, p.y, p.z));

            const indices = [];
            let vertexIndex = 1;
            [edgeAB, edgeBC, edgeCA].forEach(edge => {
                for (let i = 0; i < edge.length - 1; i++) {
                    indices.push(0, vertexIndex + i, vertexIndex + i + 1);
                }
                vertexIndex += edge.length;
            });

            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.setIndex(indices);

            const material = new THREE.MeshStandardMaterial({
                vertexColors: true,
                roughness: 0.5,
                metalness: 0.5
            });
            const mesh = new THREE.Mesh(geometry, material);

            const outlinePositions = [];
            const outlineIndices = [];
            let outlineVertexIndex = 0;

            const addEdgeToOutline = (edge) => {
                edge.forEach(p => outlinePositions.push(p.x, p.y, p.z));
                for (let i = 0; i < edge.length - 1; i++) {
                    outlineIndices.push(outlineVertexIndex + i, outlineVertexIndex + i + 1);
                }
                outlineVertexIndex += edge.length;
            };

            addEdgeToOutline(edgeAB);
            addEdgeToOutline(edgeBC);
            addEdgeToOutline(edgeCA);

            const outlineGeometry = new THREE.BufferGeometry();
            outlineGeometry.setAttribute('position', new THREE.Float32BufferAttribute(outlinePositions, 3));
            outlineGeometry.setIndex(outlineIndices);

            const outlineMaterial = new THREE.LineBasicMaterial({
                color: 0x000000,
                linewidth: 2
            });
            const outline = new THREE.LineSegments(outlineGeometry, outlineMaterial);

            return { mesh, outline };
        }

        function generateLattice(unitsX, unitsY, trimValue, isDouble) {
            const basis1 = new THREE.Vector3(3, 0, 0);
            const basis2 = new THREE.Vector3(1.5, Math.sqrt(3)*1.5, 0);

            const colorCenter = isDouble ? new THREE.Color(0x80ffff) : new THREE.Color(0xffc0cb);
            const colorEdge = isDouble ? new THREE.Color(0x008080) : new THREE.Color(0x904090);

            for (let i = -unitsX; i <= unitsX; i++) {
                for (let j = -unitsY; j <= unitsY; j++) {
                    const position = basis1.clone().multiplyScalar(i).add(basis2.clone().multiplyScalar(j));
                    const rotation = (i + j) % 2 === 0 ? 0 : Math.PI;

                    if (isDouble) {
                        const doubleOffset = basis1.clone().add(basis2).multiplyScalar(0.5);
                        position.add(doubleOffset);
                        rotation += Math.PI;
                    }

                    const { mesh, outline } = generateCurvedTriangle(trimValue, colorCenter, colorEdge);
                    mesh.position.copy(position);
                    mesh.rotation.z = rotation;
                    outline.position.copy(position);
                    outline.rotation.z = rotation;

                    latticeGroup.add(mesh);
                    latticeGroup.add(outline);
                }
            }
        }

        function addReferenceSphere() {
            const geometry = new THREE.SphereGeometry(1, 32, 32);
            const material = new THREE.MeshPhongMaterial({
                color: 0xdddddd,
                shininess: 80
            });
            const sphere = new THREE.Mesh(geometry, material);
            latticeGroup.add(sphere);
        }

        function addUnderlyingTriangles(unitsX, unitsY) {
            const basis1 = new THREE.Vector3(3, 0, 0);
            const basis2 = new THREE.Vector3(1.5, Math.sqrt(3)*1.5, 0);

            const triangleGeometry = new THREE.BufferGeometry();
            const positions = [
                1, 0, 0,
                -0.5, Math.sqrt(3)/2, 0,
                -0.5, -Math.sqrt(3)/2, 0
            ];
            triangleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));

            const material = new THREE.MeshBasicMaterial({
                color: 0x800080,
                transparent: true,
                opacity: 0.4
            });
            const triangleMesh = new THREE.Mesh(triangleGeometry, material);

            const pointsMaterial = new THREE.PointsMaterial({
                color: 0x000000,
                size: 3
            });
            const pointsGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(-0.5, Math.sqrt(3)/2, 0),
                new THREE.Vector3(-0.5, -Math.sqrt(3)/2, 0)
            ]);
            const points = new THREE.Points(pointsGeometry, pointsMaterial);

            for (let i = -unitsX; i <= unitsX; i++) {
                for (let j = -unitsY; j <= unitsY; j++) {
                    const position = basis1.clone().multiplyScalar(i).add(basis2.clone().multiplyScalar(j));
                    const rotation = (i + j) % 2 === 0 ? 0 : Math.PI;

                    const triangle = triangleMesh.clone();
                    triangle.position.copy(position);
                    triangle.rotation.z = rotation;
                    latticeGroup.add(triangle);

                    const pts = points.clone();
                    pts.position.copy(position);
                    pts.rotation.z = rotation;
                    latticeGroup.add(pts);
                }
            }
        }

        function updateScene() {
            while (latticeGroup.children.length) {
                latticeGroup.remove(latticeGroup.children[0]);
            }

            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trimValue = parseFloat(document.getElementById('slider-trim').value);
            const doubleChecked = document.getElementById('checkbox-double').checked;
            const sphereChecked = document.getElementById('checkbox-sphere').checked;
            const trianglesChecked = document.getElementById('checkbox-triangles').checked;

            document.getElementById('label-trim-value').textContent = trimValue.toFixed(3);

            generateLattice(unitsX, unitsY, trimValue, false);

            if (doubleChecked) {
                generateLattice(unitsX, unitsY, trimValue, true);
            }

            if (sphereChecked) {
                addReferenceSphere();
            }

            if (trianglesChecked) {
                addUnderlyingTriangles(unitsX, unitsY);
            }
        }
    </script>
</body>
</html>
