<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Signal Visualization Demo</title>
  <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
  <style>
    body {
      margin: 0;
      font-family: Arial, sans-serif;
    }
    #main-container {
      background-color: #f0f0f0;
      min-height: 100vh;
      padding: 20px;
      box-sizing: border-box;
      position: relative;
    }
    #info-button {
      position: absolute;
      top: 10px;
      right: 10px;
      z-index: 10;
      padding: 5px 10px;
      cursor: pointer;
    }
    #description-panel {
      margin: 20px 0;
      padding: 10px;
      background-color: white;
      border: 1px solid #ccc;
      display: none;
    }
    #control-panel {
      background-color: white;
      border: 1px solid #ccc;
      padding: 10px;
      margin-bottom: 20px;
    }
    #control-panel label {
      display: block;
      margin-top: 10px;
    }
    #function-buttons, #encoding-buttons {
      display: flex;
      gap: 5px;
      margin-top: 5px;
    }
    button {
      padding: 5px 10px;
      cursor: pointer;
      border: 1px solid #999;
      background-color: #eee;
    }
    button.active {
      background-color: #ccc;
      border-style: inset;
    }
    #canvas-container {
      width: 100%;
      height: 600px;
      background-color: white;
      border: 1px solid #ccc;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <button id="info-button">+</button>
    <div id="description-panel" style="display: none;">
      <p>This demo visualizes analog signals and their encoded representations using different time-encoding algorithms. Adjust the controls to explore how the signals change over time and how different encoding methods represent the analog input.</p>
    </div>
    <div id="control-panel">
      <label for="slider-start-time">start time</label>
      <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
      <label for="slider-total-time">total time</label>
      <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
      <label>function</label>
      <div id="function-buttons">
        <button id="btn-f">f</button>
        <button id="btn-g" class="active">g</button>
        <button id="btn-sin">sin</button>
        <button id="btn-cos">cos</button>
        <button id="btn-tan">tan</button>
      </div>
      <label>encoding method</label>
      <div id="encoding-buttons">
        <button id="btn-asdm" class="active">ASDM</button>
        <button id="btn-iaf">IAF</button>
      </div>
    </div>
    <div id="canvas-container"></div>
  </div>

  <script>
    let startTime = -1.0;
    let totalTime = 2.2;
    let selectedFunction = 'g';
    let selectedEncoding = 'ASDM';

    function analogFunction(t) {
      switch (selectedFunction) {
        case 'f': return 0.5 * t;
        case 'g': return Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(6 * Math.PI * t);
        case 'sin': return Math.sin(2 * Math.PI * t);
        case 'cos': return Math.cos(2 * Math.PI * t);
        case 'tan': return Math.tan(Math.PI * t);
      }
    }

    function simulateASDM() {
      const B = 0.25;
      const delta = 0.05;
      let currentTime = startTime;
      let outputLevel = B;
      let integral = 0;
      let flipTimes = [];
      const dt = 0.01;
      const endTime = startTime + totalTime;

      while (currentTime <= endTime) {
        const analogVal = analogFunction(currentTime);
        integral += (analogVal - outputLevel) * dt;

        let flip = false;
        if (outputLevel === B && integral < -delta) {
          flip = true;
        } else if (outputLevel === -B && integral > delta) {
          flip = true;
        }

        if (flip) {
          flipTimes.push(currentTime);
          outputLevel = outputLevel === B ? -B : B;
          integral = 0;
        }

        currentTime += dt;
      }

      return { flipTimes, initialLevel: B };
    }

    function simulateIAF() {
      const theta = 0.05;
      let currentTime = startTime;
      let integral = 0;
      let spikeTimes = [];
      const dt = 0.01;
      const endTime = startTime + totalTime;

      while (currentTime <= endTime) {
        const analogVal = analogFunction(currentTime);
        integral += analogVal * dt;

        if (integral >= theta) {
          spikeTimes.push(currentTime);
          integral = 0;
        }

        currentTime += dt;
      }

      return spikeTimes;
    }

    function updateFunctionButtons() {
      const buttons = ['btn-f', 'btn-g', 'btn-sin', 'btn-cos', 'btn-tan'];
      buttons.forEach(id => {
        document.getElementById(id).classList.remove('active');
      });
      document.getElementById('btn-' + selectedFunction).classList.add('active');
    }

    function updateEncodingButtons() {
      const buttons = ['btn-asdm', 'btn-iaf'];
      buttons.forEach(id => {
        document.getElementById(id).classList.remove('active');
      });
      document.getElementById('btn-' + selectedEncoding).classList.add('active');
    }

    function setup() {
      const canvas = createCanvas(800, 600);
      canvas.parent('canvas-container');
      noLoop();
    }

    function draw() {
      background(255);
      const margin = 40;
      const endTime = startTime + totalTime;

      // Draw axes
      stroke(0);
      line(margin, height - margin, width - margin, height - margin);
      line(margin, height - margin, margin, margin);

      // Draw x ticks
      const xStep = 0.5;
      for (let t = Math.ceil(startTime / xStep) * xStep; t <= endTime; t += xStep) {
        const x = map(t, startTime, endTime, margin, width - margin);
        line(x, height - margin, x, height - margin + 5);
        textSize(10);
        fill(0);
        textAlign(CENTER, TOP);
        text(t.toFixed(1), x, height - margin + 5);
      }

      // Draw y ticks
      const yStep = 0.5;
      for (let y = Math.ceil(-1.2 / yStep) * yStep; y <= 1.2; y += yStep) {
        const yPixel = map(y, -1.2, 1.2, height - margin, margin);
        line(margin, yPixel, margin - 5, yPixel);
        textSize(10);
        fill(0);
        textAlign(RIGHT, CENTER);
        text(y.toFixed(1), margin - 8, yPixel);
      }

      // Draw analog signal
      stroke(0, 0, 255);
      noFill();
      beginShape();
      const dt = 0.01;
      for (let t = startTime; t <= endTime; t += dt) {
        const y = analogFunction(t);
        if (selectedFunction === 'tan' && Math.abs(y) > 10) {
          endShape();
          beginShape();
          continue;
        }
        const x = map(t, startTime, endTime, margin, width - margin);
        const yPixel = map(y, -1.2, 1.2, height - margin, margin);
        vertex(x, yPixel);
      }
      endShape();

      // Draw encoded signal
      if (selectedEncoding === 'ASDM') {
        const { flipTimes, initialLevel } = simulateASDM();
        stroke(255, 0, 0);
        noFill();
        let currentLevel = initialLevel;
        let prevTime = startTime;
        for (let t of flipTimes) {
          const xStart = map(prevTime, startTime, endTime, margin, width - margin);
          const yStart = map(currentLevel, -1.2, 1.2, height - margin, margin);
          const xEnd = map(t, startTime, endTime, margin, width - margin);
          line(xStart, yStart, xEnd, yStart);
          const yNext = map(-currentLevel, -1.2, 1.2, height - margin, margin);
          line(xEnd, yStart, xEnd, yNext);
          currentLevel = -currentLevel;
          prevTime = t;
        }
        const xStart = map(prevTime, startTime, endTime, margin, width - margin);
        const yStart = map(currentLevel, -1.2, 1.2, height - margin, margin);
        const xEnd = map(endTime, startTime, endTime, margin, width - margin);
        line(xStart, yStart, xEnd, yStart);
      } else if (selectedEncoding === 'IAF') {
        const spikeTimes = simulateIAF();
        stroke(255, 0, 0);
        fill(255, 0, 0);
        const y_spike = 0.3;
        const yPixel = map(y_spike, -1.2, 1.2, height - margin, margin);
        for (let t of spikeTimes) {
          const x = map(t, startTime, endTime, margin, width - margin);
          line(x, map(0, -1.2, 1.2, height - margin, margin), x, yPixel);
          ellipse(x, yPixel, 4, 4);
        }
      }
    }

    document.getElementById('info-button').addEventListener('click', function() {
      const panel = document.getElementById('description-panel');
      panel.style.display = panel.style.display === 'none' ? 'block' : 'none';
    });

    document.getElementById('slider-start-time').addEventListener('input', function() {
      startTime = parseFloat(this.value);
      redraw();
    });

    document.getElementById('slider-total-time').addEventListener('input', function() {
      totalTime = parseFloat(this.value);
      redraw();
    });

    document.getElementById('btn-f').addEventListener('click', function() {
      selectedFunction = 'f';
      updateFunctionButtons();
      redraw();
    });

    document.getElementById('btn-g').addEventListener('click', function() {
      selectedFunction = 'g';
      updateFunctionButtons();
      redraw();
    });

    document.getElementById('btn-sin').addEventListener('click', function() {
      selectedFunction = 'sin';
      updateFunctionButtons();
      redraw();
    });

    document.getElementById('btn-cos').addEventListener('click', function() {
      selectedFunction = 'cos';
      updateFunctionButtons();
      redraw();
    });

    document.getElementById('btn-tan').addEventListener('click', function() {
      selectedFunction = 'tan';
      updateFunctionButtons();
      redraw();
    });

    document.getElementById('btn-asdm').addEventListener('click', function() {
      selectedEncoding = 'ASDM';
      updateEncodingButtons();
      redraw();
    });

    document.getElementById('btn-iaf').addEventListener('click', function() {
      selectedEncoding = 'IAF';
      updateEncodingButtons();
      redraw();
    });

    document.addEventListener('DOMContentLoaded', function() {
      updateFunctionButtons();
      updateEncodingButtons();
      redraw();
    });
  </script>
</body>
</html>
