                        <svg viewBox="0 0 40 30"><path d="M0,15 Q 20,0 40,15"/></svg>
                    </button>
                </div>
                <label for="p-wave-amplitude-slider">amplitude</label>
                <input type="range" id="p-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
                <label for="p-wave-wavelength-slider">wavelength</label>
                <input type="range" id="p-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.0">
            </fieldset>
            <fieldset>
                <legend>shear wave</legend>
                <div id="s-wave-type-selector">
                    <button id="s-wave-sine-button" class="active">
                        <svg viewBox="0 0 40 30"><path d="M0,15 Q 10,0 20,15 T 40,15"/></svg>
                    </button>
                    <button id="s-wave-pulse-button">
                        <svg viewBox="0 0 40 30"><path d="M0,15 Q 20,0 40,15"/></svg>
                    </button>
                </div>
                <label for="s-wave-amplitude-slider">amplitude</label>
                <input type="range" id="s-wave-amplitude-slider" min="0" max="0.3" step="0.01" value="0.1">
                <label for="s-wave-wavelength-slider">wavelength</label>
                <input type="range" id="s-wave-wavelength-slider" min="0.5" max="5.0" step="0.1" value="2.5">
                <label>direction</label>
                <div id="s-wave-direction-selector">
                    <button id="s-wave-dir-xy-button" class="active">x-y</button>
                    <button id="s-wave-dir-z-button">z</button>
                </div>
            </fieldset>
            <fieldset>
                <legend>viewpoint</legend>
                <div id="viewpoint-selector">
                    <button id="viewpoint-top-button" class="active">Top</button>
                    <button id="viewpoint-default-button">Default</button>
                </div>
            </fieldset>
        </div>
        <div id="visualization-container"></div>
    </div>
    <script>
        // Scene setup
        const container = document.getElementById('visualization-container');
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xE0E0E0);

        // Cameras
        const perspectiveCamera = new THREE.PerspectiveCamera(45, container.clientWidth / container.clientHeight, 0.1, 100);
        perspectiveCamera.position.set(10, 10, 10);
        perspectiveCamera.lookAt(0, 0, 0);

        const orthoCamera = new THREE.OrthographicCamera(
            -10, 10, 10, -10, 0.1, 100
        );
        orthoCamera.position.set(0, 0, 20);
        orthoCamera.lookAt(0, 0, 0);

        let activeCamera = orthoCamera;

        // Renderer
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Lights
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(10, 10, 10);
        scene.add(directionalLight);

        // Grid mesh
        let plotPoints = 40;
        let geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints, plotPoints);
        const material = new THREE.MeshLambertMaterial({
            vertexColors: true,
            side: THREE.DoubleSide
        });
        const mesh = new THREE.Mesh(geometry, material);
        scene.add(mesh);

        // Wireframe overlay
        const wireframe = new THREE.LineSegments(
            new THREE.WireframeGeometry(geometry),
            new THREE.LineBasicMaterial({ color: 0x556B2F })
        );
        scene.add(wireframe);

        // State variables
        let time = 0;
        let isPlaying = false;
        let pWaveType = 'sine';
        let sWaveType = 'sine';
        let sWaveDirection = 'xy';
        let pAmplitude = 0.1;
        let pWavelength = 2.0;
        let sAmplitude = 0.1;
        let sWavelength = 2.5;

        // Event listeners
        document.getElementById('play-pause-button').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('play-pause-button').textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', () => {
            time = 0;
            isPlaying = false;
            updateControls();
            updateVisualization();
        });

        document.getElementById('step-button').addEventListener('click', () => {
            time += 0.1;
            updateControls();
            updateVisualization();
        });

        document.getElementById('time-slider').addEventListener('input', (e) => {
            time = parseFloat(e.target.value);
            updateVisualization();
        });

        document.getElementById('plot-points-slider').addEventListener('input', (e) => {
            plotPoints = parseInt(e.target.value);
            scene.remove(mesh);
            scene.remove(wireframe);
            geometry.dispose();
            geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints, plotPoints);
            mesh.geometry = geometry;
            wireframe.geometry = new THREE.WireframeGeometry(geometry);
            scene.add(mesh);
            scene.add(wireframe);
            updateVisualization();
        });

        // Wave type buttons
        document.getElementById('p-wave-sine-button').addEventListener('click', () => {
            pWaveType = 'sine';
            setActiveButton('p-wave-sine-button', 'p-wave-pulse-button');
            updateVisualization();
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', () => {
            pWaveType = 'pulse';
            setActiveButton('p-wave-pulse-button', 'p-wave-sine-button');
            updateVisualization();
        });

        document.getElementById('s-wave-sine-button').addEventListener('click', () => {
            sWaveType = 'sine';
            setActiveButton('s-wave-sine-button', 's-wave-pulse-button');
            updateVisualization();
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', () => {
            sWaveType = 'pulse';
            setActiveButton('s-wave-pulse-button', 's-wave-sine-button');
            updateVisualization();
        });

        // S-wave direction buttons
        document.getElementById('s-wave-dir-xy-button').addEventListener('click', () => {
            sWaveDirection = 'xy';
            setActiveButton('s-wave-dir-xy-button', 's-wave-dir-z-button');
            updateVisualization();
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', () => {
            sWaveDirection = 'z';
            setActiveButton('s-wave-dir-z-button', 's-wave-dir-xy-button');
            updateVisualization();
        });

        // Viewpoint buttons
        document.getElementById('viewpoint-top-button').addEventListener('click', () => {
            activeCamera = orthoCamera;
            setActiveButton('viewpoint-top-button', 'viewpoint-default-button');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', () => {
            activeCamera = perspectiveCamera;
            setActiveButton('viewpoint-default-button', 'viewpoint-top-button');
        });

        // Slider updates
        document.getElementById('p-wave-amplitude-slider').addEventListener('input', (e) => {
            pAmplitude = parseFloat(e.target.value);
            updateVisualization();
        });

        document.getElementById('p-wave-wavelength-slider').addEventListener('input', (e) => {
            pWavelength = parseFloat(e.target.value);
            updateVisualization();
        });

        document.getElementById('s-wave-amplitude-slider').addEventListener('input', (e) => {
            sAmplitude = parseFloat(e.target.value);
            updateVisualization();
        });

        document.getElementById('s-wave-wavelength-slider').addEventListener('input', (e) => {
            sWavelength = parseFloat(e.target.value);
            updateVisualization();
        });

        function setActiveButton(activeId, inactiveId) {
            document.getElementById(activeId).classList.add('active');
            document.getElementById(inactiveId).classList.remove('active');
        }

        function updateControls() {
            document.getElementById('time-slider').value = time;
        }

        function waveFunction(type, u) {
            if (type === 'sine') {
                return Math.sin(2 * Math.PI * u);
            } else {
                return Math.exp(-u * u);
            }
        }

        function updateVisualization() {
            const positions = geometry.attributes.position.array;
            const colors = geometry.attributes.color.array;
            const count = positions.length / 3;

            for (let i = 0; i < count; i++) {
                const x = positions[i * 3];
                const y = positions[i * 3 + 1];
                const r = Math.sqrt(x * x + y * y);

                // P-wave displacement
                let pDisplacement = 0;
                if (r > 0) {
                    const pU = (r / pWavelength) - time;
                    pDisplacement = pAmplitude * waveFunction(pWaveType, pU);
                }
                const pVector = r > 0 ? [x / r * pDisplacement, y / r * pDisplacement, 0] : [0, 0, 0];

                // S-wave displacement
                let sDisplacement = 0;
                if (r > 0) {
                    const sU = (r / sWavelength) - time;
                    sDisplacement = sAmplitude * waveFunction(sWaveType, sU);
                }
                let sVector;
                if (sWaveDirection === 'z') {
                    sVector = [0, 0, sDisplacement];
                } else {
                    sVector = [-y / r * sDisplacement, x / r * sDisplacement, 0];
                }

                // Final position
                positions[i * 3] = x + pVector[0] + sVector[0];
                positions[i * 3 + 1] = y + pVector[1] + sVector[1];
                positions[i * 3 + 2] = pVector[2] + sVector[2];

                // Color based on z-value
                const z = positions[i * 3 + 2];
                const color = new THREE.Color();
                if (z > 0) {
                    color.setRGB(1, 0, 0); // Red for positive
                } else if (z < 0) {
                    color.setRGB(0.678, 0.847, 0.902); // Light blue for negative
                } else {
                    color.setRGB(1, 1, 1); // White for zero
                }
                colors[i * 3] = color.r;
                colors[i * 3 + 1] = color.g;
                colors[i * 3 + 2] = color.b;
            }

            geometry.attributes.position.needsUpdate = true;
            geometry.attributes.color.needsUpdate = true;
        }

        function animate() {
            requestAnimationFrame(animate);
            if (isPlaying) {
                time += 0.016; // Roughly 60 FPS
                document.getElementById('time-slider').value = time.toFixed(1);
                updateVisualization();
            }
            renderer.render(scene, activeCamera);
        }

        // Initial setup
        updateControls();
        setActiveButton('p-wave-sine-button', 'p-wave-pulse-button');
        setActiveButton('s-wave-sine-button', 's-wave-pulse-button');
        setActiveButton('s-wave-dir-xy-button', 's-wave-dir-z-button');
        setActiveButton('viewpoint-top-button', 'viewpoint-default-button');

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = container.clientWidth;
            const height = container.clientHeight;
            renderer.setSize(width, height);
            perspectiveCamera.aspect = width / height;
            perspectiveCamera.updateProjectionMatrix();
            orthoCamera.left = -width / height * 10;
            orthoCamera.right = width / height * 10;
            orthoCamera.updateProjectionMatrix();
        });

        animate();
    </script>
</body>
</html>
