    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <div>
                <label for="slider-z">z</label>
                <input type="range" id="slider-z" min="-5" max="5" step="0.01" value="1.5">
                <span id="label-z">1.5</span>
            </div>
            <div>
                <label for="slider-plot-range">plot range</label>
                <input type="range" id="slider-plot-range" min="1" max="10" step="0.5" value="4">
                <span id="label-plot-range">4</span>
            </div>
            <div>
                <input type="checkbox" id="check-show-labels">
                <label for="check-show-labels">show labels</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-circle">
                <label for="check-show-circle">show circle</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-axes">
                <label for="check-show-axes">show axes</label>
            </div>
            <div>
                <input type="checkbox" id="check-show-grid-lines">
                <label for="check-show-grid-lines">show grid lines</label>
            </div>
            <button id="btn-new-example">new example</button>
            <button id="btn-golden-ratio">golden ratio</button>
        </div>

        <!-- Visualization Area -->
        <div id="visualization-area">
            <div id="canvas-container"></div>
            <div id="formula-display">P(z) = <span id="formula-text">z^2 - z - 1</span></div>
            <table id="results-table">
                <thead>
                    <tr>
                        <th>P(z)</th>
                        <th>|L<sub>3</sub> L'<sub>2</sub>|</th>
                        <th>z</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="value-pz">-0.25</td>
                        <td id="value-l3l2">0.25</td>
                        <td id="value-z">1.5</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>

    <!-- MathJax and p5.js -->
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // State variables
        let a = 1, b = -1, c = -1;
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;

        // Point coordinates
        let O, L1, L2, L3, L1p, L2p;

        // p5 instance mode
        let myp5 = new p5((sketch) => {
            sketch.setup = function() {
                let canvas = sketch.createCanvas(sketch.windowWidth * 0.7, 500);
                canvas.parent('canvas-container');
                sketch.noLoop();
                update();
            };

            sketch.draw = function() {
                sketch.background(255);

                // Calculate view transformation
                const bounds = getBoundingBox();
                const scale = Math.min(sketch.width / (bounds.width * 2), sketch.height / (bounds.height * 2));
                const centerX = bounds.centerX;
                const centerY = bounds.centerY;

                sketch.push();
                sketch.translate(sketch.width/2, sketch.height/2);
                sketch.scale(scale);
                sketch.translate(-centerX, -centerY);
                sketch.scale(1, -1); // Flip Y-axis

                // Draw grid
                if (showGridLines) {
                    sketch.stroke(220);
                    sketch.strokeWeight(0.5);
                    const gridSize = plotRange / 5;
                    for (let x = -plotRange; x <= plotRange; x += gridSize) {
                        sketch.line(x, -plotRange, x, plotRange);
                        sketch.line(-plotRange, x, plotRange, x);
                    }
                }

                // Draw axes
                if (showAxes) {
                    sketch.stroke(0);
                    sketch.strokeWeight(1);
                    sketch.line(-plotRange, 0, plotRange, 0);
                    sketch.line(0, -plotRange, 0, plotRange);

                    // Axis labels
                    if (showLabels) {
                        sketch.textSize(12);
                        sketch.fill(0);
                        for (let x = -plotRange; x <= plotRange; x += gridSize) {
                            if (x !== 0) {
                                sketch.text(x.toFixed(1), x, -plotRange * 0.05);
                                sketch.text(x.toFixed(1), -plotRange * 0.05, x);
                            }
                        }
                    }
                }

                // Draw coefficient path
                sketch.stroke(0);
                sketch.strokeWeight(2);
                sketch.line(O.x, O.y, L1.x, L1.y);
                sketch.line(L1.x, L1.y, L2.x, L2.y);
                sketch.line(L2.x, L2.y, L3.x, L3.y);
                sketch.fill(0);
                sketch.ellipse(O.x, O.y, 6, 6);
                sketch.ellipse(L1.x, L1.y, 6, 6);
                sketch.ellipse(L2.x, L2.y, 6, 6);
                sketch.ellipse(L3.x, L3.y, 6, 6);

                // Draw test path
                sketch.stroke(0);
                sketch.line(O.x, O.y, L1p.x, L1p.y);
                sketch.stroke(255, 0, 255);
                sketch.line(L1p.x, L1p.y, L2p.x, L2p.y);
                sketch.stroke(255, 0, 0);
                sketch.line(L2p.x, L2p.y, L3.x, L3.y);
                sketch.fill(0);
                sketch.ellipse(L1p.x, L1p.y, 6, 6);
                sketch.ellipse(L2p.x, L2p.y, 6, 6);

                // Draw circle
                if (showCircle) {
                    sketch.noFill();
                    sketch.stroke(0);
                    sketch.ellipseMode(sketch.CENTER);
                    const circleCenter = {x: (a - c)/2, y: b/2};
                    const diameter = Math.sqrt((a - c)**2 + b**2);
                    sketch.ellipse(circleCenter.x, circleCenter.y, diameter, diameter);
                }

                // Draw labels
                if (showLabels) {
                    sketch.textSize(12);
                    sketch.fill(0);
                    sketch.text("O", O.x + 5, O.y + 15);
                    sketch.text("L1", L1.x + 5, L1.y + 15);
                    sketch.text("L2", L2.x + 5, L2.y + 15);
                    sketch.text("L3", L3.x + 5, L3.y + 15);
                    sketch.text("L1'", L1p.x + 5, L1p.y + 15);
                    sketch.text("L2'", L2p.x + 5, L2p.y + 15);
                }

                sketch.pop();
            };
        });

        // Initialize event listeners
        document.getElementById('slider-z').addEventListener('input', (e) => {
            z = parseFloat(e.target.value);
            document.getElementById('label-z').textContent = z.toFixed(2);
            update();
        });

        document.getElementById('slider-plot-range').addEventListener('input', (e) => {
            plotRange = parseFloat(e.target.value);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);
            update();
        });

        document.getElementById('check-show-labels').addEventListener('change', (e) => {
            showLabels = e.target.checked;
            update();
        });

        document.getElementById('check-show-circle').addEventListener('change', (e) => {
            showCircle = e.target.checked;
            update();
        });

        document.getElementById('check-show-axes').addEventListener('change', (e) => {
            showAxes = e.target.checked;
            update();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', (e) => {
            showGridLines = e.target.checked;
            update();
        });

        document.getElementById('btn-new-example').addEventListener('click', () => {
            a = Math.floor(Math.random() * 3 + 1) * (Math.random() < 0.5 ? -1 : 1);
            b = Math.floor(Math.random() * 11 - 5);
            c = Math.floor(Math.random() * 11 - 5);
            update();
        });

        document.getElementById('btn-golden-ratio').addEventListener('click', () => {
            a = 1; b = -1; c = -1;
            update();
        });

        function update() {
            // Update points
            O = {x: 0, y: 0};
            L1 = {x: a, y: 0};
            L2 = {x: a, y: b};
            L3 = {x: a - c, y: b};
            L1p = {x: a, y: -z * a};
            L2p = {x: a + b*z + a*z*z, y: b};

            // Calculate values
            const Pz = a*z*z + b*z + c;
            const error = Math.sqrt((L2p.x - L3.x)**2 + (L2p.y - L3.y)**2);

            // Update display
            document.getElementById('value-pz').textContent = Pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = error.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(2);

            // Update formula
            const formula = `${term(a, 'z^2')} ${term(b, 'z')} ${term(c)}`;
            document.getElementById('formula-text').textContent = formula;
            MathJax.typesetPromise([document.getElementById('formula-display')]);

            // Redraw canvas
            myp5.redraw();
        }

        function term(coeff, variable = '') {
            if (coeff === 0) return '';
            const sign = coeff > 0 ? '+' : '-';
            const abs = Math.abs(coeff);
            const coeffStr = (abs === 1 && variable) ? '' : abs;
            return `${sign} ${coeffStr}${variable}`.trim();
        }

        function getBoundingBox() {
            const points = [O, L1, L2, L3, L1p, L2p];
            if (showCircle) {
                const circleCenter = {x: (a - c)/2, y: b/2};
                const radius = Math.sqrt((a - c)**2 + b**2)/2;
                points.push(
                    {x: circleCenter.x - radius, y: circleCenter.y - radius},
                    {x: circleCenter.x + radius, y: circleCenter.y + radius}
                );
            }
            const xs = points.map(p => p.x);
            const ys = points.map(p => p.y);
            const minX = Math.min(...xs), maxX = Math.max(...xs);
            const minY = Math.min(...ys), maxY = Math.max(...ys);
            return {
                centerX: (minX + maxX) / 2,
                centerY: (minY + maxY) / 2,
                width: maxX - minX,
                height: maxY - minY
            };
        }

        // Initialize with golden ratio
        update();
    </script>
</body>
</html>
