        #parameter-controls input[type="range"] {
            flex: 1;
            margin-right: 10px;
        }

        #parameter-controls #alpha-value-display {
            width: 60px;
            text-align: right;
            font-family: monospace;
        }

        #main-display {
            display: flex;
            flex-direction: column;
            background: white;
            padding: 10px;
            border-radius: 4px;
            min-height: 600px;
        }

        #plot-view {
            width: 100%;
            height: 600px;
            position: relative;
        }

        #table-view {
            width: 100%;
            overflow-x: auto;
        }

        table {
            width: 100%;
            border-collapse: collapse;
            margin-top: 10px;
        }

        th, td {
            border: 1px solid black;
            padding: 8px;
            text-align: center;
        }

        th {
            background: #f0f0f0;
        }

        td:nth-child(1) { background: #ffffcc; }
        td:nth-child(2) { background: #ccffcc; }
