        td:nth-child(4) { background: #ffccff; }
        td:nth-child(5) { background: #ffcccc; }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <div id="view-switcher">
                <button id="btn-plot" class="active">T₂(α)</button>
                <button id="btn-table">solution list</button>
                <button id="btn-turning-points">turning points</button>
            </div>
            <div id="info-button-container">
                <button id="btn-info">+</button>
            </div>
        </div>

        <div id="parameter-controls">
            <label for="slider-alpha">α</label>
            <input type="range" id="slider-alpha" min="0.00" max="0.07" step="0.0001" value="0.039">
            <span id="alpha-value-display">0.039</span>
        </div>

        <div id="main-display">
            <div id="plot-view"></div>
            <div id="table-view" style="display: none;">
                <table id="solutions-table">
                    <thead>
                        <tr>
                            <th>root</th>
                            <th>X₁</th>
                            <th>X₂</th>
                            <th>T₁</th>
                            <th>T₂</th>
                        </tr>
                    </thead>
                    <tbody id="solutions-table-body"></tbody>
                </table>
            </div>
        </div>
    </div>

    <script>
        // State management
        let currentView = 'plot_solutions';
        const alphaSlider = document.getElementById('slider-alpha');
        const alphaDisplay = document.getElementById('alpha-value-display');
        const plotView = document.getElementById('plot-view');
        const tableView = document.getElementById('table-view');
        const parameterControls = document.getElementById('parameter-controls');

        // Predefined datasets
        const solutionCurveData = [
            {alpha: 0.00, x1: 0.0, x2: 0.0, t1: 0.0, t2: 0.0},
            {alpha: 0.01, x1: 0.5, x2: 0.3, t1: 1.2, t2: 2.5},
            {alpha: 0.02, x1: 0.8, x2: 0.5, t1: 2.1, t2: 4.0},
            {alpha: 0.03, x1: 1.0, x2: 0.7, t1: 2.8, t2: 5.5},
            {alpha: 0.04, x1: 0.9, x2: 0.8, t1: 3.0, t2: 6.2},
            {alpha: 0.05, x1: 0.7, x2: 0.9, t1: 2.7, t2: 7.0},
            {alpha: 0.06, x1: 0.4, x2: 1.0, t1: 2.0, t2: 7.5},
            {alpha: 0.07, x1: 0.0, x2: 1.0, t1: 1.0, t2: 8.0}
        ];

        const turningPointsData = [
            {alpha: 0.005, t2: 1.0},
            {alpha: 0.015, t2: 3.0},
            {alpha: 0.025, t2: 5.0},
            {alpha: 0.035, t2: 6.5},
            {alpha: 0.045, t2: 7.2},
            {alpha: 0.055, t2: 7.8}
        ];

        // Initialize slider and display
        alphaSlider.value = 0.039;
        alphaDisplay.textContent = '0.039';

        // View switching logic
        document.getElementById('btn-plot').addEventListener('click', () => {
            currentView = 'plot_solutions';
            updateView();
        });

        document.getElementById('btn-table').addEventListener('click', () => {
            currentView = 'table';
            updateView();
            updateTable();
        });

        document.getElementById('btn-turning-points').addEventListener('click', () => {
            currentView = 'plot_turning_points';
            updateView();
        });

        // Slider update logic
        alphaSlider.addEventListener('input', () => {
            const value = parseFloat(alphaSlider.value);
            alphaDisplay.textContent = value.toFixed(3);

            if (currentView === 'plot_solutions') {
                redrawPlot();
            } else if (currentView === 'table') {
                updateTable();
            }
        });

        // Update UI based on current view
        function updateView() {
            // Update button states
            document.querySelectorAll('#view-switcher button').forEach(btn => {
                btn.classList.remove('active');
            });
            if (currentView === 'plot_solutions') {
                document.getElementById('btn-plot').classList.add('active');
            } else if (currentView === 'table') {
                document.getElementById('btn-table').classList.add('active');
            } else if (currentView === 'plot_turning_points') {
                document.getElementById('btn-turning-points').classList.add('active');
            }

            // Show/hide elements
            plotView.style.display = currentView === 'plot_turning_points' || currentView === 'plot_solutions' ? 'block' : 'none';
            tableView.style.display = currentView === 'table' ? 'block' : 'none';
            parameterControls.style.display = currentView === 'plot_turning_points' ? 'none' : 'flex';

            // Redraw content
            if (currentView === 'plot_solutions' || currentView === 'plot_turning_points') {
                redrawPlot();
            } else if (currentView === 'table') {
                updateTable();
            }
        }

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                p.createCanvas(600, 600);
                redrawPlot();
            };

            p.draw = function() {
                // Drawing handled in redrawPlot
            };
        };

        new p5(sketch, 'plot-view');

        // Plot redrawing logic
        function redrawPlot() {
            const sketch = window._p5Instance;
            if (!sketch) return;

            sketch.draw = function() {
                const canvas = this.canvas;
                const padding = 40;
                const width = canvas.width - padding * 2;
                const height = canvas.height - padding * 2;

                // Background
                this.background(255);

                // Coordinate system
                this.push();
                this.translate(padding, canvas.height - padding);

                // Axes
                this.stroke(0);
                this.line(0, 0, width, 0); // X-axis
                this.line(0, 0, 0, -height); // Y-axis

                // Grid and labels
                const alphaRange = 0.07;
                const t2Range = 8;

                // X-axis labels
                for (let i = 0; i <= 10; i++) {
                    const x = (i / 10) * width;
                    const alpha = (i / 10) * alphaRange;
                    this.line(x, 0, x, -5);
                    this.textSize(10);
                    this.text(alpha.toFixed(2), x - 10, 15);
                }

                // Y-axis labels
                for (let i = 0; i <= 8; i++) {
                    const y = -(i / 8) * height;
                    this.line(0, y, 5, y);
                    this.textSize(10);
                    this.text(i.toFixed(1), -25, y + 4);
                }

                // Draw solution curve
                this.stroke(255, 0, 0);
                this.strokeWeight(2);
                this.noFill();
                this.beginShape();
                solutionCurveData.forEach(point => {
                    const x = (point.alpha / alphaRange) * width;
                    const y = -(point.t2 / t2Range) * height;
                    this.vertex(x, y);
                });
                this.endShape();

                // View-specific drawing
                if (currentView === 'plot_solutions') {
                    // Draw vertical line
                    const currentAlpha = parseFloat(alphaSlider.value);
                    const x = (currentAlpha / alphaRange) * width;
                    this.stroke(0, 200, 0);
                    this.strokeWeight(1);
                    this.drawingContext.setLineDash([5, 5]);
                    this.line(x, 0, x, -height);
                    this.drawingContext.setLineDash([]);

                    // Find and draw intersection points
                    const intersections = findIntersections(currentAlpha);
                    this.noStroke();
                    intersections.forEach(point => {
                        const y = -(point.t2 / t2Range) * height;
                        this.fill(0, 0, 255);
                        this.ellipse(x, y, 8, 8);
                    });
                } else if (currentView === 'plot_turning_points') {
                    // Draw turning points
                    this.noStroke();
                    turningPointsData.forEach(point => {
                        const x = (point.alpha / alphaRange) * width;
                        const y = -(point.t2 / t2Range) * height;
                        this.fill(0, 255, 0);
                        this.ellipse(x, y, 8, 8);
                    });
                }

                this.pop();
            };

            sketch.redraw();
        }

        // Table update logic
        function updateTable() {
            const tbody = document.getElementById('solutions-table-body');
            const currentAlpha = parseFloat(alphaSlider.value);
            const intersections = findIntersections(currentAlpha);

            tbody.innerHTML = '';

            intersections.forEach((point, index) => {
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${index + 1}</td>
                    <td>${point.x1.toFixed(4)}</td>
                    <td>${point.x2.toFixed(4)}</td>
                    <td>${point.t1.toFixed(4)}</td>
                    <td>${point.t2.toFixed(4)}</td>
                `;
                tbody.appendChild(row);
            });
        }

        // Linear interpolation to find intersections
        function findIntersections(targetAlpha) {
            const results = [];
            const data = solutionCurveData;

            for (let i = 0; i < data.length - 1; i++) {
                const p1 = data[i];
                const p2 = data[i + 1];

                if ((p1.alpha <= targetAlpha && p2.alpha >= targetAlpha) ||
                    (p1.alpha >= targetAlpha && p2.alpha <= targetAlpha)) {

                    // Linear interpolation
                    const t = (targetAlpha - p1.alpha) / (p2.alpha - p1.alpha);
                    const interpolated = {
                        alpha: targetAlpha,
                        x1: p1.x1 + (p2.x1 - p1.x1) * t,
                        x2: p1.x2 + (p2.x2 - p1.x2) * t,
                        t1: p1.t1 + (p2.t1 - p1.t1) * t,
                        t2: p1.t2 + (p2.t2 - p1.t2) * t
                    };
                    results.push(interpolated);
                }
            }

            return results;
        }

        // Initialize
        updateView();
    </script>
</body>
</html>
