<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Logarithmic Spiral Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: sans-serif;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel {
            width: 100%;
            background: #f0f0f0;
            padding: 10px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
        #controls-row-1, #controls-row-2 {
            display: flex;
            gap: 20px;
            margin-bottom: 10px;
        }
        #type-controls, #item-controls {
            display: flex;
            align-items: center;
            gap: 5px;
        }
        button {
            padding: 5px 10px;
            border: 1px solid #ccc;
            background: #fff;
            cursor: pointer;
        }
        button.active {
            background: #007bff;
            color: white;
            border-color: #0056b3;
        }
        #growth-slider-container {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #canvas-container {
            width: 100%;
            display: flex;
            justify-content: center;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="controls-row-1">
                <div id="type-controls">
                    <span>type</span>
                    <button id="btn-type-single">single</button>
                    <button id="btn-type-double" class="active">double</button>
                </div>
                <div id="item-controls">
                    <span>item</span>
                    <button id="btn-item-none">none</button>
                    <button id="btn-item-point" class="active">point</button>
                    <button id="btn-item-fox">fox</button>
                    <button id="btn-item-filled-fox">filled fox</button>
                </div>
            </div>
            <div id="controls-row-2">
                <div id="growth-slider-container">
                    <label for="slider-growth">spiral growth</label>
                    <input type="range" id="slider-growth" min="0.01" max="0.5" step="0.0005" value="0.15">
                    <span id="display-growth">0.1500</span>
                    <button id="btn-reset-growth">+</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };
        let dragging = null;

        function setup() {
            const canvas = createCanvas(800, 800);
            canvas.parent('canvas-container');
            noLoop();
            setupControls();
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            if (spiralType === 'single') {
                drawSingleSpiral();
                drawLocator(z0_loc, 'yellow', 'z0Loc');
            } else {
                drawDoubleSpiral();
                drawLocator(z_plus_loc, 'yellow', 'z+');
                drawLocator(z_minus_loc, 'green', 'z-');
            }
        }

        function drawSingleSpiral() {
            const k = spiralGrowth;
            const tMax = 8 * PI;
            const tMin = -tMax;
            const step = 0.1;
            beginShape();
            stroke('#4A6DA8');
            strokeWeight(2);
            noFill();
            for (let t = tMin; t <= tMax; t += step) {
                const r = exp(k * t);
                const x = z0_loc.x + r * cos(t);
                const y = z0_loc.y - r * sin(t);
                vertex(x, y);
            }
            endShape();
            drawItems(tMin, tMax, step);
        }

        function drawDoubleSpiral() {
            const k = spiralGrowth;
            const p = z_plus_loc;
            const q = z_minus_loc;
            const tMax = 8 * PI;
            const epsilon = 0.01;
            const step = 0.1;
            const branches = [[-tMax, -epsilon], [epsilon, tMax]];

            for (const [tStart, tEnd] of branches) {
                beginShape();
                stroke('#4A6DA8');
                strokeWeight(2);
                noFill();
                for (let t = tStart; t <= tEnd; t += step) {
                    const w = exp((k + 1i) * t);
                    const denom = w - 1;
                    const z = { x: (p.x * w.real - q.x) / denom.real, y: (p.y * w.imag - q.y) / denom.imag };
                    vertex(z.x, z.y);
                }
                endShape();
            }
            drawItems(-tMax, tMax, step);
        }

        function drawItems(tMin, tMax, step) {
            if (itemType === 'none') return;
            const k = spiralGrowth;
            const p = z_plus_loc;
            const q = z_minus_loc;
            stroke('#CB3335');
            fill('#CB3335');

            for (let t = tMin; t <= tMax; t += step * 10) {
                if (spiralType === 'single') {
                    const r = exp(k * t);
                    const x = z0_loc.x + r * cos(t);
                    const y = z0_loc.y - r * sin(t);
                    if (itemType === 'point') {
                        ellipse(x, y, 5, 5);
                    } else {
                        drawFox(x, y, t);
                    }
                } else {
                    const w = exp((k + 1i) * t);
                    const denom = w - 1;
                    const z = { x: (p.x * w.real - q.x) / denom.real, y: (p.y * w.imag - q.y) / denom.imag };
                    if (itemType === 'point') {
                        ellipse(z.x, z.y, 5, 5);
                    } else {
                        drawFox(z.x, z.y, t);
                    }
                }
            }
        }

        function drawFox(x, y, t) {
            const vertices = [
                {x:-15,y:0}, {x:-10,y:-5}, {x:-5,y:-4}, {x:0,y:-5}, {x:5,y:-5}, 
                {x:8,y:-2}, {x:10,y:-6}, {x:12,y:-6}, {x:15,y:0}, {x:12,y:6}, 
                {x:10,y:6}, {x:8,y:2}, {x:5,y:5}, {x:0,y:5}, {x:-5,y:4}, {x:-10,y:5}
            ];
            push();
            translate(x, y);
            rotate(atan2(-sin(t), cos(t)) + PI/2);
            scale(0.3);
            beginShape();
            if (itemType === 'fox') {
                noFill();
                stroke('#D6292D');
                strokeWeight(1);
            } else {
                fill('#D6292D');
                noStroke();
            }
            for (const v of vertices) vertex(v.x, v.y);
            endShape(CLOSE);
            pop();
        }

        function drawLocator(loc, color, label) {
            fill(color);
            stroke(0);
            ellipse(loc.x, loc.y, 20, 20);
            line(loc.x - 10, loc.y, loc.x + 10, loc.y);
            line(loc.x, loc.y - 10, loc.x, loc.y + 10);
            fill(0);
            textSize(12);
            text(label, loc.x + 12, loc.y + 5);
        }

        function setupControls() {
            // Button groups
            const typeButtons = {
                'single': document.getElementById('btn-type-single'),
                'double': document.getElementById('btn-type-double')
            };
            const itemButtons = {
                'none': document.getElementById('btn-item-none'),
                'point': document.getElementById('btn-item-point'),
                'fox': document.getElementById('btn-item-fox'),
                'filled-fox': document.getElementById('btn-item-filled-fox')
            };

            function setActive(buttons, key) {
                for (const btn of Object.values(buttons)) btn.classList.remove('active');
                buttons[key].classList.add('active');
            }

            Object.entries(typeButtons).forEach(([key, btn]) => {
                btn.addEventListener('click', () => {
                    spiralType = key;
                    setActive(typeButtons, key);
                    redraw();
                });
            });

            Object.entries(itemButtons).forEach(([key, btn]) => {
                btn.addEventListener('click', () => {
                    itemType = key;
                    setActive(itemButtons, key);
                    redraw();
                });
            });

            // Slider
            const slider = document.getElementById('slider-growth');
            const display = document.getElementById('display-growth');
            slider.addEventListener('input', () => {
                spiralGrowth = parseFloat(slider.value);
                display.textContent = spiralGrowth.toFixed(4);
                redraw();
            });

            // Reset button
            document.getElementById('btn-reset-growth').addEventListener('click', () => {
                spiralGrowth = 0.25;
                slider.value = 0.25;
                display.textContent = '0.2500';
                redraw();
            });
        }

        function mousePressed() {
            const locators = spiralType === 'single' ? [z0_loc] : [z_plus_loc, z_minus_loc];
            for (const loc of locators) {
                if (dist(mouseX - width/2, mouseY - height/2, loc.x, loc.y) < 10) {
                    dragging = loc;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (dragging) {
                dragging.x = mouseX - width/2;
                dragging.y = mouseY - height/2;
                redraw();
            }
        }

        function mouseReleased() {
            dragging = null;
        }

        function redraw() {
            loop();
            noLoop();
        }
    </script>
</body>
</html>
