    </style>
</head>
<body>
    <div id="control-panel">
        <button id="start-over-btn">start over</button>
        <label>initial vertex</label>
        <div class="vertex-grid">
            <button id="btn-m">m</button><button id="btn-n">n</button><button id="btn-o">o</button><button id="btn-p">p</button>
            <button id="btn-i">i</button><button id="btn-j">j</button><button id="btn-k">k</button><button id="btn-l">l</button>
            <button id="btn-e">e</button><button id="btn-f">f</button><button id="btn-g">g</button><button id="btn-h">h</button>
            <button id="btn-a">a</button><button id="btn-b">b</button><button id="btn-c">c</button><button id="btn-d">d</button>
        </div>
        <div>
            <button id="fixed-example-btn">fixed example</button>
            <button id="random-example-btn">random example</button>
        </div>
        <label>implement algorithm</label>
        <button id="next-step-btn">next step</button>
    </div>
    <div id="viz-container">
        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container"></div>
    </div>

    <script>
        const vertices = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        const fixedEdges = {
            'm-n':8, 'n-o':7, 'o-p':7,
            'i-j':9, 'j-k':7, 'k-l':1,
            'e-f':1, 'f-g':7, 'g-h':6,
            'a-b':10, 'b-c':4, 'c-d':8,
            'm-i':6, 'i-e':9, 'e-a':9,
            'n-j':7, 'j-f':6, 'f-b':5,
            'o-k':1, 'k-g':8, 'g-c':8,
            'p-l':2, 'l-h':1, 'h-d':7
        };
        let edges = {...fixedEdges};
        let distances = {};
        let visited = new Set();
        let predecessors = {};
        let startVertex = 'm';
        let nextStepEnabled = true;

        function setup() {
            let canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            resetAlgorithm();
        }

        function draw() {
            background(255);
            drawGraph();
        }

        function drawGraph() {
            // Draw edges
            for (let edge in edges) {
                const [a, b] = edge.split('-');
                const [x1, y1] = getVertexPosition(a);
                const [x2, y2] = getVertexPosition(b);
                stroke(0);
                strokeWeight(1);
                line(x1, y1, x2, y2);

                // Draw edge weight
                fill(255);
                noStroke();
                const midX = (x1 + x2) / 2;
                const midY = (y1 + y2) / 2;
                text(edges[edge], midX, midY);
            }

            // Draw vertices and labels
            for (let v of vertices) {
                const [x, y] = getVertexPosition(v);
                fill(visited.has(v) ? '#ff0000' : '#000000');
                ellipse(x, y, 15, 15);

                // Draw vertex label
                fill(0);
                textSize(12);
                text(v, x + 10, y);

                // Draw distance label
                const dist = distances[v] === Infinity ? '∞' : `(${distances[v]})`;
                fill(visited.has(v) ? '#ff0000' : '#000000');
                textSize(10);
                text(dist, x, y - 10);
            }

            // Draw shortest path tree edges
            for (let v in predecessors) {
                if (predecessors[v]) {
                    const u = predecessors[v];
                    const [x1, y1] = getVertexPosition(u);
                    const [x2, y2] = getVertexPosition(v);
                    stroke('#0000ff');
                    strokeWeight(3);
                    line(x1, y1, x2, y2);
                }
            }
        }

        function getVertexPosition(v) {
            const index = vertices.indexOf(v);
            const col = index % 4;
            const row = Math.floor(index / 4);
            const padding = 50;
            const spacing = 150;
            return [padding + col * spacing, padding + row * spacing];
        }

        function resetAlgorithm() {
            visited.clear();
            predecessors = {};
            distances = {};
            vertices.forEach(v => distances[v] = Infinity);
            distances[startVertex] = 0;
            nextStepEnabled = true;
            document.getElementById('next-step-btn').disabled = false;
            redraw();
        }

        function resetToDefaults() {
            startVertex = 'm';
            edges = {...fixedEdges};
            resetAlgorithm();
            updateUI();
        }

        function updateUI() {
            document.getElementById('title-display').innerText = `Dijkstra's algorithm starting at vertex ${startVertex}`;
            vertices.forEach(v => {
                const btn = document.getElementById(`btn-${v}`);
                btn.classList.toggle('active', v === startVertex);
            });
        }

        function generateRandomWeights() {
            const randomEdges = {};
            const edgeList = Object.keys(fixedEdges);
            edgeList.forEach(edge => {
                randomEdges[edge] = Math.floor(Math.random() * 10) + 1;
            });
            return randomEdges;
        }

        function dijkstraStep() {
            if (nextStepEnabled) {
                const unvisited = vertices.filter(v => !visited.has(v));
                if (unvisited.length === 0) {
                    nextStepEnabled = false;
                    document.getElementById('next-step-btn').disabled = true;
                    return;
                }

                // Find unvisited vertex with smallest distance
                let u = unvisited.reduce((a, b) => distances[a] < distances[b] ? a : b);

                visited.add(u);

                // Update neighbors
                const neighbors = getNeighbors(u);
                neighbors.forEach(v => {
                    if (!visited.has(v)) {
                        const weight = getEdgeWeight(u, v);
                        const newDist = distances[u] + weight;
                        if (newDist < distances[v]) {
                            distances[v] = newDist;
                            predecessors[v] = u;
                        }
                    }
                });

                redraw();
            }
        }

        function getNeighbors(v) {
            const neighbors = [];
            for (let edge in edges) {
                const [a, b] = edge.split('-');
                if (a === v) neighbors.push(b);
                if (b === v) neighbors.push(a);
            }
            return neighbors;
        }

        function getEdgeWeight(a, b) {
            return edges[`${a}-${b}`] || edges[`${b}-${a}`];
        }

        // Event Listeners
        document.getElementById('start-over-btn').addEventListener('click', () => {
            resetToDefaults();
        });

        vertices.forEach(v => {
            document.getElementById(`btn-${v}`).addEventListener('click', () => {
                startVertex = v;
                resetAlgorithm();
                updateUI();
            });
        });

        document.getElementById('fixed-example-btn').addEventListener('click', () => {
            edges = {...fixedEdges};
            resetAlgorithm();
            redraw();
        });

        document.getElementById('random-example-btn').addEventListener('click', () => {
            edges = generateRandomWeights();
            resetAlgorithm();
            redraw();
        });

        document.getElementById('next-step-btn').addEventListener('click', () => {
            dijkstraStep();
        });

        // Initial setup
        function setup() {
            let canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            resetAlgorithm();
            updateUI();
        }
    </script>
</body>
</html>
