<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Platonic Solid Cones</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #app-container {
            display: flex;
            height: 100vh;
        }
        #controls-panel {
            width: 320px;
            padding: 20px;
            background: #f8f8f8;
            border-right: 1px solid #ccc;
            overflow-y: auto;
        }
        #canvas-container {
            flex-grow: 1;
            position: relative;
        }
        label {
            display: block;
            margin-top: 15px;
            font-weight: bold;
        }
        input[type="range"] {
            width: 100%;
            margin-top: 5px;
        }
        #polyhedron-buttons {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            gap: 8px;
            margin-top: 10px;
        }
        button {
            padding: 8px;
            background: #e0e0e0;
            border: 1px solid #ccc;
            cursor: pointer;
            border-radius: 4px;
        }
        button.active {
            background: #007bff;
            color: white;
            border-color: #0056b3;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <label for="slider-sphere-diameter">Diameter of sphere</label>
            <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">

            <label>Polyhedron vertices</label>
            <div id="polyhedron-buttons">
                <button id="btn-cube">Cube</button>
                <button id="btn-dodecahedron">Dodecahedron</button>
                <button id="btn-icosahedron">Icosahedron</button>
                <button id="btn-octahedron" class="active">Octahedron</button>
                <button id="btn-tetrahedron">Tetrahedron</button>
            </div>

            <label for="slider-cone-height">Height</label>
            <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">

            <label for="slider-cone-base">Base</label>
            <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/three@0.155.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.155.0/examples/jsm/controls/OrbitControls.min.js"></script>
    <script>
        let scene, camera, renderer, controls;
        let sphere, cones = [];
        let currentPolyhedron = 'octahedron';
        const defaultSphereDiameter = 1.5;
        const coneMaterial = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });
        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0xdddddd, shininess: 50 });

        init();
        animate();

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 100);
            camera.position.set(0, 0, 8);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls setup
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040, 1);
            scene.add(ambientLight);

            const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight1.position.set(1, 1, 1);
            scene.add(dirLight1);

            const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
            dirLight2.position.set(-1, -1, -1);
            scene.add(dirLight2);

            // Initialize 3D objects
            createSphere();
            updateCones();

            // Event listeners
            document.getElementById('slider-sphere-diameter').addEventListener('input', updateScene);
            document.getElementById('slider-cone-height').addEventListener('input', updateCones);
            document.getElementById('slider-cone-base').addEventListener('input', updateCones);

            const buttons = {
                'cube': document.getElementById('btn-cube'),
                'dodecahedron': document.getElementById('btn-dodecahedron'),
                'icosahedron': document.getElementById('btn-icosahedron'),
                'octahedron': document.getElementById('btn-octahedron'),
                'tetrahedron': document.getElementById('btn-tetrahedron')
            };

            Object.entries(buttons).forEach(([polyhedron, button]) => {
                button.addEventListener('click', () => {
                    currentPolyhedron = polyhedron;
                    Object.values(buttons).forEach(btn => btn.classList.remove('active'));
                    button.classList.add('active');
                    updateCones();
                });
            });

            window.addEventListener('resize', onWindowResize);
        }

        function createSphere() {
            const geometry = new THREE.SphereGeometry(1, 32, 32);
            sphere = new THREE.Mesh(geometry, sphereMaterial);
            scene.add(sphere);
        }

        function updateScene() {
            const diameter = parseFloat(document.getElementById('slider-sphere-diameter').value);
            sphere.scale.set(diameter / defaultSphereDiameter, diameter / defaultSphereDiameter, diameter / defaultSphereDiameter);
            updateCones();
        }

        function updateCones() {
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            const coneBase = parseFloat(document.getElementById('slider-cone-base').value);
            const sphereRadius = parseFloat(document.getElementById('slider-sphere-diameter').value) / 2;

            // Remove existing cones
            cones.forEach(cone => scene.remove(cone));
            cones = [];

            // Get vertices
            let vertices = [];
            switch(currentPolyhedron) {
                case 'cube':
                    vertices = getVertices(new THREE.BoxGeometry(1, 1, 1));
                    break;
                case 'dodecahedron':
                    vertices = getVertices(new THREE.DodecahedronGeometry(1));
                    break;
                case 'icosahedron':
                    vertices = getVertices(new THREE.IcosahedronGeometry(1));
                    break;
                case 'octahedron':
                    vertices = getVertices(new THREE.OctahedronGeometry(1));
                    break;
                case 'tetrahedron':
                    vertices = getVertices(new THREE.TetrahedronGeometry(1));
                    break;
            }

            // Create new cones
            vertices.forEach(vertex => {
                const direction = vertex.clone().normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);

                const geometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                const cone = new THREE.Mesh(geometry, coneMaterial);
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                scene.add(cone);
                cones.push(cone);
            });
        }

        function getVertices(geometry) {
            const vertices = [];
            const positionAttribute = geometry.attributes.position;
            for (let i = 0; i < positionAttribute.count; i++) {
                const vertex = new THREE.Vector3();
                vertex.fromBufferAttribute(positionAttribute, i);
                vertices.push(vertex);
            }
            return vertices;
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
    </script>
</body>
</html>
