<!DOCTYPE html>
<html>
<head>
    <title>Analemma Simulation</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
        }
        #main-container {
            display: flex;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f0f0f0;
        }
        #visualization-area {
            flex-grow: 1;
            padding: 20px;
        }
        .control-group {
            margin-bottom: 15px;
        }
        label {
            display: block;
            margin-top: 10px;
        }
        input[type="range"] {
            width: 100%;
        }
        input:disabled {
            opacity: 0.5;
            cursor: not-allowed;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="select-planet">planet</label>
                <select id="select-planet">
                    <option value="manual" selected>-- choose orbit manually --</option>
                    <option value="Earth">Earth</option>
                    <option value="Mars">Mars</option>
                </select>
            </div>
            <div id="manual-controls" class="control-group">
                <input type="checkbox" id="checkbox-manual" checked>
                <label for="checkbox-manual">choose orbit manually</label>
                <div>
                    <label for="slider-tilt">axis angle relative to ecliptic</label>
                    <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
                </div>
                <div>
                    <label for="slider-equinox">spring equinox point</label>
                    <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
                </div>
                <div>
                    <label for="slider-eccentricity">orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
                </div>
            </div>
            <div id="display-controls" class="control-group">
                <input type="checkbox" id="checkbox-snapshots" checked>
                <label for="checkbox-snapshots">show sun snapshots</label>
                <input type="checkbox" id="checkbox-scales" checked>
                <label for="checkbox-scales">show scales</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="analemma-canvas"></canvas>
        </div>
    </div>

    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let currentTilt = 23.5;
        let currentEccentricity = 0.1;
        let currentEquinox = 0;
        let showSnapshots = true;
        let showScales = true;

        function setup() {
            let canvas = createCanvas(windowWidth - 340, windowHeight - 40);
            canvas.parent('visualization-area');
            noLoop();
            redrawCanvas();
        }

        function draw() {
            background(255);

            // Read controls
            currentTilt = parseFloat(document.getElementById('slider-tilt').value) * DEG_TO_RAD;
            currentEccentricity = parseFloat(document.getElementById('slider-eccentricity').value);
            currentEquinox = parseFloat(document.getElementById('slider-equinox').value) * DEG_TO_RAD;
            showSnapshots = document.getElementById('checkbox-snapshots').checked;
            showScales = document.getElementById('checkbox-scales').checked;

            // Calculate analemma points
            const points = calculateAnalemma();

            // Draw orbit ellipse
            drawOrbitEllipse();

            // Draw scales
            if (showScales) drawScales();

            // Draw analemma curve
            stroke('orange');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let p of points) {
                vertex(p.x, p.y);
            }
            endShape();

            // Draw sun snapshots
            if (showSnapshots) {
                fill('orange');
                noStroke();
                for (let i = 0; i < points.length; i += 20) {
                    circle(points[i].x, points[i].y, 8);
                }
            }
        }

        function calculateAnalemma() {
            const points = [];
            const days = 365;
            const C = 200;

            for (let d = 0; d < days; d++) {
                // Mean anomaly
                const M = (2 * PI * d) / 365.25;

                // Solve Kepler's equation
                let E = M;
                for (let i = 0; i < 10; i++) {
                    E = E - (E - currentEccentricity * sin(E) - M) / (1 - currentEccentricity * cos(E));
                }

                // True anomaly
                const v = 2 * atan2(sqrt(1 + currentEccentricity) * sin(E/2), sqrt(1 - currentEccentricity) * cos(E/2));

                // Ecliptic longitude
                const lambda = v + currentEquinox;

                // Declination (Y)
                const y = asin(sin(currentTilt) * sin(lambda));

                // Right ascension
                const alpha = atan2(cos(currentTilt) * sin(lambda), cos(lambda));

                // Equation of time (X)
                let EoT = M - alpha;
                // Handle angle wrapping
                if (EoT < -PI) EoT += TWO_PI;
                if (EoT > PI) EoT -= TWO_PI;

                // Convert to canvas coordinates
                const x = EoT * C;
                const yCoord = y * C;

                points.push({ x, y: yCoord });
            }
            return points;
        }

        function drawOrbitEllipse() {
            push();
            translate(width/2, 50);
            noFill();
            stroke(139, 69, 19);
            strokeWeight(1);
            const a = 100;
            const b = a * sqrt(1 - currentEccentricity * currentEccentricity);
            ellipse(0, 0, a*2, b*2);
            pop();
        }

        function drawScales() {
            push();
            translate(width/2, height/2);

            // Axes
            stroke(0);
            strokeWeight(1);
            line(-width/2, 0, width/2, 0);
            line(0, -height/2, 0, height/2);

            // Tick marks and labels
            const tickSpacing = 20;
            for (let x = -width/2; x <= width/2; x += tickSpacing) {
                line(x, -5, x, 5);
                if (x % 40 === 0) text(round(x/20), x, 20);
            }
            for (let y = -height/2; y <= height/2; y += tickSpacing) {
                line(-5, y, 5, y);
                if (y % 40 === 0) text(round(y/20), 20, y);
            }
            pop();
        }

        function redrawCanvas() {
            redraw();
        }

        // Event listeners
        function setupEventListeners() {
            const planetSelect = document.getElementById('select-planet');
            const manualCheckbox = document.getElementById('checkbox-manual');
            const tiltSlider = document.getElementById('slider-tilt');
            const equinoxSlider = document.getElementById('slider-equinox');
            const eccentricitySlider = document.getElementById('slider-eccentricity');

            planetSelect.addEventListener('change', () => {
                const val = planetSelect.value;
                if (val === 'manual') {
                    manualCheckbox.checked = true;
                    tiltSlider.disabled = false;
                    equinoxSlider.disabled = false;
                    eccentricitySlider.disabled = false;
                } else {
                    const data = planetData[val];
                    tiltSlider.value = data.tilt;
                    equinoxSlider.value = data.equinox;
                    eccentricitySlider.value = data.eccentricity;
                    manualCheckbox.checked = false;
                    tiltSlider.disabled = true;
                    equinoxSlider.disabled = true;
                    eccentricitySlider.disabled = true;
                }
                redrawCanvas();
            });

            manualCheckbox.addEventListener('change', () => {
                const checked = manualCheckbox.checked;
                tiltSlider.disabled = !checked;
                equinoxSlider.disabled = !checked;
                eccentricitySlider.disabled = !checked;
                if (checked) planetSelect.value = 'manual';
                redrawCanvas();
            });

            [tiltSlider, equinoxSlider, eccentricitySlider].forEach(slider => {
                slider.addEventListener('input', () => {
                    if (planetSelect.value !== 'manual') {
                        planetSelect.value = 'manual';
                    }
                    redrawCanvas();
                });
            });

            [document.getElementById('checkbox-snapshots'), 
             document.getElementById('checkbox-scales')].forEach(checkbox => {
                checkbox.addEventListener('change', () => {
                    redrawCanvas();
                });
            });
        }

        function windowResized() {
            resizeCanvas(windowWidth - 340, windowHeight - 40);
            redrawCanvas();
        }

        // Initialize
        setupEventListeners();
        redrawCanvas();
    </script>
</body>
</html>
