                { label: 'C', x: 60, y: 240 },
                { label: 'D', x: 150, y: 340 },
                { label: 'E', x: 250, y: 340 },
                { label: 'F', x: 340, y: 240 },
                { label: 'G', x: 320, y: 120 }
            ],
            adjacencyMatrix: [
                [-1, 11, 16, 44, 30, 44, 23],
                [11, -1, 12, 12, 10, 30, 25],
                [16, 12, -1, 43, 32, 36, 44],
                [44, 12, 43, -1, 41, 30, 35],
                [30, 10, 32, 41, -1, 31, 20],
                [44, 30, 36, 30, 31, -1, 18],
                [23, 25, 44, 35, 20, 18, -1]
            ]
        };

        const fixedOptimal = {
            path: [
                ['A', 'C'], ['C', 'B'], ['B', 'D'], ['D', 'F'], ['F', 'G'], ['G', 'E'], ['E', 'A']
            ],
            weight: 16 + 12 + 12 + 30 + 18 + 20 + 30
        };

        let currentGraph = JSON.parse(JSON.stringify(fixedGraph));
        let optimalPath = fixedOptimal.path;
        let optimalWeight = fixedOptimal.weight;
        let currentAlgorithm = 'nearest-neighbor';
        let currentPath = [];
        let totalWeight = 0;
        let startingVertex = null;
        let isAlgorithmComplete = false;
        let algoSketch, optimalSketch;
        let nnVisited = [];
        let nnCurrentVertex = null;
        let clEdges = [];
        let clIndex = 0;
        let clAddedEdges = [];
        let clDegrees = {};
        let clUnionFind = null;

        function* permutations(array) {
            if (array.length === 0) yield [];
            for (let i = 0; i < array.length; i++) {
                let rest = permutations(array.slice(0, i).concat(array.slice(i + 1)));
                for (let x of rest) {
                    yield [array[i], ...x];
                }
            }
        }

        class UnionFind {
            constructor(elements) {
                this.parent = {};
                elements.forEach(el => this.parent[el] = el);
            }
            find(x) {
                if (this.parent[x] !== x) {
                    this.parent[x] = this.find(this.parent[x]);
                }
                return this.parent[x];
            }
            union(x, y) {
                const rootX = this.find(x);
                const rootY = this.find(y);
                if (rootX !== rootY) {
                    this.parent[rootY] = rootX;
                }
            }
        }

        function setupAlgoSketch() {
            const wrapper = document.getElementById('canvas-algo-wrapper');
            const sketch = (p) => {
                p.setup = () => {
                    const canvas = p.createCanvas(wrapper.clientWidth, wrapper.clientHeight);
                    canvas.parent(wrapper);
                };
                p.draw = () => {
                    p.background(255);
                    drawGraph(p, currentGraph, currentPath, 'orange');
                };
            };
            algoSketch = new p5(sketch, 'canvas-algo-wrapper');
        }

        function setupOptimalSketch() {
            const wrapper = document.getElementById('canvas-optimal-wrapper');
            const sketch = (p) => {
                p.setup = () => {
                    const canvas = p.createCanvas(wrapper.clientWidth, wrapper.clientHeight);
                    canvas.parent(wrapper);
                };
                p.draw = () => {
                    p.background(255);
                    drawGraph(p, currentGraph, optimalPath, 'blue');
                };
            };
            optimalSketch = new p5(sketch, 'canvas-optimal-wrapper');
        }

        function drawGraph(p, graph, highlightedEdges, highlightColor) {
            p.stroke(150);
            p.strokeWeight(1);
            p.noFill();
            for (let i = 0; i < graph.vertices.length; i++) {
                for (let j = i + 1; j < graph.vertices.length; j++) {
                    const v1 = graph.vertices[i];
                    const v2 = graph.vertices[j];
                    if (v1.label < v2.label) {
                        const weight = graph.adjacencyMatrix[i][j];
                        p.line(v1.x, v1.y, v2.x, v2.y);
                        const midX = (v1.x + v2.x) / 2;
                        const midY = (v1.y + v2.y) / 2;
                        p.fill(255);
                        p.noStroke();
                        p.textSize(10);
                        p.text(weight, midX, midY);
                        p.fill(0);
                        p.text(weight, midX, midY);
                    }
                }
            }

            if (highlightedEdges && highlightColor) {
                p.stroke(highlightColor);
                p.strokeWeight(3);
                p.noFill();
                highlightedEdges.forEach(edge => {
                    const v1 = graph.vertices.find(v => v.label === edge[0]);
                    const v2 = graph.vertices.find(v => v.label === edge[1]);
                    if (v1 && v2) {
                        p.line(v1.x, v1.y, v2.x, v2.y);
                    }
                });
            }

            p.fill(255);
            p.stroke(0);
            p.strokeWeight(1);
            graph.vertices.forEach(v => {
                p.ellipse(v.x, v.y, 20, 20);
                p.fill(0);
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);
                p.text(v.label, v.x, v.y);
                p.fill(255);
            });
        }

        document.addEventListener('DOMContentLoaded', () => {
            setupAlgoSketch();
            setupOptimalSketch();
            resetAlgorithm();

            const selectedVertexButtons = Array.from(document.querySelectorAll('.vertex-btn'));

            document.getElementById('algo-cheapest-link').addEventListener('change', () => {
                currentAlgorithm = 'cheapest-link';
                document.getElementById('starting-vertex-selector').style.display = 'none';
                resetAlgorithm();
            });

            document.getElementById('algo-nearest-neighbor').addEventListener('change', () => {
                currentAlgorithm = 'nearest-neighbor';
                document.getElementById('starting-vertex-selector').style.display = 'block';
                resetAlgorithm();
            });

            document.getElementById('btn-start-over').addEventListener('click', resetAlgorithm);

            document.getElementById('btn-fixed-example').addEventListener('click', () => {
                currentGraph = JSON.parse(JSON.stringify(fixedGraph));
                optimalPath = fixedOptimal.path;
                optimalWeight = fixedOptimal.weight;
                resetAlgorithm();
            });

            document.getElementById('btn-random-example').addEventListener('click', () => {
                generateRandomGraph();
                computeOptimalPath();
                resetAlgorithm();
            });

            selectedVertexButtons.forEach(btn => {
                btn.addEventListener('click', () => {
                    if (currentAlgorithm === 'nearest-neighbor' && !isAlgorithmComplete) {
                        startingVertex = btn.dataset.vertex;
                        selectedVertexButtons.forEach(b => b.disabled = true);
                        document.getElementById('btn-next-step').disabled = false;
                        document.getElementById('title-algo').textContent = `The nearest neighbor algorithm starting at vertex ${startingVertex}`;
                        initializeNN();
                    }
                });
            });

            document.getElementById('btn-next-step').addEventListener('click', nextStep);

            document.getElementById('checkbox-optimal').addEventListener('change', (e) => {
                const optimalContainer = document.getElementById('optimal-result-container');
                if (e.target.checked) {
                    optimalContainer.style.display = 'block';
                    if (optimalSketch) {
                        optimalSketch.redraw();
                    }
                } else {
                    optimalContainer.style.display = 'none';
                }
            });
        });

        function resetAlgorithm() {
            currentPath = [];
            totalWeight = 0;
            isAlgorithmComplete = false;
            document.getElementById('btn-next-step').disabled = true;
            const tbody = document.getElementById('table-edge-list').getElementsByTagName('tbody')[0];
            tbody.innerHTML = '';
            document.getElementById('text-total-weight').textContent = '';
            document.getElementById('title-algo').textContent = 'Select a starting vertex';

            if (currentAlgorithm === 'nearest-neighbor') {
                document.getElementById('starting-vertex-selector').style.display = 'block';
                const selectedVertexButtons = Array.from(document.querySelectorAll('.vertex-btn'));
                selectedVertexButtons.forEach(btn => {
                    btn.disabled = false;
                });
                startingVertex = null;
                nnVisited = [];
                nnCurrentVertex = null;
            } else if (currentAlgorithm === 'cheapest-link') {
                document.getElementById('starting-vertex-selector').style.display = 'none';
                clEdges = [];
                const vertices = currentGraph.vertices;
                for (let i = 0; i < vertices.length; i++) {
                    for (let j = i + 1; j < vertices.length; j++) {
                        const weight = currentGraph.adjacencyMatrix[i][j];
                        clEdges.push({ v1: vertices[i].label, v2: vertices[j].label, weight });
                    }
                }
                clEdges.sort((a, b) => a.weight - b.weight);
                clIndex = 0;
                clAddedEdges = [];
                clDegrees = {};
                vertices.forEach(v => clDegrees[v.label] = 0);
                clUnionFind = new UnionFind(vertices.map(v => v.label));
                document.getElementById('btn-next-step').disabled = false;
            }
            algoSketch.redraw();
        }

        function initializeNN() {
            nnVisited = [startingVertex];
            nnCurrentVertex = startingVertex;
            currentPath = [];
            totalWeight = 0;
            const tbody = document.getElementById('table-edge-list').getElementsByTagName('tbody')[0];
            tbody.innerHTML = '';
            document.getElementById('text-total-weight').textContent = '';
        }

        function nextStep() {
            if (currentAlgorithm === 'nearest-neighbor') {
                if (nnVisited.length === currentGraph.vertices.length) {
                    const start = nnVisited[0];
                    const last = nnVisited[nnVisited.length - 1];
                    const edge = [last, start];
                    const weight = getEdgeWeight(last, start);
                    currentPath.push(edge);
                    totalWeight += weight;
                    const row = document.createElement('tr');
                    row.innerHTML = `<td>${edge[0]}-${edge[1]}</td><td>${weight}</td>`;
                    document.getElementById('table-edge-list').getElementsByTagName('tbody')[0].appendChild(row);
                    document.getElementById('text-total-weight').textContent = `Total weight: ${totalWeight}`;
                    isAlgorithmComplete = true;
                    document.getElementById('btn-next-step').disabled = true;
                    algoSketch.redraw();
                    return;
                }

                const currentVertex = nnCurrentVertex;
                const currentVertexIndex = currentGraph.vertices.findIndex(v => v.label === currentVertex);
                let minWeight = Infinity;
                let nearestNeighbor = null;
                currentGraph.vertices.forEach(v => {
                    if (!nnVisited.includes(v.label)) {
                        const weight = currentGraph.adjacencyMatrix[currentVertexIndex][currentGraph.vertices.findIndex(v2 => v2.label === v.label)];
                        if (weight < minWeight) {
                            minWeight = weight;
                            nearestNeighbor = v.label;
                        }
                    }
                });

                if (nearestNeighbor) {
                    const edge = [currentVertex, nearestNeighbor];
                    currentPath.push(edge);
                    totalWeight += minWeight;
                    nnVisited.push(nearestNeighbor);
                    nnCurrentVertex = nearestNeighbor;

                    const row = document.createElement('tr');
                    row.innerHTML = `<td>${edge[0]}-${edge[1]}</td><td>${minWeight}</td>`;
                    document.getElementById('table-edge-list').getElementsByTagName('tbody')[0].appendChild(row);
                    document.getElementById('text-total-weight').textContent = `Total weight: ${totalWeight}`;

                    algoSketch.redraw();
                }

                if (nnVisited.length === currentGraph.vertices.length) {
                    document.getElementById('btn-next-step').disabled = false;
                }
            } else if (currentAlgorithm === 'cheapest-link') {
                if (clIndex >= clEdges.length || clAddedEdges.length >= currentGraph.vertices.length) {
                    document.getElementById('btn-next-step').disabled = true;
                    return;
                }

                while (clIndex < clEdges.length) {
                    const edge = clEdges[clIndex];
                    clIndex++;
                    const { v1, v2, weight } = edge;
                    const root1 = clUnionFind.find(v1);
                    const root2 = clUnionFind.find(v2);
                    if (root1 !== root2 && clDegrees[v1] < 2 && clDegrees[v2] < 2) {
                        clAddedEdges.push([v1, v2]);
                        clDegrees[v1]++;
                        clDegrees[v2]++;
                        clUnionFind.union(v1, v2);
                        currentPath.push([v1, v2]);
                        totalWeight += weight;

                        const row = document.createElement('tr');
                        row.innerHTML = `<td>${v1}-${v2}</td><td>${weight}</td>`;
                        document.getElementById('table-edge-list').getElementsByTagName('tbody')[0].appendChild(row);
                        document.getElementById('text-total-weight').textContent = `Total weight: ${totalWeight}`;

                        algoSketch.redraw();

                        const allDegrees2 = Object.values(clDegrees).every(d => d === 2);
                        if (allDegrees2) {
                            document.getElementById('btn-next-step').disabled = true;
                        }
                        break;
                    }
                }
            }
        }

        function getEdgeWeight(v1, v2) {
            const i = currentGraph.vertices.findIndex(v => v.label === v1);
            const j = currentGraph.vertices.findIndex(v => v.label === v2);
            return currentGraph.adjacencyMatrix[i][j];
        }

        function generateRandomGraph() {
            const vertices = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];
            const positions = generateHeptagonPositions(200, 200, 150);
            currentGraph.vertices = vertices.map((label, i) => ({
                label,
                x: positions[i].x,
                y: positions[i].y
            }));
            currentGraph.adjacencyMatrix = [];
            for (let i = 0; i < 7; i++) {
                currentGraph.adjacencyMatrix[i] = [];
                for (let j = 0; j < 7; j++) {
                    if (i === j) {
                        currentGraph.adjacencyMatrix[i][j] = -1;
                    } else {
                        currentGraph.adjacencyMatrix[i][j] = Math.floor(Math.random() * 41) + 10;
                    }
                }
            }
            for (let i = 0; i < 7; i++) {
                for (let j = i + 1; j < 7; j++) {
                    currentGraph.adjacencyMatrix[j][i] = currentGraph.adjacencyMatrix[i][j];
                }
            }
        }

        function generateHeptagonPositions(centerX, centerY, radius) {
            const positions = [];
            const n = 7;
            for (let i = 0; i < n; i++) {
                const angle = (i * 2 * Math.PI) / n - Math.PI / 2;
                positions.push({
                    x: centerX + radius * Math.cos(angle),
                    y: centerY + radius * Math.sin(angle)
                });
            }
            return positions;
        }

        function computeOptimalPath() {
            const vertices = currentGraph.vertices.map(v => v.label);
            let minWeight = Infinity;
            let bestPath = [];

            const perms = permutations(vertices);
            for (const perm of perms) {
                let weight = 0;
                for (let i = 0; i < perm.length - 1; i++) {
                    weight += getEdgeWeight(perm[i], perm[i + 1]);
                }
                weight += getEdgeWeight(perm[perm.length - 1], perm[0]);
                if (weight < minWeight) {
                    minWeight = weight;
                    bestPath = perm;
                }
            }

            optimalPath = [];
            for (let i = 0; i < bestPath.length - 1; i++) {
                optimalPath.push([bestPath[i], bestPath[i + 1]]);
            }
            optimalPath.push([bestPath[bestPath.length - 1], bestPath[0]]);
            optimalWeight = minWeight;
            document.getElementById('text-optimal-weight').textContent = `Total weight: ${optimalWeight}`;
        }
    </script>
</body>
</html>
