</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-au">A.u</label>
                <input type="range" id="slider-au" min="-2" max="2" step="0.01" value="0.8">
                <span id="value-au">0.80</span>
            </div>
            <div class="slider-group">
                <label for="slider-bu">B.u</label>
                <input type="range" id="slider-bu" min="-2" max="2" step="0.01" value="-0.5">
                <span id="value-bu">-0.50</span>
            </div>
            <div class="slider-group">
                <label for="slider-av">A.v</label>
                <input type="range" id="slider-av" min="-2" max="2" step="0.01" value="1.2">
                <span id="value-av">1.20</span>
            </div>
            <div class="slider-group">
                <label for="slider-bv">B.v</label>
                <input type="range" id="slider-bv" min="-2" max="2" step="0.01" value="1.0">
                <span id="value-bv">1.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-aw">A.w</label>
                <input type="range" id="slider-aw" min="-2" max="2" step="0.01" value="1.1">
                <span id="value-aw">1.10</span>
            </div>
            <div class="slider-group">
                <label for="slider-bw">B.w</label>
                <input type="range" id="slider-bw" min="-2" max="2" step="0.01" value="-0.2">
                <span id="value-bw">-0.20</span>
            </div>
            <div class="slider-group">
                <label for="slider-x-scale">x scale</label>
                <input type="range" id="slider-x-scale" min="20" max="200" step="1" value="80">
                <span id="value-x-scale">80</span>
            </div>
            <div class="slider-group">
                <label for="slider-y-scale">y scale</label>
                <input type="range" id="slider-y-scale" min="20" max="200" step="1" value="80">
                <span id="value-y-scale">80</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>
    <script>
        let u = [0, 0], v = [0, 0], w = [0, 0];
        let A = [0, 0], B = [0, 0];
        let xScale = 80, yScale = 80;

        function updateValueDisplay(sliderId, spanId) {
            const slider = document.getElementById(sliderId);
            const span = document.getElementById(spanId);
            slider.addEventListener('input', () => {
                span.textContent = parseFloat(slider.value).toFixed(2);
            });
            span.textContent = slider.value;
        }

        function setupSliders() {
            const dotProductSliders = ['au', 'bu', 'av', 'bv', 'aw', 'bw'];
            dotProductSliders.forEach(id => {
                updateValueDisplay('slider-' + id, 'value-' + id);
                document.getElementById('slider-' + id).addEventListener('input', recalculate);
            });

            const scaleSliders = ['x-scale', 'y-scale'];
            scaleSliders.forEach(id => {
                updateValueDisplay('slider-' + id, 'value-' + id);
                document.getElementById('slider-' + id).addEventListener('input', () => {
                    xScale = parseFloat(document.getElementById('slider-x-scale').value);
                    yScale = parseFloat(document.getElementById('slider-y-scale').value);
                    redraw();
                });
            });
        }

        function initialize() {
            setupSliders();
            recalculate();
        }

        function recalculate() {
            try {
                const au = parseFloat(document.getElementById('slider-au').value);
                const bu = parseFloat(document.getElementById('slider-bu').value);
                const av = parseFloat(document.getElementById('slider-av').value);
                const bv = parseFloat(document.getElementById('slider-bv').value);
                const aw = parseFloat(document.getElementById('slider-aw').value);
                const bw = parseFloat(document.getElementById('slider-bw').value);

                const M = [[au, bu], [av, bv], [aw, bw]];
                const svd = math.svd(M);
                const U = svd.U;

                const S_hat = [];
                for (let i = 0; i < 3; i++) {
                    S_hat.push([U[i][0], U[i][1]]);
                }

                const K = [];
                for (let i = 0; i < 3; i++) {
                    const a = S_hat[i][0], b = S_hat[i][1];
                    K.push([a*a, 2*a*b, b*b]);
                }

                const l_vec = math.lusolve(K, [1, 1, 1]);
                const L = [[l_vec[0], l_vec[1]], [l_vec[1], l_vec[2]]];

                const detL = L[0][0]*L[1][1] - L[0][1]*L[1][0];
                const traceL = L[0][0] + L[1][1];
                if (traceL <= 0 || detL <= 0) return;

                const Q = math.chol(math.matrix(L));
                const S_matrix = math.multiply(math.matrix(S_hat), Q);
                const D_matrix = math.diag(svd.S);
                const Vt = math.transpose(svd.V);
                const P = math.multiply(math.multiply(math.inv(Q), D_matrix), Vt);

                const S_array = S_matrix.toArray();
                u = S_array[0]; v = S_array[1]; w = S_array[2];
                A = [P.get([0,0]), P.get([1,0])];
                B = [P.get([0,1]), P.get([1,1])];

                xScale = parseFloat(document.getElementById('slider-x-scale').value);
                yScale = parseFloat(document.getElementById('slider-y-scale').value);
                redraw();
            } catch (error) {
                console.error('Calculation error:', error);
            }
        }

        function drawProjections(point, dotProducts, axes) {
            stroke(128);
            fill(0, 255, 0);
            for (let i = 0; i < 3; i++) {
                const proj = [dotProducts[i] * axes[i][0], dotProducts[i] * axes[i][1]];
                line(point[0], point[1], proj[0], proj[1]);
                ellipse(proj[0], proj[1], 6, 6);
            }
        }

        function draw() {
            background(240);
            translate(width/2, height/2);
            scale(xScale, -yScale);
            strokeWeight(3);

            const axisLength = 1000;
            const axes = [u, v, w];
            const colors = [[0, 0, 205], [100, 149, 237], [0, 0, 205]];
            const labels = ['u', 'v', 'w'];

            axes.forEach((axis, i) => {
                const [r, g, b] = colors[i];
                stroke(r, g, b);
                const [x, y] = axis;
                if (x !== 0 || y !== 0) {
                    const len = Math.sqrt(x*x + y*y);
                    const dirX = x/len, dirY = y/len;
                    line(-dirX*axisLength, -dirY*axisLength, dirX*axisLength, dirY*axisLength);
                    fill(r, g, b);
                    noStroke();
                    text(labels[i], dirX*(axisLength+10), dirY*(axisLength+10));
                }
            });

            stroke(0);
            strokeWeight(1);
            fill(255, 0, 0);
            ellipse(0, 0, 8, 8);
            text('O', 10, 10);

            const au = parseFloat(document.getElementById('slider-au').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);

            fill(255, 0, 0);
            ellipse(A[0], A[1], 10, 10);
            text('A', A[0]+10, A[1]+10);
            drawProjections(A, [au, av, aw], axes);

            fill(255, 165, 0);
            ellipse(B[0], B[1], 10, 10);
            text('B', B[0]+10, B[1]+10);
            drawProjections(B, [bu, bv, bw], axes);
        }

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = createCanvas(800, 600);
            canvas.parent(canvasContainer);
            noLoop();
            initialize();
        }

        function redraw() {
            draw();
        }
    </script>
</body>
</html>
