    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Global state
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0, t2 = 0;
        let scene, camera, renderer, controls;
        let timeHandle = document.getElementById('time-handle');
        let timePad = document.getElementById('time-pad');
        let padSize = 150;
        let isDragging = false;

        // Initialize three.js
        function initThree() {
            scene = new THREE.Scene();
            const ambient = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambient);
            const directional = new THREE.DirectionalLight(0xffffff, 0.8);
            directional.position.set(5, 5, 5).normalize();
            scene.add(directional);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 250, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            setup2DCamera();
            animate();
        }

        function setup2DCamera() {
            camera = new THREE.OrthographicCamera(-5, 5, 5, -5, 0.1, 100);
            camera.position.set(0, 0, 5);
            if (controls) controls.dispose();
            controls = null;
        }

        function setup3DCamera() {
            camera = new THREE.PerspectiveCamera(45, (window.innerWidth - 250)/window.innerHeight, 0.1, 100);
            camera.position.set(3, 3, 3);
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
        }

        function updateVisualization() {
            scene.children = scene.children.filter(child => child.type === 'AmbientLight' || child.type === 'DirectionalLight');
            const T1 = t1 * 1.5;
            const T2 = t2 * 1.5;

            if (currentDimension === '2D') {
                const geometry = new THREE.BufferGeometry();
                const points = [];
                const step = 0.01;
                let U_min, U_max;

                switch(currentType) {
                    case '2A1':
                        U_min = -2; U_max = 2;
                        for (let U = U_min; U <= U_max; U += step) {
                            const x = U + T1;
                            const y = U*U + T2;
                            points.push(new THREE.Vector3(x, y, 0));
                        }
                        break;
                    case '2A2':
                        U_min = -1.5; U_max = 1.5;
                        for (let U = U_min; U <= U_max; U += step) {
                            const x = 2*U*U*U + T1;
                            const y = 3*U*U + T2;
                            points.push(new THREE.Vector3(x, y, 0));
                        }
                        break;
                    case '2B2':
                        U_min = -2; U_max = 2;
                        for (let U = U_min; U <= U_max; U += step) {
                            const x = U*U + T1;
                            const y = U*U*U*U + T2;
                            points.push(new THREE.Vector3(x, y, 0));
                        }
                        break;
                }

                geometry.setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
                const line = new THREE.Line(geometry, material);
                scene.add(line);
            } else {
                let parametricFunc;
                const wireframe = currentType === '3A3';

                switch(currentType) {
                    case '3A1p': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, 0.3*(U*U + V*V) + T1*U + T2*V);
                    }; break;
                    case '3A1m': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, -0.3*(U*U + V*V) + T1*U + T2*V);
                    }; break;
                    case '3A2': parametricFunc = (u, v) => {
                        const U = u*3 - 1.5, V = v*4 - 2;
                        return new THREE.Vector3(2*U*U*U + T1, 3*U*U + T2, V);
                    }; break;
                    case '3A3': parametricFunc = (u, v) => {
                        const U = u*3 - 1.5, V = v*8 - 4;
                        return new THREE.Vector3(3*U*U*U*U + U*U*V, -4*U*U*U - 2*U*V + T1*2, V + T2*2);
                    }; break;
                    case '3B2p': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, (U*U + T1)**2 + V*V + T2);
                    }; break;
                    case '3B2m': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, -(U*U*U*U + T1*2*U*U + V*V - T2));
                    }; break;
                    case '3B3': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, U*U*U + T1*U + V*V + T2);
                    }; break;
                    case '3C3p': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, U*U*U - 3*U*V*V + T1*2*(U*U + V*V) + T2*4*U);
                    }; break;
                    case '3C3m': parametricFunc = (u, v) => {
                        const U = u*4 - 2, V = v*4 - 2;
                        return new THREE.Vector3(U, V, U*U*U + V*V*V + T1*4*U + T2*4*V);
                    }; break;
                }

                const geometry = new THREE.ParametricGeometry(parametricFunc, 50, 50);
                const material = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide
                });
                const mesh = new THREE.Mesh(geometry, material);
                scene.add(mesh);

                if (wireframe) {
                    const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 1 });
                    const wireframeGeometry = new THREE.WireframeGeometry(geometry);
                    const wireframeMesh = new THREE.LineSegments(wireframeGeometry);
                    scene.add(wireframeMesh);
                }
            }
        }

        function animate() {
            requestAnimationFrame(animate);
            if (controls) controls.update();
            renderer.render(scene, camera);
        }

        // Event Listeners
        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                button.classList.add('active');
                currentType = button.id.replace('btn-', '');
                currentDimension = currentType.startsWith('2') ? '2D' : '3D';
                if (currentDimension === '2D' && !camera.isOrthographicCamera) {
                    setup2DCamera();
                } else if (currentDimension === '3D' && !camera.isPerspectiveCamera) {
                    setup3DCamera();
                }
                updateVisualization();
            });
        });

        timePad.addEventListener('mousedown', (e) => {
            isDragging = true;
            updateHandlePosition(e);
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) updateHandlePosition(e);
        });

        document.addEventListener('mouseup', () => {
            isDragging = false;
        });

        timePad.addEventListener('click', (e) => {
            if (!isDragging) updateHandlePosition(e);
        });

        function updateHandlePosition(e) {
            const rect = timePad.getBoundingClientRect();
            let x = e.clientX - rect.left;
            let y = e.clientY - rect.top;

            x = Math.max(0, Math.min(padSize, x));
            y = Math.max(0, Math.min(padSize, y));

            timeHandle.style.left = `${x}px`;
            timeHandle.style.top = `${y}px`;

            t1 = (x / padSize) * 2 - 1;
            t2 = 1 - (y / padSize) * 2;

            updateVisualization();
        }

        // Initialize
        initThree();
        updateVisualization();
        document.getElementById('btn-2A2').click();

        // Handle window resize
        window.addEventListener('resize', () => {
            if (camera.isPerspectiveCamera) {
                camera.aspect = (window.innerWidth - 250) / window.innerHeight;
                camera.updateProjectionMatrix();
            }
            renderer.setSize(window.innerWidth - 250, window.innerHeight);
        });
    </script>
</body>
</html>
