    <div id="plot-area">
        <div id="legend"></div>
        <script>
            let feedConfig = "feed A to B";
            let plotType = "semibatch";
            let feedRate = 10;
            let UA = 15000;
            let dH1 = 1000;
            let dH2 = 3000;
            let simulationData = [];
            let selectivityRatio = [];

            function setup() {
                createCanvas(800, 600);
                noLoop();
                redrawPlot();
            }

            function draw() {
                background('#F0F0F0');
                drawAxes();
                drawGrid();
                drawCurves();
                drawLegend();
            }

            function drawAxes() {
                stroke(0);
                line(50, height - 50, width - 50, height - 50);
                line(50, height - 50, 50, 50);

                // X-axis labels
                for (let t = 0; t <= 2.0; t += 0.5) {
                    let x = map(t, 0, 2.0, 50, width - 50);
                    line(x, height - 50, x, height - 45);
                    textAlign(CENTER, TOP);
                    text(t.toFixed(1), x, height - 45);
                }

                // Y-axis labels
                let yMin, yMax;
                if (plotType === 'selectivity') {
                    if (selectivityRatio.length === 0) return;
                    let values = selectivityRatio.map(p => p[1]);
                    let minY = Math.min(...values);
                    let maxY = Math.max(...values);
                    let pad = (maxY - minY) * 0.1;
                    yMin = Math.max(0, minY - pad);
                    yMax = maxY + pad;
                } else {
                    yMin = 0;
                    yMax = 100;
                }

                for (let yVal = yMin; yVal <= yMax; yVal += (yMax - yMin)/5) {
                    let y = map(yVal, yMin, yMax, height - 50, 50);
                    line(50, y, 55, y);
                    textAlign(RIGHT, CENTER);
                    text(yVal.toFixed(0), 45, y);
                }
            }

            function drawGrid() {
                stroke(200);
                for (let t = 0; t <= 2.0; t += 0.5) {
                    let x = map(t, 0, 2.0, 50, width - 50);
                    line(x, height - 50, x, 50);
                }
                let yMin, yMax;
                if (plotType === 'selectivity') {
                    if (selectivityRatio.length === 0) return;
                    let values = selectivityRatio.map(p => p[1]);
                    let minY = Math.min(...values);
                    let maxY = Math.max(...values);
                    let pad = (maxY - minY) * 0.1;
                    yMin = Math.max(0, minY - pad);
                    yMax = maxY + pad;
                } else {
                    yMin = 0;
                    yMax = 100;
                }
                for (let yVal = yMin; yVal <= yMax; yVal += (yMax - yMin)/5) {
                    let y = map(yVal, yMin, yMax, height - 50, 50);
                    line(50, y, width - 50, y);
                }
            }

            function drawCurves() {
                if (plotType === 'selectivity') {
                    drawSelectivityCurve();
                } else {
                    drawMoleCurves();
                }
            }

            function drawMoleCurves() {
                const colors = {
                    Na: '#2ca02c',
                    Nb: '#000000',
                    Nd: '#1f77b4',
                    Nu: '#d62728'
                };

                const dataMap = {
                    Na: simulationData.map(p => [p[0], p[1]]),
                    Nb: simulationData.map(p => [p[0], p[2]]),
                    Nd: simulationData.map(p => [p[0], p[3]]),
                    Nu: simulationData.map(p => [p[0], p[4]])
                };

                for (const [key, data] of Object.entries(dataMap)) {
                    stroke(colors[key]);
                    if (key === 'Na' || key === 'Nb') {
                        drawDashedLine(data);
                    } else {
                        beginShape();
                        data.forEach(p => {
                            let x = map(p[0], 0, 2.0, 50, width - 50);
                            let y = map(p[1], 0, 100, height - 50, 50);
                            vertex(x, y);
                        });
                        endShape();
                    }
                }
            }

            function drawSelectivityCurve() {
                if (selectivityRatio.length === 0) return;
                stroke('#800080');
                beginShape();
                selectivityRatio.forEach(p => {
                    let x = map(p[0], 0, 2.0, 50, width - 50);
                    let yMin = Math.min(...selectivityRatio.map(p => p[1]));
                    let yMax = Math.max(...selectivityRatio.map(p => p[1]));
                    let pad = (yMax - yMin) * 0.1;
                    yMin = Math.max(0, yMin - pad);
                    yMax = yMax + pad;
                    let y = map(p[1], yMin, yMax, height - 50, 50);
                    vertex(x, y);
                });
                endShape();
            }

            function drawLegend() {
                const legendDiv = document.getElementById('legend');
                legendDiv.innerHTML = '';

                if (plotType === 'selectivity') {
                    const item = document.createElement('div');
                    item.className = 'legend-item';
                    const colorDiv = document.createElement('div');
                    colorDiv.className = 'legend-color';
                    colorDiv.style.backgroundColor = '#800080';
                    item.appendChild(colorDiv);
                    const label = document.createElement('span');
                    label.innerHTML = 'semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)';
                    item.appendChild(label);
                    legendDiv.appendChild(item);
                    MathJax.typesetPromise([label]);
                } else {
                    const items = [
                        {color: '#2ca02c', label: '\\(N_a\\)'},
                        {color: '#000000', label: '\\(N_b\\)'},
                        {color: '#1f77b4', label: '\\(N_d\\)'},
                        {color: '#d62728', label: '\\(N_u\\)'}
                    ];

                    items.forEach(item => {
                        const div = document.createElement('div');
                        div.className = 'legend-item';
                        const colorDiv = document.createElement('div');
                        colorDiv.className = 'legend-color';
                        colorDiv.style.backgroundColor = item.color;
                        div.appendChild(colorDiv);
                        const label = document.createElement('span');
                        label.innerHTML = item.label;
                        div.appendChild(label);
                        legendDiv.appendChild(div);
                        MathJax.typesetPromise([label]);
                    });
                }
            }

            function drawDashedLine(data) {
                beginShape();
                let dashLength = 5;
                let gapLength = 5;
                let totalLength = 0;
                let prevX, prevY;

                data.forEach((p, i) => {
                    let x = map(p[0], 0, 2.0, 50, width - 50);
                    let y = map(p[1], 0, 100, height - 50, 50);

                    if (i === 0) {
                        prevX = x;
                        prevY = y;
                        return;
                    }

                    let dx = x - prevX;
                    let dy = y - prevY;
                    let segmentLength = Math.sqrt(dx*dx + dy*dy);
                    let numDashes = Math.floor(segmentLength / (dashLength + gapLength));

                    for (let j = 0; j < numDashes; j++) {
                        let start = j * (dashLength + gapLength);
                        let end = start + dashLength;
                        if (end > segmentLength) end = segmentLength;

                        let x1 = prevX + (dx * start / segmentLength);
                        let y1 = prevY + (dy * start / segmentLength);
                        let x2 = prevX + (dx * end / segmentLength);
                        let y2 = prevY + (dy * end / segmentLength);

                        line(x1, y1, x2, y2);
                    }

                    prevX = x;
                    prevY = y;
                });
                endShape();
            }

            function rk4(derivs, y0, t) {
                let n = t.length;
                let y = new Array(n);
                y[0] = y0.slice();

                for (let i = 0; i < n - 1; i++) {
                    let dt = t[i+1] - t[i];
                    let k1 = derivs(t[i], y[i]);
                    let k2 = derivs(t[i] + dt/2, y[i].map((val, j) => val + dt*k1[j]/2));
                    let k3 = derivs(t[i] + dt/2, y[i].map((val, j) => val + dt*k2[j]/2));
                    let k4 = derivs(t[i] + dt, y[i].map((val, j) => val + dt*k3[j]));

                    y[i+1] = y[i].map((val, j) => 
                        val + dt*(k1[j] + 2*k2[j] + 2*k3[j] + k4[j])/6
                    );
                }
                return y;
            }

            function simulate() {
                const V = 100;
                const T_initial = 330;
                const R = 8.314;
                const Cp_total = 150000;
                const k1_0 = 3e4;
                const E1 = 30000;
                const k2_0 = 1e5;
                const E2 = 40000;

                let t_f = 100 / feedRate;

                let derivs;
                let y0;

                if (plotType === 'batch') {
                    y0 = [100, 100, 0, 0, T_initial];
                    derivs = (t, y) => {
                        let [Na, Nb, Nd, Nu, T] = y;
                        let k1 = k1_0 * Math.exp(-E1/(R*T));
                        let k2 = k2_0 * Math.exp(-E2/(R*T));
                        let r1 = k1 * (Na/V) * Math.pow(Nb/V, 2);
                        let r2 = k2 * (Na/V) * (Nb/V);
                        let dNa = -(r1 + r2) * V;
                        let dNb = -(r1 + r2) * V;
                        let dNd = r1 * V;
                        let dNu = r2 * V;
                        let dT = (UA * (T_initial - T) + (-dH1 * r1 - dH2 * r2) * V) / Cp_total;
                        return [dNa, dNb, dNd, dNu, dT];
                    };
                } else if (plotType === 'semibatch') {
                    if (feedConfig === 'feed A to B') {
                        y0 = [0, 100, 0, 0, T_initial];
                        derivs = (t, y) => {
                            let [Na, Nb, Nd, Nu, T] = y;
                            let F_A0 = t < t_f ? feedRate : 0;
                            let k1 = k1_0 * Math.exp(-E1/(R*T));
                            let k2 = k2_0 * Math.exp(-E2/(R*T));
                            let r1 = k1 * (Na/V) * Math.pow(Nb/V, 2);
                            let r2 = k2 * (Na/V) * (Nb/V);
                            let dNa = F_A0 - (r1 + r2) * V;
                            let dNb = -(r1 + r2) * V;
                            let dNd = r1 * V;
                            let dNu = r2 * V;
                            let dT = (UA * (T_initial - T) + (-dH1 * r1 - dH2 * r2) * V) / Cp_total;
                            return [dNa, dNb, dNd, dNu, dT];
                        };
                    } else {
                        y0 = [100, 0, 0, 0, T_initial];
                        derivs = (t, y) => {
                            let [Na, Nb, Nd, Nu, T] = y;
                            let F_B0 = t < t_f ? feedRate : 0;
                            let k1 = k1_0 * Math.exp(-E1/(R*T));
                            let k2 = k2_0 * Math.exp(-E2/(R*T));
                            let r1 = k1 * (Na/V) * Math.pow(Nb/V, 2);
                            let r2 = k2 * (Na/V) * (Nb/V);
                            let dNa = -(r1 + r2) * V;
                            let dNb = F_B0 - (r1 + r2) * V;
                            let dNd = r1 * V;
                            let dNu = r2 * V;
                            let dT = (UA * (T_initial - T) + (-dH1 * r1 - dH2 * r2) * V) / Cp_total;
                            return [dNa, dNb, dNd, dNu, dT];
                        };
                    }
                }

                let t = [];
                for (let i = 0; i <= 200; i++) {
                    t.push(i * 0.01);
                }

                if (plotType === 'selectivity') {
                    // Batch simulation
                    let yBatch = rk4(derivs, [100, 100, 0, 0, T_initial], t);
                    let S_batch = yBatch.map(y => y[3] / (y[4] || 1e-10));

                    // Semibatch simulation
                    let ySemi = rk4(derivs, feedConfig === 'feed A to B' ? [0, 100, 0, 0, T_initial] : [100, 0, 0, 0, T_initial], t);
                    let S_semi = ySemi.map(y => y[3] / (y[4] || 1e-10));

                    selectivityRatio = t.slice(1).map((ti, i) => [ti, S_semi[i+1] / S_batch[i+1]]);
                } else {
                    simulationData = rk4(derivs, y0, t).map((y, i) => [t[i], ...y]);
                }

                redraw();
            }

            function redrawPlot() {
                simulate();
                redraw();
            }

            function redraw() {
                draw();
            }

            document.addEventListener('DOMContentLoaded', () => {
                // Initialize controls
                document.getElementById('select-plot').value = 'semibatch';
                document.getElementById('cycler-feed-label').textContent = 'feed A to B';
                document.getElementById('slider-feed-rate').value = 10;
                document.getElementById('input-feed-rate').value = 10;
                document.getElementById('slider-ua').value = 15000;
                document.getElementById('input-ua').value = 15000;
                document.getElementById('slider-dh1').value = 1000;
                document.getElementById('input-dh1').value = 1000;
                document.getElementById('slider-dh2').value = 3000;
                document.getElementById('input-dh2').value = 3000;

                // Event listeners for controls
                document.getElementById('select-plot').addEventListener('change', (e) => {
                    plotType = e.target.value;
                    redrawPlot();
                });

                document.getElementById('btn-feed-up').addEventListener('click', () => {
                    feedConfig = feedConfig === 'feed A to B' ? 'feed B to A' : 'feed A to B';
                    document.getElementById('cycler-feed-label').textContent = feedConfig;
                    redrawPlot();
                });

                document.getElementById('btn-feed-down').addEventListener('click', () => {
                    feedConfig = feedConfig === 'feed A to B' ? 'feed B to A' : 'feed A to B';
                    document.getElementById('cycler-feed-label').textContent = feedConfig;
                    redrawPlot();
                });

                const sliders = [
                    {slider: 'slider-feed-rate', input: 'input-feed-rate'},
                    {slider: 'slider-ua', input: 'input-ua'},
                    {slider: 'slider-dh1', input: 'input-dh1'},
                    {slider: 'slider-dh2', input: 'input-dh2'}
                ];

                sliders.forEach(({slider, input}) => {
                    const sliderEl = document.getElementById(slider);
                    const inputEl = document.getElementById(input);

                    sliderEl.addEventListener('input', () => {
                        inputEl.value = sliderEl.value;
                        updateValues();
                    });

                    inputEl.addEventListener('input', () => {
                        sliderEl.value = inputEl.value;
                        updateValues();
                    });
                });

                function updateValues() {
                    feedRate = parseFloat(document.getElementById('input-feed-rate').value);
                    UA = parseFloat(document.getElementById('input-ua').value);
                    dH1 = parseFloat(document.getElementById('input-dh1').value);
                    dH2 = parseFloat(document.getElementById('input-dh2').value);
                    redrawPlot();
                }
            });
        </script>
    </div>
</body>
</html>
