            border: none;
            cursor: pointer;
        }
        #tree-type-buttons .active {
            background: #007bff;
            color: white;
        }
        #select-obstacle {
            width: 100%;
            padding: 5px;
            margin-bottom: 20px;
        }
        #add-nodes-buttons {
            display: grid;
            grid-template-columns: repeat(2, 1fr);
            gap: 5px;
            margin-bottom: 20px;
        }
        #add-nodes-buttons button {
            padding: 8px;
            background: #28a745;
            color: white;
            border: none;
            cursor: pointer;
        }
        #slider-bias, #slider-radius {
            width: 100%;
            margin: 5px 0;
        }
        #canvas-container {
            flex: 1;
            position: relative;
        }
        #status-text {
            position: absolute;
            top: 10px;
            left: 50%;
            transform: translateX(-50%);
            background: rgba(255,255,255,0.8);
            padding: 5px 10px;
            border-radius: 5px;
            font-size: 14px;
            margin: 0;
        }
        #btn-reset {
            position: absolute;
            top: 10px;
            right: 10px;
            padding: 8px 12px;
            font-size: 18px;
            background: #dc3545;
            color: white;
            border: none;
            border-radius: 50%;
            cursor: pointer;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Tree type</h4>
            <div id="tree-type-buttons">
                <button id="btn-rt" class="active">Random Tree</button>
                <button id="btn-rrt">RRT</button>
                <button id="btn-rrt-star">RRT*</button>
            </div>
            <h4>obstacle type</h4>
            <select id="select-obstacle">
                <option value="narrow-passage" selected>narrow passage</option>
                <option value="empty">empty</option>
                <option value="random-rects">random rects</option>
            </select>
            <h4>number of nodes to add:</h4>
            <div id="add-nodes-buttons">
                <button id="btn-add-1">1</button>
                <button id="btn-add-10">10</button>
                <button id="btn-add-100">100</button>
                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>
            <h4>exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>
            <h4>goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>
        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let sketch = function(p) {
            const CANVAS_SIZE = 500;
            const START_POS = {x: 50, y: 250};
            const DEFAULT_GOAL = {x: 450, y: 450};
            let goal = {...DEFAULT_GOAL};
            let nodes = [];
            let edges = [];
            let goalReached = false;
            let path = [];
            let pathLength = 0;
            let currentTreeType = 'rt';
            let obstacleType = 'narrow-passage';
            let explorationBias = 0;
            let goalRadius = 1;
            let isDraggingGoal = false;
            let nodeCount = 1;

            p.setup = function() {
                let canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-container');
                nodes = [{...START_POS, parent: null, cost: 0}];
                drawScene();
            };

            p.draw = function() {};

            p.mousePressed = function() {
                if (p.dist(p.mouseX, p.mouseY, goal.x, goal.y) < goalRadius) {
                    isDraggingGoal = true;
                }
            };

            p.mouseDragged = function() {
                if (isDraggingGoal) {
                    goal.x = p.constrain(p.mouseX, 0, CANVAS_SIZE);
                    goal.y = p.constrain(p.mouseY, 0, CANVAS_SIZE);
                    resetSimulation();
                }
            };

            p.mouseReleased = function() {
                isDraggingGoal = false;
            };

            function drawScene() {
                p.background(255);
                drawObstacles();
                drawNodes();
                drawGoal();
                if (currentTreeType === 'rrt-star' && edges.length > 0) {
                    p.stroke(0);
                    p.strokeWeight(0.5);
                    edges.forEach(edge => p.line(edge.x1, edge.y1, edge.x2, edge.y2));
                }
                if (goalReached && path.length > 0) {
                    p.stroke(0, 255, 0);
                    p.strokeWeight(3);
                    for (let i = 0; i < path.length - 1; i++) {
                        p.line(path[i].x, path[i].y, path[i+1].x, path[i+1].y);
                    }
                }
            }

            function drawObstacles() {
                p.stroke('blue');
                p.strokeWeight(8);
                p.noFill();
                if (obstacleType === 'narrow-passage') {
                    p.rect(0, 0, CANVAS_SIZE, CANVAS_SIZE);
                    p.rect(150, 50, 10, 200);
                    p.rect(340, 300, 10, 200);
                } else if (obstacleType === 'random-rects') {
                    p.rect(100, 100, 80, 80);
                    p.rect(300, 200, 60, 120);
                    p.rect(200, 350, 100, 50);
                }
            }

            function drawNodes() {
                p.noStroke();
                nodes.forEach((node, i) => {
                    p.fill(i === 0 ? 'green' : 'red');
                    p.circle(node.x, node.y, 5);
                });
            }

            function drawGoal() {
                p.fill(255, 255, 0, 100);
                p.stroke(0);
                p.ellipse(goal.x, goal.y, goalRadius*2);
                p.line(goal.x - 10, goal.y, goal.x + 10, goal.y);
                p.line(goal.x, goal.y - 10, goal.x, goal.y + 10);
            }

            function resetSimulation() {
                nodes = [{...START_POS, parent: null, cost: 0}];
                edges = [];
                goalReached = false;
                path = [];
                pathLength = 0;
                nodeCount = 1;
                updateStatus();
                drawScene();
            }

            function addNodes(count) {
                for (let i = 0; i < count; i++) {
                    let newNode;
                    if (currentTreeType === 'rt') {
                        newNode = generateRandomNode();
                    } else {
                        newNode = generateRRTNode();
                    }
                    nodes.push(newNode);
                    if (currentTreeType === 'rrt-star') {
                        edges.push({x1: newNode.parent.x, y1: newNode.parent.y, x2: newNode.x, y2: newNode.y});
                    }
                    if (!goalReached && p.dist(newNode.x, newNode.y, goal.x, goal.y) < goalRadius) {
                        goalReached = true;
                        path = tracePath(newNode);
                        pathLength = calculatePathLength(path);
                    }
                }
                nodeCount += count;
                updateStatus();
                drawScene();
            }

            function generateRandomNode() {
                return {
                    x: p.random(CANVAS_SIZE),
                    y: p.random(CANVAS_SIZE),
                    parent: nodes[0]
                };
            }

            function generateRRTNode() {
                let target = p.random() < explorationBias ? goal : {x: p.random(CANVAS_SIZE), y: p.random(CANVAS_SIZE)};
                let nearest = nodes[0];
                let minDist = p.dist(nearest.x, nearest.y, target.x, target.y);
                nodes.forEach(node => {
                    let d = p.dist(node.x, node.y, target.x, target.y);
                    if (d < minDist) {
                        minDist = d;
                        nearest = node;
                    }
                });
                let angle = p.atan2(target.y - nearest.y, target.x - nearest.x);
                let step = 20;
                let newX = nearest.x + p.cos(angle) * step;
                let newY = nearest.y + p.sin(angle) * step;
                return {x: newX, y: newY, parent: nearest};
            }

            function tracePath(node) {
                let path = [];
                while (node) {
                    path.unshift(node);
                    node = node.parent;
                }
                return path;
            }

            function calculatePathLength(path) {
                let length = 0;
                for (let i = 0; i < path.length - 1; i++) {
                    length += p.dist(path[i].x, path[i].y, path[i+1].x, path[i+1].y);
                }
                return length;
            }

            function updateStatus() {
                let text = `${nodeCount} node${nodeCount !== 1 ? 's' : ''}, `;
                if (goalReached) {
                    text += `path length ${pathLength.toFixed(2)}`;
                } else {
                    text += 'goal not yet reached';
                }
                document.getElementById('status-text').textContent = text;
            }

            window.resetSimulation = resetSimulation;
            window.addNodes = addNodes;
            window.setTreeType = function(type) {
                currentTreeType = type;
                resetSimulation();
            };
            window.setObstacleType = function(type) {
                obstacleType = type;
                resetSimulation();
            };
            window.setExplorationBias = function(value) {
                explorationBias = value;
            };
            window.setGoalRadius = function(value) {
                goalRadius = value;
                drawScene();
            };
        };

        new p5(sketch);

        // Event Listeners
        document.getElementById('btn-rt').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rt').classList.add('active');
            window.setTreeType('rt');
        });

        document.getElementById('btn-rrt').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rrt').classList.add('active');
            window.setTreeType('rrt');
        });

        document.getElementById('btn-rrt-star').addEventListener('click', () => {
            document.querySelectorAll('#tree-type-buttons button').forEach(btn => btn.classList.remove('active'));
            document.getElementById('btn-rrt-star').classList.add('active');
            window.setTreeType('rrt-star');
        });

        document.getElementById('select-obstacle').addEventListener('change', (e) => {
            window.setObstacleType(e.target.value);
        });

        document.getElementById('slider-bias').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('bias-value-label').textContent = value.toFixed(2);
            window.setExplorationBias(value);
        });

        document.getElementById('slider-radius').addEventListener('input', (e) => {
            const value = parseFloat(e.target.value);
            document.getElementById('radius-value-label').textContent = value.toFixed(1);
            window.setGoalRadius(value);
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            window.resetSimulation();
        });

        document.querySelectorAll('#add-nodes-buttons button').forEach(btn => {
            btn.addEventListener('click', () => {
                const count = parseInt(btn.id.split('-')[2]);
                window.addNodes(count);
            });
        });
    </script>
</body>
</html>
