            <input type="range" id="slider-zeta0" min="0" max="1.6" step="0.005" value="0.455">
            <input type="number" id="input-zeta0" value="0.455" step="0.005">
        </div>
        <div class="control-group">
            <label for="slider-e0">\(e_0\)</label>
            <input type="range" id="slider-e0" min="2.01" max="5.0" step="0.01" value="2.93">
            <input type="number" id="input-e0" value="2.93" step="0.01">
        </div>
