        </div>
    </div>
    <div id="visualization-container">
        <canvas id="three-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 100);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('three-canvas'), antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setSize(window.innerWidth, window.innerHeight);
        renderer.setClearColor(0xf0f0f0);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.target.set(0, 1, 0);
        camera.position.set(4, 4, 4);
        camera.lookAt(0, 1, 0);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x666666);
        scene.add(ambientLight);
        const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
        directionalLight.position.set(5, 5, 5);
        scene.add(directionalLight);

        // Axes helper
        const axes = new THREE.AxesHelper(4);
        scene.add(axes);

        // Potential surface
        const potentialGeometry = new THREE.ParametricGeometry((u, v) => {
            const rho = 0.4 + u * 2.1;
            const theta = v * 2 * Math.PI;
            const x = rho * Math.cos(theta);
            const y = rho * rho + 1 / (rho * rho);
            const z = -rho * Math.sin(theta);
            return new THREE.Vector3(x, y, z);
        }, 50, 50);
        const potentialMaterial = new THREE.MeshStandardMaterial({
            color: 0xE69943,
            metalness: 0.4,
            roughness: 0.5,
            side: THREE.DoubleSide
        });
        const potentialSurface = new THREE.Mesh(potentialGeometry, potentialMaterial);
        scene.add(potentialSurface);

        // Central cylinder
        const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xcccccc,
            metalness: 0.1,
            roughness: 0.9
        });
        const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
        cylinder.position.set(0, 1.25, 0);
        scene.add(cylinder);

        // Initialize parameters
        const params = {
            t: 49,
            rho0: 1.9,
            zeta0: 0.455,
            e0: 2.93,
            v: 0,
            lambda: 1.1
        };

        // Link sliders and number inputs
        function setupControl(id, paramKey) {
            const slider = document.getElementById(`slider-${id}`);
            const input = document.getElementById(`input-${id}`);
            slider.value = params[paramKey];
            input.value = params[paramKey];

            const updateValue = (value) => {
                params[paramKey] = parseFloat(value);
                input.value = params[paramKey];
            };

            slider.addEventListener('input', (e) => updateValue(e.target.value));
            input.addEventListener('input', (e) => {
                const value = parseFloat(e.target.value);
                if (!isNaN(value)) {
                    params[paramKey] = value;
                    slider.value = value;
                }
            });
        }

        ['t', 'rho0', 'zeta0', 'e0', 'v', 'lambda'].forEach(key => setupControl(key, key));

        // RK4 integration
        function rk4Step(y, dydt, s, ds, derivs) {
            const k1 = derivs(y, s);
            const k2 = derivs(y.map((yi, i) => yi + ds * k1[i] / 2), s + ds / 2);
            const k3 = derivs(y.map((yi, i) => yi + ds * k2[i] / 2), s + ds / 2);
            const k4 = derivs(y.map((yi, i) => yi + ds * k3[i]), s + ds);

            return y.map((yi, i) => yi + ds * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]) / 6);
        }

        function derivs(Y, s) {
            const [rho, drhods, phi] = Y;
            const d2rhods2 = -(2 * rho - 2 / Math.pow(rho, 3)) - 4 * Math.pow(params.lambda, 2) * rho + 4 * params.e0 / Math.pow(rho, 3);
            const dphids = 2 * (params.e0 / Math.pow(rho, 2) - params.lambda);
            return [drhods, d2rhods2, dphids];
        }

        // Update visualization
        function updateVisualization() {
            const points = [];
            let Y = [params.rho0, 0, 0];
            const ds = 0.05;

            for (let s = 0; s <= params.t; s += ds) {
                const [rho, drhods, phi] = Y;

                // Calculate coordinates
                const u_rho = rho * rho + 1 / (rho * rho);
                const sqrt_arg = params.e0 - u_rho;
                const z_calc = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * params.v * s + params.zeta0);
                const x = rho * Math.cos(phi);
                const y = rho * Math.sin(phi);

                points.push(new THREE.Vector3(x, z_calc, -y));

                // RK4 step
                Y = rk4Step(Y, derivs, s, ds, derivs);
            }

            // Remove existing path
            const oldPath = scene.getObjectByName('ion-path');
            if (oldPath) {
                scene.remove(oldPath);
                oldPath.geometry.dispose();
                oldPath.material.dispose();
            }

            // Create new path
            const curve = new THREE.CatmullRomCurve3(points);
            const geometry = new THREE.TubeGeometry(curve, 512, 0.04, 8);
            const material = new THREE.MeshBasicMaterial({ color: 0x61AEEF });
            const path = new THREE.Mesh(geometry, material);
            path.name = 'ion-path';
            scene.add(path);
        }

        // Initial render
        updateVisualization();
        MathJax.typesetPromise();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
</html>
