      margin-bottom: 20px;
    }
    #density-plots-container {
      display: flex;
      justify-content: space-around;
      width: 100%;
    }
    #bonding-plot-container, #antibonding-plot-container {
      width: 45%;
      height: 500px;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <label for="slider-r">R (Å)</label>
      <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
      <output id="slider-r-value">0.001</output>
    </div>
    <div id="potential-energy-plot-container"></div>
    <div id="density-plots-container">
      <div id="bonding-plot-container"></div>
      <div id="antibonding-plot-container"></div>
    </div>
  </div>

  <script>
    function linspace(start, end, num) {
      const result = [];
      const step = (end - start) / (num - 1);
      for (let i = 0; i < num; i++) {
        result.push(start + step * i);
      }
      return result;
    }

    // Generate static potential energy data
    function generatePotentialEnergyData() {
      const R_au = linspace(0.2, 15, 100);
      const E_b = [];
      const E_a = [];
      for (let r of R_au) {
        const S_ab = (1 + r + (r**2)/3) * Math.exp(-r);
        const J = (1/r) - (1 + 1/r) * Math.exp(-2*r);
        const K = (1 + r) * Math.exp(-r);
        const Eb = (J + K) / (1 + S_ab);
        const Ea = (J - K) / (1 - S_ab);
        E_b.push(Eb);
        E_a.push(Ea);
      }
      const R_angstroms = R_au.map(r => r * 0.529177);
      return { x: R_angstroms, y_b: E_b, y_a: E_a };
    }

    // Initialize potential energy plot
    const peData = generatePotentialEnergyData();
    const trace1 = {
      x: peData.x,
      y: peData.y_b,
      type: 'line',
      name: 'bonding PE',
      line: { color: 'blue' }
    };
    const trace2 = {
      x: peData.x,
      y: peData.y_a,
      type: 'line',
      name: 'antibonding PE',
      line: { color: 'red' }
    };
    const peLayout = {
      title: 'potential energy',
      xaxis: { title: 'R (Å)', range: [0, 8] },
      yaxis: { title: 'Energy (Hartrees)', range: [-0.6, 0.4] },
      showlegend: true,
      legend: { x: 0.8, y: 1 }
    };
    Plotly.newPlot('potential-energy-plot-container', [trace1, trace2], peLayout);

    // Initialize and update 3D plots
    function updateDensityPlots(R_angstroms) {
      const R_au = R_angstroms / 0.529177;
      const S_ab = (1 + R_au + (R_au**2)/3) * Math.exp(-R_au);
      const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
      const N_a = 1 / Math.sqrt(2 * (1 - S_ab));

      const x = linspace(-3, 3, 50);
      const y = linspace(-3, 3, 50);
      const z_bonding = [];
      const z_antibonding = [];

      for (let j = 0; j < y.length; j++) {
        const row_b = [];
        const row_a = [];
        for (let i = 0; i < x.length; i++) {
          const x_val = x[i];
          const y_val = y[j];
          const dist_A = Math.sqrt(Math.pow(x_val + R_au/2, 2) + Math.pow(y_val, 2));
          const dist_B = Math.sqrt(Math.pow(x_val - R_au/2, 2) + Math.pow(y_val, 2));
          const phi_A = Math.exp(-dist_A);
          const phi_B = Math.exp(-dist_B);
          const psi_b = N_b * (phi_A + phi_B);
          const psi_a = N_a * (phi_A - phi_B);
          row_b.push(Math.pow(psi_b, 2));
          row_a.push(Math.pow(psi_a, 2));
        }
        z_bonding.push(row_b);
        z_antibonding.push(row_a);
      }

      const surfaceLayout = {
        showlegend: false,
        autosize: true,
        scene: {
          xaxis: { range: [-3, 3], showgrid: false, zeroline: false, showticklabels: false },
          yaxis: { range: [-3, 3], showgrid: false, zeroline: false, showticklabels: false },
          zaxis: { range: [0, 0.5], showgrid: false, zeroline: false, showticklabels: false },
          camera: { eye: {x: 1.5, y: 1.5, z: 1.5} }
        },
        margin: { l: 0, r: 0, b: 0, t: 0 }
      };

      Plotly.react('bonding-plot-container', [{
        x: x, y: y, z: z_bonding,
        type: 'surface',
        colorscale: 'YlOrBr',
        showscale: false
      }], surfaceLayout);

      Plotly.react('antibonding-plot-container', [{
        x: x, y: y, z: z_antibonding,
        type: 'surface',
        colorscale: 'YlOrBr',
        showscale: false
      }], surfaceLayout);
    }

    // Slider event listener
    const slider = document.getElementById('slider-r');
    const output = document.getElementById('slider-r-value');
    slider.addEventListener('input', function() {
      const value = parseFloat(this.value);
      output.value = value.toFixed(3);
      updateDensityPlots(value);
    });

    // Initial plot
    updateDensityPlots(0.001);
  </script>
</body>
</html>
