<!DOCTYPE html>
<html>
<head>
  <title>Moon Simulation</title>
  <style>
    * {
      box-sizing: border-box;
    }
    body {
      margin: 0;
      font-family: Arial, sans-serif;
    }
    #app-container {
      display: flex;
      height: 100vh;
      overflow: hidden;
    }
    #control-panel {
      width: 300px;
      background-color: #f5f5f5;
      padding: 15px;
      overflow-y: auto;
    }
    #visualization-area {
      flex: 1;
      display: flex;
      justify-content: center;
      align-items: center;
      background-color: #111;
    }
    canvas {
      width: 100%;
      height: 100%;
      display: block;
    }
    h4 {
      margin-top: 10px;
      margin-bottom: 5px;
      color: #333;
    }
    #rotation-selector button {
      margin: 2px;
      padding: 5px 10px;
      background-color: #e0e0e0;
      border: none;
      cursor: pointer;
    }
    #rotation-selector .active {
      background-color: #3B82F6;
      color: white;
    }
    button {
      margin: 2px;
      padding: 5px 10px;
      background-color: #e0e0e0;
      border: none;
      cursor: pointer;
    }
    input[type="checkbox"] {
      margin-right: 5px;
    }
    #display-time {
      margin: 10px 0;
      font-weight: bold;
    }
  </style>
</head>
<body>
  <div id="app-container">
    <div id="control-panel">
      <h4>time</h4>
      <input type="range" id="slider-time" min="0" max="672" step="1">
      <div>
        <button id="btn-play-pause">▶</button>
        <button id="btn-step-back">«</button>
        <button id="btn-reset">⥀</button>
        <button id="btn-step-forward">»</button>
      </div>
      <p id="display-time">0 d 0 h</p>
      <hr>
      <h4>Moon's rotational period related to the current value</h4>
      <div id="rotation-selector">
        <button id="btn-rot-half">half</button>
        <button id="btn-rot-normal">normal</button>
        <button id="btn-rot-double">double</button>
        <button id="btn-rot-none">no rotation</button>
      </div>
      <hr>
      <div>
        <input type="checkbox" id="check-day-night">
        <label for="check-day-night">day/night</label>
      </div>
      <div>
        <input type="checkbox" id="check-near-side" checked>
        <label for="check-near-side">near side of the Moon</label>
      </div>
      <div>
        <input type="checkbox" id="check-earth-moon-line" checked>
        <label for="check-earth-moon-line">Earth-Moon line</label>
      </div>
      <div>
        <input type="checkbox" id="check-moon-only">
        <label for="check-moon-only">the Moon only</label>
      </div>
    </div>
    <div id="visualization-area">
      <canvas id="main-canvas"></canvas>
    </div>
  </div>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
  <script>
    let time = 0;
    let isPlaying = false;
    let rotationMode = 'normal';
    let showDayNight = false;
    let showNearSide = true;
    let showEarthMoonLine = true;
    let showMoonOnly = false;
    let animationInterval;

    function setup() {
      const vizArea = document.getElementById('visualization-area');
      const canvas = createCanvas(vizArea.clientWidth, vizArea.clientHeight);
      canvas.parent('visualization-area');
      noLoop();
      windowResized();
      initEventListeners();
      updateUI();
    }

    function windowResized() {
      const vizArea = document.getElementById('visualization-area');
      resizeCanvas(vizArea.clientWidth, vizArea.clientHeight);
    }

    function draw() {
      background(0);
      const earthRadius = 80;
      const moonRadius = 25;
      const orbitalRadius = 250;

      const earthRotation = (time / 24) * TWO_PI;
      const moonOrbit = (time / 672) * TWO_PI;

      let moonRotation;
      switch (rotationMode) {
        case 'half':
          moonRotation = (time / (672 / 2)) * TWO_PI;
          break;
        case 'double':
          moonRotation = (time / (672 * 2)) * TWO_PI;
          break;
        case 'none':
          moonRotation = 0;
          break;
        default:
          moonRotation = moonOrbit;
      }

      if (!showMoonOnly) {
        push();
        rotate(earthRotation);
        fill('#3B82F6');
        ellipse(0, 0, earthRadius * 2);
        fill(255);
        ellipse(0, -earthRadius, 10);
        fill(0, 128, 0);
        beginShape();
        vertex(-20, -earthRadius + 20);
        vertex(20, -earthRadius + 20);
        vertex(30, -earthRadius + 50);
        vertex(-30, -earthRadius + 50);
        endShape(CLOSE);
        stroke(255, 0, 0);
        line(0, -earthRadius, 0, -earthRadius - 20);
        line(-5, -earthRadius - 10, 5, -earthRadius - 10);
        line(0, -earthRadius - 20, -5, -earthRadius - 30);
        line(0, -earthRadius - 20, 5, -earthRadius - 30);
        ellipse(0, -earthRadius - 25, 10);
        if (showDayNight) {
          fill(0, 128);
          noStroke();
          arc(0, 0, earthRadius * 2, earthRadius * 2, PI/2, THREE_PI/2);
        }
        pop();
      }

      const mx = orbitalRadius * cos(moonOrbit - PI/2);
      const my = orbitalRadius * sin(moonOrbit - PI/2);

      let moonX = showMoonOnly ? 0 : mx;
      let moonY = showMoonOnly ? 0 : my;

      push();
      translate(moonX, moonY);
      fill('#808080');
      ellipse(0, 0, moonRadius * 2);

      if (showNearSide) {
        push();
        rotate(moonRotation);
        fill('#22C55E');
        noStroke();
        arc(0, 0, moonRadius * 2, moonRadius * 2, -PI/2, PI/2);
        stroke('#D946EF');
        line(0, -moonRadius, 0, -moonRadius - 20);
        line(-5, -moonRadius - 10, 5, -moonRadius - 10);
        line(0, -moonRadius - 20, -5, -moonRadius - 30);
        line(0, -moonRadius - 20, 5, -moonRadius - 30);
        ellipse(0, -moonRadius - 25, 10);
        pop();
      }

      if (showDayNight) {
        fill(0, 128);
        noStroke();
        arc(0, 0, moonRadius * 2, moonRadius * 2, PI/2, THREE_PI/2);
      }
      pop();

      if (!showMoonOnly && showEarthMoonLine) {
        stroke(255, 255, 0, 128);
        strokeWeight(1);
        strokeDash(5, 5);
        line(0, 0, mx, my);
        strokeDash(0);
      }
    }

    function initEventListeners() {
      const slider = document.getElementById('slider-time');
      const playPauseBtn = document.getElementById('btn-play-pause');
      const stepBackBtn = document.getElementById('btn-step-back');
      const stepForwardBtn = document.getElementById('btn-step-forward');
      const resetBtn = document.getElementById('btn-reset');
      const rotationButtons = document.querySelectorAll('#rotation-selector button');
      const dayNightCheckbox = document.getElementById('check-day-night');
      const nearSideCheckbox = document.getElementById('check-near-side');
      const earthMoonLineCheckbox = document.getElementById('check-earth-moon-line');
      const moonOnlyCheckbox = document.getElementById('check-moon-only');

      slider.addEventListener('input', () => {
        time = parseInt(slider.value);
        updateUI();
        redraw();
      });

      playPauseBtn.addEventListener('click', () => {
        isPlaying = !isPlaying;
        playPauseBtn.textContent = isPlaying ? '❚❚' : '▶';
        if (isPlaying) startAnimation();
        else stopAnimation();
      });

      stepBackBtn.addEventListener('click', () => {
        time = Math.max(0, time - 1);
        updateUI();
        redraw();
      });

      stepForwardBtn.addEventListener('click', () => {
        time = Math.min(672, time + 1);
        updateUI();
        redraw();
      });

      resetBtn.addEventListener('click', () => {
        time = 0;
        updateUI();
        redraw();
      });

      rotationButtons.forEach(btn => {
        btn.addEventListener('click', () => {
          rotationMode = btn.id.replace('btn-rot-', '');
          rotationButtons.forEach(b => b.classList.remove('active'));
          btn.classList.add('active');
          redraw();
        });
      });

      dayNightCheckbox.addEventListener('change', () => {
        showDayNight = dayNightCheckbox.checked;
        redraw();
      });

      nearSideCheckbox.addEventListener('change', () => {
        showNearSide = nearSideCheckbox.checked;
        redraw();
      });

      earthMoonLineCheckbox.addEventListener('change', () => {
        showEarthMoonLine = earthMoonLineCheckbox.checked;
        redraw();
      });

      moonOnlyCheckbox.addEventListener('change', () => {
        showMoonOnly = moonOnlyCheckbox.checked;
        redraw();
      });

      document.getElementById('btn-rot-normal').classList.add('active');
    }

    function updateUI() {
      document.getElementById('slider-time').value = time;
      updateDisplayTime();
    }

    function updateDisplayTime() {
      const days = Math.floor(time / 24);
      const hours = Math.floor(time % 24);
      document.getElementById('display-time').textContent = `${days} d ${hours} h`;
    }

    function startAnimation() {
      stopAnimation();
      animationInterval = setInterval(() => {
        time = (time + 1) % 673;
        updateUI();
        redraw();
      }, 100);
    }

    function stopAnimation() {
      if (animationInterval) {
        clearInterval(animationInterval);
        animationInterval = null;
      }
    }
  </script>
</body>
</html>
