            <span id="span-x-lower-value">902</span>
        </div>
        <div>
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" step="1" value="1091">
            <span id="span-x-upper-value">1091</span>
        </div>
    </div>

    <script>
        const nu0 = 1000;
        const B_upper = 0.98;
        const A_upper = 1.95;
        const B_lower = 1.0;
        const A_lower = 2.0;
        const T = 300;
        const k_boltzmann = 0.695;
        const J_max = 60;

        function generateData() {
            let traces = [];
            const currentView = document.querySelector('input[name="view-selector"]:checked').value;
            const currentK = parseInt(document.querySelector('input[name="k-selector"]:checked').value);
            const currentSubband = document.querySelector('input[name="subband-type"]:checked').value;
            const currentBranch = document.querySelector('input[name="branch-selector"]:checked').value;

            if (currentView === 'full') {
                let x = [], y = [];
                for (let K of [0,1,2,3,4,5]) {
                    for (let subband of ['positive', 'negative']) {
                        const deltaK = subband === 'positive' ? 1 : -1;
                        const K_lower = K;
                        const K_upper = K_lower + deltaK;
                        if (K_upper < 0 || (subband === 'negative' && K === 0)) continue;

                        for (let branch of ['P', 'Q', 'R']) {
                            for (let J_lower = 0; J_lower <= J_max; J_lower++) {
                                let J_upper;
                                if (branch === 'P') {
                                    J_upper = J_lower - 1;
                                    if (J_upper < 0) continue;
                                } else if (branch === 'Q') {
                                    J_upper = J_lower;
                                } else {
                                    J_upper = J_lower + 1;
                                    if (J_upper > J_max) continue;
                                }

                                if (K_upper > J_upper || K_lower > J_lower) continue;

                                const E_lower = B_lower * J_lower * (J_lower + 1) + (A_lower - B_lower) * K_lower**2;
                                const E_upper = B_upper * J_upper * (J_upper + 1) + (A_upper - B_upper) * K_upper**2;
                                const nu = nu0 + E_upper - E_lower;
                                const intensity = (2 * J_lower + 1) * Math.exp(-E_lower / (k_boltzmann * T));

                                x.push(nu);
                                y.push(intensity);
                            }
                        }
                    }
                }
                const maxIntensity = Math.max(...y);
                y = y.map(val => val / maxIntensity);
                traces.push({ x, y, type: 'bar', width: 0.1, marker: { color: 'black' } });
            } else {
                const subbands = currentSubband === 'full' ? ['positive', 'negative'] : [currentSubband];
                for (let subband of subbands) {
                    const deltaK = subband === 'positive' ? 1 : -1;
                    const K_lower = currentK;
                    const K_upper = K_lower + deltaK;
                    if (K_upper < 0 || (subband === 'negative' && currentK === 0)) continue;

                    const color = subband === 'positive' ? 'red' : 'blue';
                    const branches = currentSubband === 'full' ? ['P', 'Q', 'R'] : [currentBranch];

                    for (let branch of branches) {
                        let x = [], y = [];
                        for (let J_lower = 0; J_lower <= J_max; J_lower++) {
                            let J_upper;
                            if (branch === 'P') {
                                J_upper = J_lower - 1;
                                if (J_upper < 0) continue;
                            } else if (branch === 'Q') {
                                J_upper = J_lower;
                            } else {
                                J_upper = J_lower + 1;
                                if (J_upper > J_max) continue;
                            }

                            if (K_upper > J_upper || K_lower > J_lower) continue;

                            const E_lower = B_lower * J_lower * (J_lower + 1) + (A_lower - B_lower) * K_lower**2;
                            const E_upper = B_upper * J_upper * (J_upper + 1) + (A_upper - B_upper) * K_upper**2;
                            const nu = nu0 + E_upper - E_lower;
                            const intensity = (2 * J_lower + 1) * Math.exp(-E_lower / (k_boltzmann * T));

                            x.push(nu);
                            y.push(intensity);
                        }
                        if (x.length > 0) {
                            const maxIntensity = Math.max(...y);
                            y = y.map(val => val / maxIntensity);
                            traces.push({ x, y, type: 'bar', width: 0.1, marker: { color } });
                        }
                    }
                }
            }
            return traces;
        }

        function updatePlot() {
            const traces = generateData();
            const view = document.querySelector('input[name="view-selector"]:checked').value;
            let title = 'Rovibronic Spectrum';
            if (view === 'sub') {
                const K = document.querySelector('input[name="k-selector"]:checked').value;
                const subband = document.querySelector('input[name="subband-type"]:checked').value;
                title = `K = ${K} sub-band`;
                if (subband !== 'full') title += `<br>"${subband}" sub-band`;
            }

            const layout = {
                title,
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [
                        parseInt(document.getElementById('slider-x-lower').value),
                        parseInt(document.getElementById('slider-x-upper').value)
                    ]
                },
                yaxis: { title: 'absorbance', range: [0, 1.2] },
                showlegend: false,
                bargap: 0
            };
            Plotly.newPlot('plot-div', traces, layout);
        }

        function updateControls() {
            const isSubView = document.getElementById('radio-view-sub').checked;
            const kGroup = document.getElementById('k-selector-group');
            const subbandGroup = document.getElementById('subband-type-group');
            const branchGroup = document.getElementById('branch-selector-group');
            const kValue = parseInt(document.querySelector('input[name="k-selector"]:checked').value);

            if (isSubView) {
                kGroup.style.opacity = 1;
                kGroup.style.pointerEvents = 'auto';
                subbandGroup.style.opacity = 1;
                subbandGroup.style.pointerEvents = 'auto';

                const negativeRadio = document.getElementById('radio-subband-negative');
                if (kValue === 0) {
                    negativeRadio.disabled = true;
                    if (negativeRadio.checked) document.getElementById('radio-subband-full').checked = true;
                } else {
                    negativeRadio.disabled = false;
                }

                const subbandType = document.querySelector('input[name="subband-type"]:checked').value;
                branchGroup.style.display = subbandType === 'full' ? 'none' : 'block';
            } else {
                kGroup.style.opacity = 0.5;
                kGroup.style.pointerEvents = 'none';
                subbandGroup.style.opacity = 0.5;
                subbandGroup.style.pointerEvents = 'none';
                branchGroup.style.display = 'none';
            }
        }

        document.addEventListener('DOMContentLoaded', () => {
            updateControls();
            updatePlot();

            const radios = document.querySelectorAll('input[name="view-selector"], input[name="k-selector"], input[name="subband-type"], input[name="branch-selector"]');
            radios.forEach(radio => radio.addEventListener('change', () => {
                updateControls();
                updatePlot();
            }));

            const lowerSlider = document.getElementById('slider-x-lower');
            const upperSlider = document.getElementById('slider-x-upper');
            const lowerValueSpan = document.getElementById('span-x-lower-value');
            const upperValueSpan = document.getElementById('span-x-upper-value');

            lowerSlider.addEventListener('input', () => {
                let lower = parseInt(lowerSlider.value);
                let upper = parseInt(upperSlider.value);
                if (lower >= upper) {
                    upperSlider.value = lower + 1;
                    upper = lower + 1;
                }
                lowerValueSpan.textContent = lower;
                Plotly.relayout('plot-div', 'xaxis.range', [lower, upper]);
            });

            upperSlider.addEventListener('input', () => {
                let upper = parseInt(upperSlider.value);
                let lower = parseInt(lowerSlider.value);
                if (upper <= lower) {
                    lowerSlider.value = upper - 1;
                    lower = upper - 1;
                }
                upperValueSpan.textContent = upper;
                Plotly.relayout('plot-div', 'xaxis.range', [lower, upper]);
            });

            lowerSlider.value = 902;
            upperSlider.value = 1091;
            lowerValueSpan.textContent = '902';
            upperValueSpan.textContent = '1091';
        });
    </script>
</body>
</html>
