            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.163.0/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.163.0/examples/jsm/"
            }
        }
    </script>
    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        let scene, camera, renderer, controls;
        let radiationGroup, radiationMesh;
        let boxHelper;
        let currentWaveType = 'P';

        init();
        animate();

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(0, -5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Orbit controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const dirLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
            dirLight1.position.set(5, 5, 5).normalize();
            scene.add(dirLight1);

            const dirLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
            dirLight2.position.set(-5, -5, -5).normalize();
            scene.add(dirLight2);

            // Radiation group
            radiationGroup = new THREE.Object3D();
            radiationGroup.rotation.order = 'ZXY';
            scene.add(radiationGroup);

            // Create initial mesh
            createRadiationMesh(currentWaveType);

            // Compass
            createCompass();

            // U arrow
            createUArrow();

            // Box helper
            boxHelper = new THREE.BoxHelper(radiationGroup, 0x0000ff);
            boxHelper.visible = false;
            scene.add(boxHelper);

            // Event listeners
            setupEventListeners();

            // MathJax rendering
            MathJax.typeset();
        }

        function createRadiationMesh(waveType) {
            const geometry = createRadiationGeometry(waveType);
            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide
            });
            radiationMesh = new THREE.Mesh(geometry, material);
            radiationGroup.add(radiationMesh);
            updateRotations();
        }

        function createRadiationGeometry(waveType) {
            return new THREE.ParametricGeometry((u, v) => {
                const phi = u * 2 * Math.PI;
                const theta = v * Math.PI;
                let r;

                switch (waveType) {
                    case 'P':
                        r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        break;
                    case 'SV':
                        r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        break;
                    case 'SH':
                        r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        break;
                    default:
                        r = 1;
                }

                const x = r * Math.sin(theta) * Math.cos(phi);
                const y = r * Math.sin(theta) * Math.sin(phi);
                const z = r * Math.cos(theta);
                return new THREE.Vector3(x, y, z);
            }, 64, 64);
        }

        function createCompass() {
            const compassGroup = new THREE.Group();
            const radius = 1.5;

            // Circle
            const circleGeometry = new THREE.CircleGeometry(radius, 64);
            circleGeometry.rotateX(-Math.PI / 2);
            const circleMaterial = new THREE.LineDashedMaterial({
                color: 0x000000,
                dashSize: 0.1,
                gapSize: 0.05
            });
            const circle = new THREE.Line(circleGeometry, circleMaterial);
            circle.computeLineDistances();
            compassGroup.add(circle);

            // Labels
            const directions = [
                { name: 'N', angle: Math.PI / 2 },
                { name: 'E', angle: 0 },
                { name: 'S', angle: -Math.PI / 2 },
                { name: 'W', angle: Math.PI }
            ];

            directions.forEach(dir => {
                const sprite = createTextSprite(dir.name);
                sprite.position.set(
                    radius * Math.cos(dir.angle),
                    radius * Math.sin(dir.angle),
                    0.01
                );
                sprite.scale.set(0.1, 0.1, 0.1);
                compassGroup.add(sprite);
            });

            scene.add(compassGroup);
        }

        function createUArrow() {
            const arrow = new THREE.ArrowHelper(
                new THREE.Vector3(0, 0, 1),
                new THREE.Vector3(0, 0, 0),
                1,
                0x000000,
                0.1,
                0.05
            );
            scene.add(arrow);

            const uText = createTextSprite('U');
            uText.position.set(0, 0, 1.1);
            scene.add(uText);
        }

        function createTextSprite(text) {
            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            canvas.width = 64;
            canvas.height = 64;
            context.font = 'bold 32px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillStyle = 'black';
            context.fillText(text, 32, 32);

            const texture = new THREE.CanvasTexture(canvas);
            texture.minFilter = THREE.LinearFilter;
            texture.wrapS = THREE.ClampToEdgeWrapping;
            texture.wrapT = THREE.ClampToEdgeWrapping;

            const material = new THREE.SpriteMaterial({ map: texture });
            return new THREE.Sprite(material);
        }

        function setupEventListeners() {
            // Sliders
            const strikeSlider = document.getElementById('slider-strike');
            strikeSlider.addEventListener('input', () => {
                document.getElementById('strike-value').textContent = strikeSlider.value;
                updateRotations();
            });

            const dipSlider = document.getElementById('slider-dip');
            dipSlider.addEventListener('input', () => {
                document.getElementById('dip-value').textContent = dipSlider.value;
                updateRotations();
            });

            const rakeSlider = document.getElementById('slider-rake');
            rakeSlider.addEventListener('input', () => {
                document.getElementById('rake-value').textContent = rakeSlider.value;
                updateRotations();
            });

            // Wave type buttons
            document.getElementById('btn-p').addEventListener('click', () => {
                if (currentWaveType !== 'P') {
                    currentWaveType = 'P';
                    updateWaveType();
                }
            });

            document.getElementById('btn-sv').addEventListener('click', () => {
                if (currentWaveType !== 'SV') {
                    currentWaveType = 'SV';
                    updateWaveType();
                }
            });

            document.getElementById('btn-sh').addEventListener('click', () => {
                if (currentWaveType !== 'SH') {
                    currentWaveType = 'SH';
                    updateWaveType();
                }
            });

            // Checkbox
            document.getElementById('checkbox-box').addEventListener('change', (event) => {
                boxHelper.visible = event.target.checked;
                renderer.render(scene, camera);
            });

            // Window resize
            window.addEventListener('resize', () => {
                camera.aspect = window.innerWidth / window.innerHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(window.innerWidth, window.innerHeight);
            });
        }

        function updateWaveType() {
            if (radiationMesh) {
                radiationGroup.remove(radiationMesh);
                radiationMesh.geometry.dispose();
                radiationMesh.material.dispose();
            }
            createRadiationMesh(currentWaveType);
            updateRotations();
        }

        function updateRotations() {
            const strike = parseFloat(document.getElementById('slider-strike').value);
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const rake = parseFloat(document.getElementById('slider-rake').value);

            radiationGroup.rotation.z = THREE.MathUtils.degToRad(strike);
            radiationGroup.rotation.x = THREE.MathUtils.degToRad(dip);
            radiationGroup.rotation.y = THREE.MathUtils.degToRad(rake);
            renderer.render(scene, camera);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }
    </script>
</body>
</html>
